CREATE TABLE `pending_post` (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` text,
  `content` text,
  `slug` text,
  `user_id` int DEFAULT NULL,
  `category_id` int DEFAULT NULL,
  `intro` text,
  `image_thumbnail` text,
  PRIMARY KEY (`id`),
  KEY `userid_idx` (`user_id`),
  KEY `sad_idx` (`category_id`),
  CONSTRAINT `` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `sad` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=69 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE `posts` (
  `id` int NOT NULL AUTO_INCREMENT,
  `category_id` int DEFAULT NULL,
  `title` text,
  `intro` text,
  `content` text,
  `count_comment` int DEFAULT NULL,
  `slug` text,
  `active` tinyint DEFAULT NULL,
  `user_id` int DEFAULT NULL,
  `count_view` int DEFAULT NULL,
  `count_reaction` int DEFAULT NULL,
  `image_thumbnail` text,
  PRIMARY KEY (`id`),
  KEY `category_id_idx` (`category_id`),
  KEY `user_id_idx` (`user_id`),
  CONSTRAINT `category_id` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`),
  CONSTRAINT `id_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=178 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `posts` VALUES (115,12,'Đà Nẵng ưu tiên xét nghiệm Covid-19 cho thí sinh là F1 thi tốt nghiệp','Ngành giáo dục Đà Nẵng sẽ bố trí phòng riêng biệt để thí sinh nghi nhiễm (F1, F2) tham dự kỳ thi tốt nghiệp THPT năm 2020.','Ngành giáo dục Đà Nẵng sẽ bố trí phòng riêng biệt để thí sinh nghi nhiễm (F1, F2) tham dự kỳ thi tốt nghiệp THPT năm 2020.\nSáng 31/7, bà Lê Thị Bích Thuận, Giám đốc Sở GD&amp;ĐT Đà Nẵng, cho biết kỳ thi tốt nghiệp THPT năm 2020 trên địa bàn thành phố vẫn diễn ra từ 8/8 đến 10/8.\n\nNhư vậy, không có việc Đà Nẵng tạm dừng kỳ thi thốt nghiệp do ảnh hưởng của dịch Covid-19.\n\nƯu tiên xét nghiệm cho thí sinh diện F1\nBà Ngô Thị Kim Yến, Giám đốc Sở Y tế Đà Nẵng, cho biết bắt đầu từ ngày 31/7, Trung tâm Kiểm soát bệnh tật (CDC) thành phố sẽ phối hợp với các đơn vị liên quan xét nghiệm cho người dân. Với nhân sự và trang thiết bị hiện có, ngành y tế Đà Nẵng dự kiến mỗi ngày lấy mẫu xét nghiệm cho 4.000-5.000 người.\n\nDo nhu cầu xét nghiệm lớn, sở Y tế ưu tiên những trường hợp vùng có người mắc Covid-19, các khu dân cư bị phong tỏa, gần 3 bệnh viện đang điều trị cho bệnh nhân nhiễm virus SARS-CoV-2.\n\nNgoài những đối tượng trên, ngành y tế Đà Nẵng ưu tiên xét nghiệm học sinh sắp thi tốt nghiệp THPT có tiếp xúc gần với bệnh nhân mắc Covid-19 (F1), với các trường hợp F2 phải chờ kết quả của F1. <img src=\"Assets/images/covid_19DN0016_zing_1.jpg\"> \"Sở Y tế sẽ thực hiện khoanh vùng, sàng lọc, ưu tiên xét nghiệm virus SARS-CoV-2 và sẽ có kết quả trước kỳ thi để các em yên tâm tập trung ôn tập. Đơn vị cũng đã có phương án cụ thể, hướng dẫn Sở GD&amp;ĐT về các biện pháp phòng chống dịch\", bà Yến thông tin.\n\nGiám đốc Ngô Thị Kim Yến khuyến cáo các phụ huynh, học sinh nên bình tĩnh, tập trung ôn thi, đảm bảo nghiêm việc phòng tránh dịch theo đúng quy định của Bộ Y tế.\n\nHọc sinh thuộc diện F1, F2 dự thi như thế nào?\n\"Nếu học sinh có hiện tượng sốt, ho… gia đình phải đưa đến các trung tâm y tế gần nhất để theo dõi và điều trị\", bà Yến khuyến cáo.\n\nBà Lê Thị Bích Thuận, cho biết địa phương đã sẵn sàng kế hoạch và các biện pháp dự phòng cho những tình huống phát sinh trong kỳ thi sắp tới.\n\nCụ thể, thí sinh thuộc diện F0 (dương tính với SARS-CoV2) phải cách ly và điều trị nên không thể dự thi. Những trường hợp này sẽ được xem xét đặc cách công nhận tốt nghiệp trung học phổ thông theo quy định hiện hành. <img src=\"Assets/images/covid_19DN0059_zing.jpg\"> Trường hợp các thí sinh này có nguyện vọng xét tuyển vào đại học, sở GD&amp;ĐT sẽ đề nghị Bộ GD&amp;ĐT chỉ đạo, hướng dẫn các cơ sở giáo dục đại học xem xét tuyển sinh bằng phương thức phù hợp trên tinh thần tự chủ đại học, đảm bảo quyền lợi tối đa cho thí sinh.\n\nVới các thí sinh thuộc diện F1 (tiếp xúc trực tiếp F0, phải cách ly tập trung) sẽ được bố trí thi tại các địa điểm dự phòng. Điểm thi này được trang bị các phương tiện phòng, chống dịch. Nhân viên làm việc sẽ được tập huấn nghiệp vụ, chuyên môn cũng như trang bị đồ bảo hộ để phòng, chống dịch.\n\nĐối với thí sinh thuộc diện F2 (tiếp xúc với F1) nếu có trên 20 thí sinh/điểm thi thì sẽ làm bài tại phòng dự phòng. Trong trường hợp có hơn 20 thí sinh/điểm thi, ngành giáo dục sẽ chuyển về các điểm thi dự phòng tại 7 quận, huyện trên địa bàn.\n\n\"Trong quá trình tổ chức kỳ thi, thí sinh có biểu hiện ho, sốt, khó thở bố trí thi tại phòng thi dự phòng của điểm thi\", bà Thuận thông tin.  ',0,'da-nang-uu-tien-xet-nghiem-covid-19-cho-thi-sinh-la-f1-thi-tot-nghiep',NULL,1,43,634,'<img src=\"Assets/images/covid_19DN0016_zing_1.jpg\">'),(116,12,'Học sinh Mỹ mắc Covid-19 trong ngày đầu trở lại trường','Đại diện trường Trung học Greenfield Central, Mỹ, xác nhận việc học sinh có kết quả xét nghiệm dương tính với virus SARS-CoV-2.','Đại diện trường Trung học Greenfield Central, Mỹ, xác nhận việc học sinh có kết quả xét nghiệm dương tính với virus SARS-CoV-2.\nSau khi có kết quả xét nghiệm, ban giám hiệu nhà trường đã gửi thư thông báo đến phụ huynh. Theo đó, Sở Y tế hạt Hancock xác nhận một học sinh nhiễm bệnh sau khi đi học lại, Greenfield Reporter đưa tin.\n\nNgay sau đó, nhà trường thông báo học sinh và nhân viên sẽ được cách ly. Mọi hoạt động của các em được ghi lại, bao gồm hoạt động ngoại khóa và lịch trình di chuyển. <img src=\"Assets/images/GettyImages_521721254.jpg\"> Việc thu thập lịch trình của từng học sinh nhằm xác định những trường hợp tiếp xúc gần người nhiễm bệnh. Toàn bộ khu vực trong trường được khử khuẩn thường xuyên.\n\nQuy định nêu rõ những học sinh tiếp xúc gần trường hợp nhiễm bệnh, hoặc những em được chuyên gia y tế chỉ định cách ly, không đi học trong 14 ngày tới.\n\nCác em chủ động ở nhà, quan sát tình hình sức khỏe. Học sinh có dấu hiệu đau họng, sốt trên 37,5 độ C, ho, mất vị giác, đau người, nên ở nhà cách ly và làm xét nghiệm sớm.\n\nHọc sinh được xét nghiệm nhưng chưa có kết quả cũng được ban giám hiệu trường Trung học Greenfield Central cho ở nhà cách ly trong 14 ngày.\n\nNhững em không tiếp xúc gần học sinh nhiễm bệnh đi học ngày 31/7. Ông Harold Olin, Hiệu trưởng nhà trường, nhấn mạnh dù ít học sinh đến lớp, nhà trường vẫn duy trì công việc giảng dạy. Trường cũng cung cấp lớp học trực tiếp hoặc trực tuyến cho học sinh lựa chọn.\n\nÔng Olin nói với FOX59 News rằng các nhân viên đang nỗ lực làm việc để kiểm soát tình hình dịch bệnh. Công việc này trở nên khó khăn hơn khi học sinh đi học trở lại.\n\nĐây không phải trường hợp học sinh đầu tiên mắc Covid-19 tại trường học. Ngày 17/7, hai học sinh được thông báo nhiễm bệnh khi tham gia lớp học hè tại trường công lập Detroit, bang Michigan, Mỹ.\n\nSau đó, Sở Y tế vào cuộc, tìm kiếm các trường hợp tiếp xúc gần hai học sinh này. Hơn 300 em và nhân viên được làm xét nghiệm, tất cả đều cho kết quả âm tính.\n\nNgày 27/7, CNN đưa tin hai học sinh nhận kết quả xét nghiệm dương tính với virus SARS-CoV-2, sau khi làm bài thi ACT tại trường Trung học Edmond North, bang Oklahoma, Mỹ.\n\nKhi có kết quả, đại diện trường đã thông báo với cơ quan y tế địa phương, hội đồng thi và những thí sinh tham gia hôm đó. Đồng thời, những em ngồi cùng phòng với trường hợp nhiễm bệnh cũng nhận được email cảnh báo, yêu cầu tự cách ly, theo dõi tình hình sức khỏe.  ',0,'hoc-sinh-my-mac-covid-19-trong-ngay-dau-tro-lai-truong',NULL,1,342,215,'<img src=\"Assets/images/GettyImages_521721254.jpg\">'),(117,7,'Bộ GD&ĐT quyết định hoãn thi tốt nghiệp THPT tại Đà Nẵng, Quảng Nam','Học sinh của thành phố Đà Nẵng, một phần tỉnh Quảng Nam đang giãn cách xã hội và thí sinh F1, F2 trong cả nước sẽ thi tốt nghiệp THPT đợt hai.','<img src=\"Assets/images/da_nang.jpg\"> Học sinh của thành phố Đà Nẵng, một phần tỉnh Quảng Nam đang giãn cách xã hội và thí sinh F1, F2 trong cả nước sẽ thi tốt nghiệp THPT đợt hai.\nThông tin từ Bộ GD&amp;ĐT, ngày 3/8, cho biết thí sinh tại thành phố Đà Nẵng và 6 thành phố, thị xã, huyện của tỉnh Quảng Nam (bao gồm thành phố Hội An, thị xã Điện Bàn, huyện Đại Lộc, huyện Quế Sơn, huyện Duy Xuyên và huyện Thăng Bình) đang thực hiện cách ly xã hội, sẽ thi đợt thứ hai.\n\nCác thí sinh thuộc diện F1, F2 trong cả nước (nếu có) sẽ dự thi cùng thời gian với học sinh của thành phố Đà Nẵng và 6 thành phố, thị xã, huyện của tỉnh Quảng Nam (chưa có thời gian cụ thể).\n\nCác địa phương còn lại tổ chức kỳ thi theo kế hoạch đã đề ra (từ ngày 8-10/8), với các biện pháp phòng, chống dịch bệnh theo hướng dẫn của Bộ Y tế và Bộ GD&amp;ĐT. Tại họp báo Chính phủ chiều cùng ngày, Thứ trưởng GD&amp;ĐT Nguyễn Hữu Độ cho biết thời điểm tổ chức đợt hai kỳ thi do địa phương đề xuất. Khi đó, tình hình dịch Covid-19 đã được kiểm soát.\n\nNhững thí sinh thuộc diện F1, F2 thi cùng đợt với thí sinh Đà Nẵng, Quảng Nam, khi ấy, cũng hoàn thành 14 ngày cách ly theo quy định.\n\nDo kỳ thi tốt nghiệp THPT chia thành hai đợt, việc tuyển sinh bằng kết quả thi này cũng chịu ảnh hưởng. Để đảm bảo quyền lợi thí sinh, ông Độ thông tin bộ sẽ có công văn chỉ đạo các trường, cơ sở giáo dục đại học tạo điều kiện thuận lợi, có phương thức tuyển sinh chia chỉ tiêu phù hợp cho từng đợt.\n\nNói thêm về phương án tổ chức hai đợt thi tốt nghiệp, ông Mai Tiến Dũng, Bộ trưởng, Chủ nhiệm Văn phòng Chính phủ, cho biết Thủ tướng kết luận không thể lùi kỳ thi vì còn liên quan xét tuyển đại học.\n\n“Thẩm quyền quyết định thi thuộc Bộ trưởng GD&amp;ĐT. Những địa phương không thuộc diện giãn cách sẽ thi bình thường. Tuy nhiên, trong quá trình thi, các tỉnh phải có giải pháp phòng chống dịch bệnh như ngồi giãn cách vừa đủ, đeo khẩu trang rửa tay, sát khuẩn”, ông Mai Tiến Dũng nói.\n\nNgoài ra, ông nói thêm việc xét đặc cách công nhận tốt nghiệp phải tính toán kỹ lưỡng, đặc biệt về vấn đề pháp lý.\n\nBộ trưởng Mai Tiến Dũng cho hay theo Luật Giáo dục, học sinh học hết chương trình THPT, đủ điều kiện theo quy định được dự thi, đạt yêu cầu sẽ được cấp bằng tốt nghiệp THPT. Việc xét đặc cách phải tính toán, báo cáo Quốc hội - cơ quan có thẩm quyền quyết định.\n\n“Tuy nhiên, chúng ta phải xem xét việc xét đặc cách ảnh hưởng quyền lợi thí sinh (liên quan xét tuyển đại học, cao đẳng - PV). Đây là vấn đề rất lớn”, ông Dũng nhấn mạnh.\n\nChủ nhiệm Văn phòng Chính phủ nêu ví dụ 42 trường thuộc khối công an, quân đội, sức khỏe chỉ sử dụng phương thức xét tuyển điểm thi tốt nghiệp THPT. Do đó, xét đặc cách công nhận tốt nghiệp sẽ thuận lợi cho những thí sinh không đăng ký xét tuyển đại học, cao đẳng. Nhưng với những em mong muốn dùng kết quả thi để tham gia tuyển sinh, cơ quan chức năng cần cân nhắc thêm.\n\n“Hôm nay, Bộ trưởng Phùng Xuân Nhạ đã báo cáo hết các nội dung để địa phương cân nhắc làm thế nào đảm bảo lợi ích cho thí sinh, phụ huynh”, ông Dũng nói.\n\nÔng nhấn mạnh thêm việc tổ chức thi tốt nghiệp THPT phải đảm bảo vấn đề phòng, chống dịch Covid-19, thực hiện Luật Giáo dục, lịch trình, công tác chuẩn bị của Bộ GD&amp;ĐT và các địa phương để vừa thi tốt, đồng thời không để lây nhiễm trong học sinh, giáo viên, trường học.\n\nTrước đó, sáng 3/8, kết luận phiên họp Chính phủ thường kỳ tháng 7, Thủ tướng Nguyễn Xuân Phúc giao Bộ trưởng GD&amp;ĐT quyết định kỳ thi tốt nghiệp THPT chu đáo, khoa học, không để xảy ra sơ suất.  ',0,'Bộ GD&ĐT quyết định hoãn thi tốt nghiệp THPT tại Đà Nẵng, Quảng Nam',NULL,1,43,523,'<img src=\"Assets/images/da_nang.jpg\">'),(118,7,'Có cần thiết tổ chức thi tốt nghiệp THPT trong dịch bệnh?','Trong khi nhiều giáo viên cho rằng kỳ thi tốt nghiệp THPT 2020 nên diễn ra ở những địa phương chưa có dịch bệnh, một số ý kiến khác đề nghị lùi hoặc hủy kỳ thi này.','Trong khi nhiều giáo viên cho rằng kỳ thi tốt nghiệp THPT 2020 nên diễn ra ở những địa phương chưa có dịch bệnh, một số ý kiến khác đề nghị lùi hoặc hủy kỳ thi này.\nTrước tình hình dịch Covid-19 diễn biến phức tạp, tại cuộc họp Ban chỉ đạo cấp quốc gia kỳ thi tốt nghiệp THPT 2020, đại diện Đà Nẵng, Quảng Nam đề xuất dừng thi tốt nghiệp THPT trên địa bàn.\n\nThông tin này khiến dư luận đặt ra câu hỏi có nên tiếp tục thi tốt nghiệp THPT không? <img src=\"Assets/images/thi2.jpg\"> Không thi tốt nghiệp THPT có công bằng?\nTrao đổi với Zing, cô Mai Thu Huyền, giáo viên dạy lớp 12 ở quận Thanh Xuân, Hà Nội, cho biết qua nắm bắt tâm lý học sinh, phần lớn đều muốn dự thi tốt nghiệp THPT. Đây là kỳ thi quan trọng, các em đã ôn tập trong quá trình dài.\n\n“Nếu trong thời gian nước rút, kỳ thi bị hủy, sẽ ảnh hưởng nặng nề đến tâm lý học sinh. Các em sẽ mất vài tháng nữa để lo lắng việc xét tuyển được thực hiện theo hình thức nào, có công bằng hay không”, cô Huyền nói.\n\nTrả lời VOV, cô giáo Đỗ Thị Hà, trường THPT Phong Châu, tỉnh Phú Thọ, cho rằng bỏ thi tốt nghiệp THPT là không đảm bảo công bằng đối với học sinh.\n\n“Địa phương nào không đảm bảo an toàn cho kỳ thi tốt nghiệp, Bộ GD&amp;ĐT có cách thức nào đó để tổ chức riêng hay xét riêng cho địa phương đó\", cô Hà nêu.\n\nCùng chung quan điểm này, thầy Trần Mạnh Tùng, giáo viên trường THPT Lương Thế Vinh (Hà Nội), nói những địa phương có dịch Covid-19 diễn biến phức tạp, nhiều người mắc bệnh, có thể không tổ chức thi mà xét tốt nghiệp. Kỳ thi THPT quốc gia không nên hủy với quy mô toàn quốc.\n\n\"Chúng ta có 63 tỉnh, thành và tình hình đang được kiểm soát tương đối tốt, đặc biệt những tỉnh chưa có ca nhiễm. Những tỉnh đó, nếu đề nghị dừng tổ chức thi, sẽ thiếu công bằng và không đảm bảo được yếu tố xét vào đại học thuận lợi cho các trường. Vì vậy, những tỉnh chưa có diễn biến phức tạp, kiểm soát được tình hình, vẫn có thể đảm bảo tổ chức kỳ thi an toàn thì nên tiến hành thi\", thầy Tùng nêu quan điểm.  ',0,'co-can-thiet-to-chuc-thi-tot-nghiep-thppt-trong-dich-benh',NULL,1,42,245,'<img src=\"Assets/images/thi2.jpg\">'),(120,5,'Nữ sinh Bách Khoa học giỏi, thích làm MC','Nguyễn Ngọc Yến là sinh viên Viện Toán ứng dụng và Tin học, Đại học Bách khoa Hà Nội','Nguyễn Ngọc Yến là sinh viên Viện Toán ứng dụng và Tin học, Đại học Bách khoa Hà Nội. Nữ sinh giành nhiều giải thưởng khoa học, làm MC cho các hoạt động của trường. <img src=\"Assets/images/71936140_149070339670996_2876356142345224192_o.jpg\"> Nguyễn Ngọc Yến, sinh năm 1997, là sinh viên năm cuối Viện Toán ứng dụng và Tin học, Đại học Bách khoa Hà Nội. Nữ sinh là gương mặt MC quen thuộc trong suốt 5 năm cho các chương trình của Đại học Bách Khoa Hà Nội. <img src=\"Assets/images/bach_khoa1.jpg\"> Tự tin và năng động, song Yến từng có khoảng thời gian \"chật vật\" khi làm quen với môi trường đại học. Khối lượng chương trình ở Đại học Bách Khoa nặng, lại thêm việc dành nhiều thời gian cho câu lạc bộ, hoạt động MC, khiến kết quả học tập của 9X không như mong muốn. <img src=\"Assets/images/67037568_130336584877705_2962056665639157760_o.jpg\"> \"Mãi đến năm thứ ba, suy nghĩ thật kỹ về mục đích vào Đại học Bách Khoa Hà Nội, mình bắt đầu chỉn chu học tập và quyết tâm sửa sai để trở thành kỹ sư đúng nghĩa\", Yến chia sẻ với Zing. <img src=\"Assets/images/68860376_138260524085311_5373979637577678848_o.jpg\"> Xuất thân là học sinh chuyên Vật lý, trường THPT chuyên Khoa học Tự nhiên (Đại học Quốc gia Hà Nội), 9X thích tìm hiểu về khoa học, kỹ thuật. Nữ sinh từng đoạt giải nhì sinh viên nghiên cứu khoa học với đề tài \"Xử lý ngôn ngữ tự nhiên\", có bài báo đăng tải cùng chủ đề trên tạp chí khoa học IEEE, bảo vệ thành công khóa luận với điểm số 9.5/10. <img src=\"Assets/images/68345784_136013560976674_3733823346261884928_o.jpg\"> Nữ sinh còn là đội trưởng Đội Văn nghệ Thể thao, Ban Thanh niên Tình nguyện; người dẫn phòng truyền thống nhà trường trong 3 năm liên tiếp. Có khả năng ca hát và dẫn chương trình, Yến tham gia các chương trình như chào tân sinh viên, ngày hội tư vấn tuyển sinh Open Day... <img src=\"Assets/images/68345784_136013560976674_3733823346261884928_o.jpg\"> Khoảng thời gian học Bách khoa giúp nữ sinh rèn luyện các kỹ năng giải quyết vấn đề, áp dụng kiến thức trong đời sống, tìm hiểu các công nghệ mới. Học tập trong môi trường đặc thù dành cho phái mạnh, 9X luôn được các bạn trong lớp ưu tiên và giúp đỡ. <img src=\"Assets/images/97345657_248824039695625_5520588084233633792_o.jpg\"> \"Với mình, môi trường kỹ thuật ở Đại học Bách Khoa Hà Nội không có sự khác biệt nhiều giữa nam và nữ. Thậm chí, các bạn nữ, nếu chăm và có phương pháp học đúng đắn, việc đạt tấm bằng khá, giỏi là điều hoàn toàn có thể\", Yến nói. <img src=\"Assets/images/68345784_136013560976674_3733823346261884928_o.jpg\"> Sau khi bảo vệ tốt nghiệp, Yến tiếp tục trau dồi, học thêm ngoại ngữ, dành thời gian nghỉ ngơi. Nữ sinh hy vọng thời gian tới sẽ tìm được công việc phù hợp, tiếp xúc công nghệ và có cơ hội được giao lưu, kết nối.  ',0,'nu-sinh-ngoai-thuơng-gioi-tieng-anh-thich-lam-clip-day-ngoai-ngu',NULL,1,3,653,'<img src=\"Assets/images/97345657_248824039695625_5520588084233633792_o.jpg\">'),(121,5,'Nữ sinh Ngoại thương giỏi tiếng Anh, thích làm clip dạy ngoại ngữ','Bùi Phương Anh đạt kết quả học tập loại giỏi tại ĐH Ngoại thương (Hà Nội). Nữ sinh mong muốn truyền cảm hứng học tiếng Anh cho các bạn trẻ.','Bùi Phương Anh đạt kết quả học tập loại giỏi tại ĐH Ngoại thương (Hà Nội). Nữ sinh mong muốn truyền cảm hứng học tiếng Anh cho các bạn trẻ. <img src=\"Assets/images/61512582_1532758750192021_5382713350494879744_o.jpg\"> Bùi Phương Anh, sinh năm 1999, là sinh viên ngành Tiếng Anh Thương mại, ĐH Ngoại thương (Hà Nội). Nữ sinh được nhiều người biết tới khi sở hữu kênh YouTube chia sẻ về làm đẹp và học tiếng Anh có gần 30.000 người theo dõi. <img src=\"Assets/images/109520288_1934565043344721_4910164851683936501_o_1_.jpg\"> Ngay từ năm nhất đại học, Phương Anh tham gia các câu lạc bộ trong và ngoài trường. 9X giữ vai trò chủ tịch của club Hanoi Free Tour By Foot, dành cho sinh viên yêu thích, rèn luyện tiếng Anh thông qua việc dẫn tour miễn phí cho du khách nước ngoài. <img src=\"Assets/images/89252200_1805744762893417_2372405729467301888_o.jpg\"> Chia sẻ với Zing về niềm đam mê ngoại ngữ, Phương Anh cho biết cô tự học, trau dồi hàng ngày qua mạng. <img src=\"Assets/images/85218756_1793792220755338_7949767826745589760_o.jpg\"> \"Bí quyết của mình là học trong 10.000 giờ, không nản chí. Trừ khi ngủ, mình thường xuyên bật âm thanh để nghe. Sau khi nghe theo, nhại giọng, mình bắt đầu học phát âm từ đầu\", Phương Anh nói. <img src=\"Assets/images/85218756_1793792220755338_7949767826745589760_o.jpg\"> Phương Anh từng đạt 27 điểm khi thi Đại học Ngoại thương. Có nền tảng tiếng Anh tốt, nữ sinh không gặp nhiều khó khăn khi bước vào môi trường đại học. Hết năm nhất, điểm GPA trung bình của 9X đạt 3.2/4. Ngoài ra, cô đoạt giải nhì cuộc thi \"Hùng biện tiếng Anh online\" do ĐH Ngoại ngữ tổ chức. <img src=\"Assets/images/74297873_1917317711736121_5878224572711229321_o.jpg\"> Vừa cân bằng việc học, vừa làm clip, đôi lúc, cô gái không sắp xếp được thời gian. May mắn có bố mẹ động viên, lại đam mê, Phương Anh cố gắng thực hiện song song hai công việc. <img src=\"Assets/images/64657611_1549660935168469_533103770414874624_o.jpg\"> Tự nhận bản thân là người sống quy tắc, Phương Anh lập thời gian biểu, hoàn thành công việc theo kế hoạch. Nữ sinh làm thêm để trang trải thu nhập. Thời gian rảnh, 9X đọc sách, tài liệu, tìm nguồn cảm hứng, sáng tạo nội dung mới. <img src=\"Assets/images/k.jpg\"> Chia sẻ về dự định tương lai, nữ sinh Ngoại thương cho biết sẽ tập trung hoàn thành việc học. Đồng thời, cô gái mong muốn truyền kiến thức, cảm hứng cho những bạn trẻ sợ tiếng Anh trong thời gian tới.  ',0,'nu-sinh-ngoai-thuơng-gioi-tieng-anh-thich-lam-clip-day-ngoai-ngu',NULL,1,52,234,'<img src=\"Assets/images/74297873_1917317711736121_5878224572711229321_o.jpg\">'),(122,12,'Bệnh nhân 426, 496 tử vong vì suy thận mạn tính và mắc Covid-19','Thông tin về sức khỏe các ca mắc Covid-19 khác, Thứ trưởng Nguyễn Trường Sơn cho biết một số trường hợp cũng có nguy cơ tử vong do bệnh nền nặng và cao tuổi.','Hai bệnh nhân ở Đà Nẵng tử vong vì suy thận mạn tính giai đoạn cuối và mắc Covid-19.\n  Sáng 4/8, Thứ trưởng Bộ Y tế Nguyễn Trường Sơn thông tin Việt Nam có thêm hai người mắc Covid-19 tử vong. Đây là các bệnh nhân tại Đà Nẵng, cao tuổi và có tiền sử suy thận mạn tính.\n  \n  Bệnh nhân 426 là nữ, 62 tuổi, ở Hòa Vang, Đà Nẵng. Người này có tiền sử suy thận mạn tính 10 năm.\n  \n  Trong thời gian 18-27/7, BN426 điều trị tại khoa Nội thận, Bệnh viện Đà Nẵng. Đến 30/7, người bệnh được chuyển sang Bệnh viện Trung ương Huế.\n  \n  Ngày 31/7, bà tiếp xúc kém, ăn uống kém, khó thở nhẹ. Một ngày sau, người phụ nữ này phải thở máy, mê man, huyết áp tụt. 17h ngày 1/8, người bệnh bị suy đa tạng, choáng nhiễm trùng, nhiễm độc.\n  \n  Từ 2/8 đến 4/8, tình trạng của người phụ nữ này diễn biến xấu, hôn mê sâu, mạch rời rạc, huyết áp tụt dần, ngừng tim. 2h30 sáng 4/8, bệnh nhân tử vong.\n  \n  Các bác sĩ chẩn đoán trường hợp này tử vong do suy thận mạn tính giai đoạn cuối, sốc nhiễm trùng, suy đa tạng và mắc Covid-19. <img src=\"Assets/images/dohoa.jpg\"> Bệnh nhân 496 là nam, 65 tuổi, trú tại Hòa Vang, Đà Nẵng. Ông bị suy thận mạn tính giai đoạn cuối, thận nhân tạo chu kỳ, nhiễm khuẩn huyết. Người đàn ông này điều trị tại khoa Nội thận, Bệnh viện Đà Nẵng, trong 5 tháng và ra viện ngày 24/7.\n  \n  Ngày 28/7, bệnh nhân có kết quả dương tính với SARS-CoV-2, được chuyển đến khoa Y học Nhiệt đới, Bệnh viện Đà Nẵng. Lúc này, ông được các bác sĩ đặt nội khí quản và thở máy.\n  \n  Từ 29/7 đến 1/8, ông nằm yên dưới tác dụng thuốc an thần và thở máy. Ngày 2/8, bệnh nhân phải thở máy, lọc máu liên tục và huyết áp giảm.\n  \n  7h45 ngày 4/8, ông hôn mê, đồng tử giãn, mất hoàn toàn phản xạ. Đến 8h30 cùng ngày, bệnh nhân tử vong.\n  \n  Nguyên nhân tử vong được xác định là suy thận mạn tính giai đoạn cuối, nhiễm trùng huyết, suy tim cấp và mắc Covid-19.\n  \n  Thông tin về sức khỏe các ca mắc Covid-19 khác, Thứ trưởng Nguyễn Trường Sơn cho biết một số trường hợp cũng có nguy cơ tử vong do bệnh nền nặng và cao tuổi.\n  \n  Trước đó, Việt Nam có 4 người mắc Covid-19 ở Đà Nẵng và 2 trường hợp tại Quảng Nam tử vong. Các trường hợp này có độ tuổi từ 50 đến 90 và đang điều trị nhiều bệnh nền nguy hiểm như suy thận mạn tính, suy tim, ung thư máu giai đoạn cuối...\n  \n  Trong 11 ngày (25/7-4/8), Việt Nam phát hiện 239 trường hợp mắc Covid-19 với 205 ca trong nước và 34 người nhập cảnh.\n  \n  9 tỉnh, thành phố đang có dịch gồm: Đà Nẵng (139 ca), Quảng Ngãi (4), Quảng Nam (46), Hà Nội (2), TP.HCM (8), Đắk Lắk (3), Thái Bình (1), Đồng Nai (1), Hà Nam (1). Đa số bệnh nhân có mối liên quan đến tâm dịch Đà Nẵng.  ',0,'benh-nhan-426-496-tu-vong-vi-suy-than-man-tinh-va-mac-covid-19',NULL,1,43,543,'<img src=\"Assets/images/dohoa.jpg\">'),(123,12,'Thêm 30 ca mắc Covid-19 tại 6 tỉnh, thành','Đáng chú ý, trong số 30 bệnh nhân Covid-19 vừa ghi nhận, một trường hợp 100 tuổi là BN592. Đây là người nhiễm SARS-CoV-2 cao tuổi nhất ở nước ta hiện nay.','18h ngày 2/8, Bộ Y tế cho biết Việt Nam ghi nhận thêm 30 ca mắc Covid-19, bao gồm 29 bệnh nhân trong nước và một người nhập cảnh.\nCác bệnh nhân (từ 591 đến 620) ghi nhận trên 6 tỉnh, thành: Đà Nẵng (16), Quảng Nam (9), Đắk Lắk (2), Đồng Nai (1), Khánh Hòa (1), Hà Nam (1).\n\nCa mắc từ 591 đến 595 là những bệnh nhân đang điều trị và thân nhân đến thăm nuôi tại các bệnh viện ở Đà Nẵng.\n\nBệnh nhân 596 là nhân viên y tế làm việc tại phòng khám tư. Người này đến Bệnh viện C Đà Nẵng thăm bệnh.\n\nBN597-602 có tiếp xúc gần bệnh nhân mắc Covid-19 trước đó. BN603 là du học sinh Mỹ, được cách ly ngay khi nhập cảnh tại Khánh Hòa.\n\nNhững người còn lại (từ 604 đến 620) sống trên địa bàn TP Đà Nẵng, được cơ sở y tế địa phương lấy mẫu xét nghiệm.\n\nĐáng chú ý, trong số 30 bệnh nhân Covid-19 vừa ghi nhận, một trường hợp 100 tuổi là BN592. Đây là người nhiễm SARS-CoV-2 cao tuổi nhất ở nước ta hiện nay.\n\nThông tin về dịch tễ của những bệnh nhân này đang được điều tra, cập nhật. <img src=\"Assets/images/Duy_Hieu_2.jpg\"> Đến nay, Việt Nam có tổng cộng 620 ca mắc Covid-19, trong đó, 307 ca nhiễm nhập cảnh được cách ly ngay, 373 người đã khỏi bệnh.\n\nTrong 9 ngày (25/7-2/8), Việt Nam đã phát hiện 207 trường hợp mắc Covid-19 với 173 bệnh nhân trong nước và 34 người nhập cảnh.\n\nChín tỉnh, thành đang ghi nhận có dịch gồm: Đà Nẵng (117 trường hợp), Quảng Ngãi (3 trường hợp), Quảng Nam (37 trường hợp), Hà Nội (2 trường hợp), TP.HCM (8 trường hợp), Đắk Lắk (3 trường hợp), Thái Bình (1 trường hợp), Đồng Nai (1 trường hợp), Hà Nam (1 trường hợp).  ',0,'benh-nhan-426-496-tu-vong-vi-suy-than-man-tinh-va-mac-covid-19',NULL,1,653,123,'<img src=\"Assets/images/Duy_Hieu_2.jpg\">'),(124,12,'Bộ Y tế đề nghị tăng cường xét nghiệm Covid-19 cho người dân','Bộ Y tế đề nghị các địa phương khẩn trương xây dựng kịch bản, kế hoạch điều tra, giám sát tất cả người trở về từ Đà Nẵng.','Trước diễn biến phức tạp của dịch Covid-19, Bộ Y tế vừa có công văn đề nghị UBND các tỉnh, thành phố về việc tăng cường xét nghiệm Covid-19.\nBộ Y tế đề nghị UBND tỉnh, thành phố chỉ đạo đơn vị trực thuộc trong và ngoài ngành y tế đầu tư trang thiết bị, cơ sở vật chất, nhân lực, sinh phẩm cho các phòng xét nghiệm trên địa bàn để đảm bảo chẩn đoán người mắc Covid-19 tại chỗ, đồng thời hỗ trợ các địa phương khác.\n\nCác đơn vị đủ năng lực cần thực hiện xét nghiệm chẩn đoán virus SARS-CoV-2, không chờ Viện Vệ sinh Dịch tễ Trung ương (Hà Nội), Viện Pasteur TP.HCM đánh giá, thẩm định. Nếu muốn thực hiện xét nghiệm khẳng định, các đơn vị liên hệ hai viện này. <img src=\"Assets/images/VLZ_2621_1_1.jpg\"> Các đơn vị đã được xác nhận đủ năng lực xét nghiệm khẳng định không cần gửi mẫu đến các cơ sở khác trong trường hợp kết quả dương tính.\n\nBộ Y tế đề nghị UBND các tỉnh, thành huy động những đơn vị y tế tư nhân có đủ năng lực xét nghiệm tham gia vào công cuộc xét nghiệm virus SARS-CoV-2.\n\nTheo thống kê từ đầu tháng 7 đến nay, hơn 800.000 người đã đến Đà Nẵng và trở về các địa phương. Trong đó, hơn 41.000 người đã đến 3 bệnh viện là tâm dịch tại Đà Nẵng.\n\nBộ Y tế đề nghị các địa phương khẩn trương xây dựng kịch bản, kế hoạch điều tra, giám sát tất cả người trở về từ Đà Nẵng. Đặc biệt, địa phương cần rà soát các địa điểm Bộ Y tế cảnh báo, đảm bảo không để dịch xảy ra trên địa bàn.\n\nNhiều địa phương đang tăng cường xét nghiệm cho những người đi về từ Đà Nẵng. Chiều 1/8, ông Nguyễn Đức Chung, Chủ tịch UBND Hà Nội cho biết đến hiện tại, thành phố rà soát được hơn 72.000 người đến TP Đà Nẵng. Hà Nội đã xét nghiệm trên 49.000 trường hợp, 127 người tiếp xúc gần (F1) đều âm tính.\n\nDự kiến, trong ngày mai (2/8), Hà Nội hoàn thành test nhanh cho những người trở về từ vùng dịch. Thành phố ưu tiên những người về từ ngày 12/7 trở lại đây bởi số test nhanh của Hà Nội đã phát 78.000/80.000.\n\nTuy nhiên, ông Chung cũng nêu rõ khi test nhanh phát hiện kháng thể cho kết quả âm tính, chúng ta vẫn chưa thể yên tâm 100%. Các trường hợp này phải cách ly, tự theo dõi sức khỏe. Khi có biểu hiện sốt, ho, người dân phải báo ngay cơ sở y tế để lấy mẫu xét nghiệm RT-PCR.  ',0,'bo-y-te-de-nghi-tang-cuong-xet-nghiem-covid-19-cho-nguoi-dan',NULL,1,13,324,'<img src=\"Assets/images/VLZ_2621_1_1.jpg\">'),(125,5,'Triển lãm ôtô quốc tế duy nhất tổ chức trong dịch Covid-19','Trong khi các triển lãm ôtô quốc tế ở nhiều nơi phải hoãn hoặc hủy bỏ.','Bất chấp việc dịch Covid-19 còn đang khá phức tạp, Bangkok International Motor Show 2020 vẫn được tổ chức với lượng khách tham quan hơn một triệu người.\nTrong khi các triển lãm ôtô quốc tế ở nhiều nơi phải hoãn hoặc hủy bỏ như Geneva International Motor Show, North American International Auto Show, Auto China, and International Motor Show Germany (IAA), thì triển lãm Bangkok International Motor Show (Triển lãm Ôtô Quốc tế Bangkok) năm 2020 vẫn được tổ chức giữa điều kiện dịch COVID-19 còn đang hoành hành trên thế giới.\n\nTheo đó, nhà tổ chức của triển lãm ôtô tại Thái Lan này đã thông báo số lượt tham quan năm nay đạt hơn một triệu người với lượng đặt hàng hơn 18.000 chiếc xe trong khoảng thời gian diễn ra triển lãm từ ngày 13/7 đến 26/7. <img src=\"Assets/images/trien_lam_oto_bangkok_mpks.jpg\"> Nhà tổ chức tin tưởng sự thành công của triển lãm Bangkok International Motor Show nhờ vào những biện pháp phòng ngừa và quy trình sàng lọc theo tiêu chuẩn quốc tế.\n\nÔng Jaturont Komolmis - Phó chủ tịch của Triển lãm Ôtô Quốc tế Bangkok - cho biết: \"Năm nay, Triển lãm Ôtô Quốc tế Bangkok lần thứ 41 đã có số lượng khách tham quan là 1.049.046, con số cao này một phần nhờ chuyến thăm của Tiến Sĩ Satit Pitutecha, Thứ trưởng Bộ Y tế Công Cộng cùng với Tiến sĩ Suwannachai Wattanayingcharoenchai, Cục trưởng Cục Kiểm soát Dịch bệnh\". <img src=\"Assets/images/trien_lam_oto_bangkok_2_qbmi.jpg\"> Ngoài ra, triển lãm này đạt tổng cộng 18.381 lượt đặt hàng xe kéo dài trong suốt 2 tuần, số lượng được cho lớn hơn dự đoán của nhiều chuyên gia trong ngành ôtô. Đa số các loại xe được đặt đều là MPV và SUV với giá từ 1 triệu baht (khoảng 742 triệu đồng).\n\nTrong đó, dẫn đầu trong số các hãng nhận được lượng đặt hàng nhiều nhất tại Triển lãm Ôtô Quốc tế Bangkok năm 2020 là Toyota với 3.745 lượt, trong khi BMW đứng đầu trong số các hãng xe sang với 888 lượt đặt hàng. <img src=\"Assets/images/trien_lam_oto_bangkok_3_vtzo.jpg\"> Kết quả từ Triển lãm Ôtô Quốc tế Bangkok lần thứ 41 cho thấy các triển lãm ôtô vẫn còn rất quan trọng và người tiêu dùng vẫn muốn có các tương tác vật lý thay vì những trải nghiệm ảo qua Internet. Kết hợp với sức mạnh của nền công nghiệp ôtô Thái Lan, các nhà tổ chức kỳ vọng điều này sẽ giúp đảo ngược xu hướng giảm trước đây và kích thích thị trường ôtô trong nửa cuối năm nay.  ',0,'trien-lam-oto-quoc-te-duy-nhat-to-chuc-trong-dich-covid-19',NULL,1,523,524,'<img src=\"Assets/images/trien_lam_oto_bangkok_mpks.jpg\">'),(127,12,'Dịch Covid-19 ở Đà Nẵng, Quảng Nam được kiểm soát','Trên thế giới, rất nhiều nước do lơi lỏng, dịch bệnh đã quay lại, bùng phát thành làn sóng mới, đe dọa rất nghiêm trọng.','Nhận định tình hình ở Đà Nẵng và Quảng Nam đang được kiểm soát, nhưng Ban chỉ đạo quốc gia dự báo sẽ còn phát hiện các ca nhiễm trong cộng đồng và ca tử vong do bệnh nền nặng.\nNgày 4/8, Phó thủ tướng Vũ Đức Đam chủ trì phiên họp của Ban chỉ đạo quốc gia phòng, chống dịch Covid-19.\n\nTại cuộc họp này, Ban chỉ đạo đã bàn về nội dung dự thảo Chỉ thị của Thủ tướng về tăng cường, nâng cao hiệu quả thực hiện các biện pháp phòng, chống dịch Covid-19 trong giai đoạn mới; công tác mua sắm vật tư, trang thiết bị y tế, máy móc phục vụ phòng chống dịch; điều chỉnh giá xét nghiệm; vấn đề đặt máy, thuê máy phục vụ phòng chống dịch…\n\nChưa có bằng chứng khẳng định dịch lây mạnh trong cộng đồng\nGS.TS Đặng Đức Anh, Viện trưởng Viện Vệ sinh dịch tễ Trung ương, cho biết trên địa bàn Đà Nẵng, viện đã tiến hành xét nghiệm kháng thể 5.000/7.000 mẫu ở các khu cách ly và trong cộng đồng.\n\nTrên cơ sở kết quả thu được, có căn cứ để xác định các mẫu bị nhiễm vào khoảng đầu tháng 7 và ổ dịch ở Đà Nẵng khởi phát vào đầu tháng này. Thời gian tới, Viện Vệ sinh dịch tễ Trung ương sẽ tiếp tục thu thập mẫu, xét nghiệm để phục vụ công tác phòng, chống dịch bệnh. <img src=\"Assets/images/DDN_8793.jpeg\"> Theo PGS.TS Trần Đắc Phu, căn cứ trên xét nghiệm các ca mắc và qua điều tra dịch tễ, có thể thấy số ca lây lan ra ngoài cộng đồng chưa nhiều. Đến nay, mới phát hiện 6 ca cộng đồng. Hiện chưa có các trường hợp nào bị lây từ các bệnh nhân này. Về cơ bản, các ca nhiễm đều liên quan đến 3 bệnh viện ở Đà Nẵng.\n\nQua xét nghiệm tại Hà Nội, TP.HCM và một số cơ sở y tế lớn, chưa phát hiện các ca mắc Covid-19 ngoài cộng đồng, trừ các ca liên quan đến Đà Nẵng. Như vậy, chưa có bằng chứng để khẳng định dịch bệnh lây nhiễm rất mạnh ngoài cộng đồng.\n\nGS.TS Nguyễn Thanh Long, quyền Bộ trưởng Bộ Y tế, nhắc lại thời điểm bắt đầu dịch là từ tháng 7. Đặc biệt, qua phân tích gen của virus gây bệnh trên các bệnh nhân, có thể khẳng định đây là nguồn mới xâm nhập, từ một điểm phát ra (khu 3 bệnh viện ở Đà Nẵng)… Do virus lần này đã đột biến, dẫn tới tình trạng lây nhiễm trong gia đình, lây nhiễm chéo cao.\n\nBộ Y tế sẽ tăng quân cho Đà Nẵng, Quảng Nam\nVề công tác phòng, chống dịch trong thời gian tới, ông Nguyễn Thanh Long cho biết tới đây Bộ Y tế sẽ tập trung chỉ đạo, hỗ trợ chống dịch cho Quảng Nam như đã làm với Đà Nẵng.\n\nBộ Y tế sẽ tiếp tục “tăng quân” vào khu vực này, đẩy nhanh hơn nữa tiến độ truy tìm các trường hợp lây nhiễm trong cộng đồng để kịp thời cách ly, khoanh vùng, dập dịch, nhanh chóng kiểm soát tình hình, giảm thiểu tử vong.\n\n <img src=\"Assets/images/DDN_8818.jpeg\"> Theo ý kiến của các thành viên Ban chỉ đạo, kết quả ngăn chặn dịch bệnh vừa qua cho thấy các chỉ đạo, quyết sách chống dịch là đúng.\n\nBan chỉ đạo thống nhất nhận định tình hình ở Đà Nẵng và Quảng Nam đang được kiểm soát. Dự báo trong những ngày tới đây sẽ tiếp tục phát hiện các ca nhiễm trong cộng đồng và nhiều khả năng sẽ còn các ca tử vong là những bệnh nhân nặng đang điều trị tại các khoa chạy thận nhân tạo, hồi sức cấp cứu, tim mạch.\n\nBan chỉ đạo cũng nhấn mạnh với gần 100 triệu dân, đường biên giới dài hơn 4.000 km nên nguy cơ có dịch luôn thường trực trong cộng đồng. Vì vậy, phải thiết lập trạng thái bình thường mới với từng người dân, gia đình, tổ chức và toàn xã hội.\n\nTheo đó, đất nước vẫn sản xuất, kinh doanh, giải trí, làm những việc cần thiết trong cuộc sống, nhưng phải trong điều kiện mới, cách làm mới để bảo đảm an toàn, phòng, chống dịch.\n\nCuộc chiến còn dài\nPhát biểu kết luận cuộc họp, Phó thủ tướng Vũ Đức Đam yêu cầu trong thời gian tới, một mặt phải tập trung dập dịch nhanh nhất có thể. Mặt khác cũng phải siết chặt lại kỷ cương, “lên dây cót” cả hệ thống, trước hết trong ngành y tế.\n\n“Cuộc chiến này còn dài đến khi nào có vaccine, thuốc đặc trị. Chúng ta mới thắng từng trận đánh, từng chiến dịch chứ chưa thắng cả cuộc chiến”, Phó thủ tướng nhấn mạnh.\n\nTrên thế giới, rất nhiều nước do lơi lỏng, dịch bệnh đã quay lại, bùng phát thành làn sóng mới, đe dọa rất nghiêm trọng. Dịch bệnh ở thành phố Đà Nẵng thời gian qua là lời cảnh báo rất nghiêm khắc cho tất cả các bệnh viện, các địa phương, các ngành, các cấp. Phó thủ tướng ví phòng, chống dịch giống như bảo vệ tuyến đê trong mùa lũ, điểm xung yếu nhất phải được canh giữ cẩn trọng nhất. Mọi rò rỉ, nhất là ở nơi xung yếu, phải được phát hiện ngay và bịt lại.\n\n“Chúng ta phải thực hiện triệt để các chỉ đạo về quản lý sức khỏe của người dân, đặc biệt người cao tuổi, có bệnh nền, siết lại kỷ cương trong các bệnh viện, thực hiện nghiêm, triệt để quy định phòng dịch đối với mọi người đến khám bệnh và nhân viên y tế”, Phó thủ tướng lưu ý và cho rằng mỗi sự cố xảy ra là một bài học và không được để điều đó lặp lại.\n\nPhó thủ tướng yêu cầu Bộ Y tế rà soát lại toàn bộ quy định, hướng dẫn phòng, chống dịch. Theo ông, nếu thực hiện tốt, chúng ta hoàn toàn có lòng tin sẽ chống được dịch bệnh, có cuộc sống, sản xuất kinh doanh bình thường mới.\n\n“Ở đâu đó có làn sóng dịch bệnh thứ hai nhưng Việt Nam quyết tâm không để xảy ra. Không để câu chuyện quay lại giãn cách xã hội trên diện rộng, ở quy mô toàn quốc. Chúng ta phải khoanh vùng nhỏ nhất có thể, còn những nơi khác phải ở trong trạng thái bình thường mới để phát triển”, Phó thủ tướng nhấn mạnh.  ',0,'dich-covid-19-o-da-nang-quang-nam-duoc-kiem-soat',NULL,1,54,34,'<img src=\"Assets/images/DDN_8793.jpeg\">'),(129,2,'Người Việt Nam tay gãy làm 3, bàng hoàng kể vụ nổ ở Beirut','Công dân người Việt Nam chưa hết bàng hoàng sau vụ nổ hơn 2.700 tấn ammonium nitrate xé toạc thủ đô Beirut của Lebanon.','Vụ nổ tại thủ đô Beirut của Lebanon hôm 4/8 khiến hơn 100 người thiệt mạng và khoảng 4.000 người bị thương. Một công dân Việt Nam gặp nạn trong vụ nổ là chị Đặng Huyền Nga, quê Thái Nguyên. Cho tới nay, chị Nga vẫn chưa hết bàng hoàng sau vụ việc.\n\n\"Tôi vào viện từ hôm qua (4/8) nhưng tới giờ vẫn chưa được bó bột hay làm gì cả. Tôi bị thương nhiều chỗ nên đau lắm. Bác sỹ chụp bảo gẫy tay, gẫy làm 3 đoạn. Tôi cũng bị nhiều vết thương ở đầu và vai\", chị Nga cho hay.\n\nHiện Đại sứ quán Việt Nam tại Ai Cập kiêm nhiệm Lebanon đang phối hợp với cơ quan chức năng tại Beirut để cập nhật tình hình sau vụ nổ và triển khai kịp thời công tác bảo hộ công dân của Việt Nam. <img src=\"Assets/images/Beirut_3.jpg\"> \"Chúng tôi tiếp tục làm việc với cơ quan Bộ Ngoại giao Lebanon để nắm rõ xem còn công dân Việt Nam nào bị ảnh hưởng trong vụ nổ này không, đồng thời sẽ rà soát lại qua các đầu mối cộng đồng người Việt Nam tại Lebanon. Cơ quan lãnh sự danh dự Việt Nam tại Lebanon sẽ phải phối hợp với các cơ quan chức năng, chính quyền tại Beirut xem tình hình công dân Việt Nam tại đây.\n\nNếu có trường hợp đặc biệt chúng tôi sẽ can thiệp với cơ quan chức năng và chỉ đạo cơ quan lãnh sự danh dự có các biện pháp bảo hộ công dân kịp thời và cấp bách. Nếu có trường hợp nào có thể khó khăn hoặc nặng hơn chúng tôi sẽ xin phép để cử đoàn sang Lebanon để hỗ trợ công dân\", Đại sứ Việt Nam tại Ai Cập kiêm nhiệm Lebanon Trần Thành Công cho biết.\n\nTrước đó, theo xác nhận của giới chức Lebanon, khoảng 2.700 tấn ammonium nitrate đã phát nổ bên trong cảng Beirut hôm 4/8 gây ra vụ nổ làm rung chuyển thủ đô Beirut.\n\nVụ nổ phá hủy mọi thứ ở bán kính 1 km và thiệt hại bao trùm bán kính 15km.\n\nKhu vực phía Đông của cảng Beirut bị phá hủy hoàn toàn trong khi hiện trường vụ nổ giờ là một đống đổ nát và hoang tàn. Các bệnh viện ở Beirut rơi vào tình trạng quá tải khi vừa phải điều trị cho các bệnh nhân Covid-19 vừa tiếp nhận người bị thương.\n\nCác nhân viên cứu hộ của Lebanon đang phải đào bới đống đổ nát để tìm kiếm những người sống sót khi mà Hội Chữ thập đỏ Lebanon cảnh báo số người chết sẽ còn tăng cao.\n\nThống đốc Beirut Marwan Abboud ước tính thiệt hại từ vụ nổ rơi vào khoảng 3-5 tỷ USD, đồng thời xác nhận về cái chết của 10 nhân viên cứu hộ sau thảm kịch.  ',0,'nguoi-viet-nam-tay-gay-lam-3-bang-hoang-ke-vu-no-o-beirut',NULL,1,524,215,'<img src=\"Assets/images/Beirut_3.jpg\">'),(130,12,'Bệnh nhân 651 tử vong vì suy thượng thận mạn tính và mắc Covid-19','BN651 là nữ, 67 tuổi, ở Duy Xuyên, Quảng Nam. Bà được điều trị Covid-19 tại Bệnh viện Đà Nẵng.','<img src=\"Assets/images/Covid_19_DN_001.jpg\"> Sáng 6/8, Thứ trưởng Bộ Y tế Nguyễn Trường Sơn thông tin về trường hợp mắc Covid-19 tử vong thứ 9. Bệnh nhân N.T.H. có tiền sử suy thận mạn tính, lupus ban đỏ, đái tháo đường type II, nhiễm nấm máu.\n\nNgày 18/7, bà được đưa đến cấp cứu tại khoa Nội Thận, Bệnh viện Đà Nẵng. Sau đó, bệnh nhân được chuyển đến Bệnh viện Gia Đình và điều trị tại đây đến ngày 31/7.\n\nNgày 2/8, người phụ nữ này có kết quả dương tính với virus SARS-CoV-2 và được chuyển vào khoa Y học Nhiệt đới, Bệnh viện Đà Nẵng.\n\nNgày 3/8, bệnh nhân lơ mơ, thể trạng suy kiệt, loét vùng cẳng tay và bàn chân, xuất huyết dưới da, khó thở nhẹ. Ngày 4/8, bà H. suy hô hấp, được đặt nội khí quản, thở máy.\n\n0h45 ngày 6/8, bệnh nhân hôn mê sâu, suy hô hấp, tím da đầu chi, nhịp tim chậm dần rồi ngưng, không đo được huyết áp, hồi sinh tim, phổi không hiệu quả. 1h30 cùng ngày, người phụ nữ này tử vong. Trước đó, Việt Nam ghi nhận 8 ca tử vong khác, đó là BN428, BN437 (ngày 31/7), BN499 (ngày 1/8), BN524, BN475, BN429 (ngày 2/8), BN426, BN496 (ngày 4/8). Họ đều lớn tuổi, mắc nhiều bệnh lý nền như suy thận mạn, cao huyết áp, tiểu đường type II...  ',0,'benh-nhan-651-tu-vong-vi-suy-thuong-than-man-tinh-va-mac-covid-19',NULL,1,236,5422,'<img src=\"Assets/images/Covid_19_DN_001.jpg\">'),(131,12,'Việc cần làm để bảo vệ bệnh nhân suy thận mạn tính không mắc Covid-19','5 bệnh nhân Covid-19 ở Việt Nam tử vong đều có tiền sử suy thận mạn tính, phải lọc máu thường xuyên.','Theo BS Trần Hồng Xinh, khoa Nội thận và Lọc máu, Bệnh viện Quân đội Trung ương 108, Hà Nội, người bị suy thận mạn tính, phải lọc máu thường xuyên được xếp vào nhóm có sức đề kháng kém, nguy cơ mắc bệnh cao.\n\nBên cạnh đó, đa phần bệnh nhân là người cao tuổi. Ngoài suy thận, họ còn có nhiều bệnh lý nền khác đi kèm như tiểu đường type II, tăng huyết áp, suy tim. Thậm chí, một số trường hợp đã dùng thuốc ức chế miễn dịch trong thời gian dài. Điều này làm giảm cơ hội sống sót của bệnh nhân khi mắc Covid-19, gây khó khăn cho quá trình điều trị.\n\nBS Trần Hồng Xinh cho biết bệnh nhân lọc máu chu kỳ tiếp xúc từ 4 đến 16 tiếng với nhân viên y tế và các người bệnh khác nên khả năng lây nhiễm SARS-CoV-2 cao hơn.\n\n <img src=\"Assets/images/chaythan.jpg\"> Để giảm nguy cơ lây nhiễm SARS-CoV-2 cho nhóm bệnh nhân này, BS Xinh khuyến cáo:\n\nCác đơn vị lọc máu cần sớm nhận biết và cách ly những người có triệu chứng về hô hấp. Bệnh nhân tới thăm khám, điều trị cần được khai báo y tế trước khi vào đơn vị lọc máu.\n\nNếu người bệnh có các triệu chứng ho, sốt cần được phân tách ở khu riêng và yêu cầu sử dụng khẩu trang trong suốt thời gian lọc máu. Nhân viên y tế nên cung cấp cho bệnh nhân các kiến thức làm sạch tay, giữ vệ sinh đường hô hấp và đeo khẩu trang đúng cách.\n\nỞ khu vực lọc máu và phòng chờ, các bệnh nhân phải đảm bảo giữ khoảng cách tối thiểu 2 m. Bề mặt ghế lọc máu và bàn làm việc của nhân viên y tế cần được làm sạch liên tục.\n\nKhi có bệnh nhân nghi nhiễm hoặc mắc Covid-19, bệnh viện phải thực hiện cách ly, thông báo toàn bộ nhân viên y tế sử dụng phương tiện phòng hộ.  ',0,'viec-can-lam-de-bao-ve-benh-nhan-suy-than-man-tinh-khong-mac-covid-19',NULL,1,235,4123,'<img src=\"Assets/images/chaythan.jpg\">'),(132,1,'Bệnh viện C Đà Nẵng gỡ phong tỏa','0h ngày 8/8, Bệnh viện C Đà Nẵng gỡ bỏ lệnh phong tỏa sau 14 ngày cách ly. Từ phía trong, nhiều cán bộ vui mừng, vẫy tay chào mọi người.','<img src=\"Assets/images/BVC_DANANG0002_zing.jpg\"> 0h ngày 8/8, Bệnh viện C Đà Nẵng gỡ bỏ lệnh phong tỏa sau 14 ngày cách ly. Từ phía trong, nhiều cán bộ vui mừng, vẫy tay chào mọi người.\nDo lenh phong toa Benh vien C Da Nang anh 1\nHơn 23h ngày 7/8, nhiều phóng viên, người dân đến khu vực cổng chính của Bệnh viện C Đà Nẵng để chứng kiến khoảnh khắc bệnh viện được dỡ lệnh phong tỏa sau 14 ngày. <img src=\"Assets/images/BVC_DANANG0001_zing.jpg\"> Lực lượng cảnh sát vẫn duy trì ca trực trước thời điểm hết cách ly. <img src=\"Assets/images/BVC_DANANG0005_zing.jpg\"> Đúng 0h, lực lượng an ninh tháo dỡ lớp barie phong tỏa trước cổng chính Bệnh viện C trong sự vui mừng của nhiều y, bác sĩ bệnh viện. <img src=\"Assets/images/BVC_DANANG0006_zing.jpg\"> Nhân viên bảo vệ mang chiếc bảng thông báo đặt phía ngoài cổng chính, thông báo bệnh viện hết lệnh phong tỏa và có thể tiếp nhận bệnh nhân. Tuy nhiên, bệnh nhân được tiếp nhận điều trị sẽ dừng lại trong khuôn khổ nhóm bệnh nhân cấp cứu, bệnh chuyển tuyến, bệnh nhân đăng ký bảo hiểm y tế khám chữa bệnh ban đầu tại Bệnh viện C Đà Nẵng đến khám ngoại trú. <img src=\"Assets/images/BVC_DANANG0007_zing.jpg\"> Dù lệnh phong tỏa được gỡ bỏ, nhưng đội ngũ y bác sĩ bệnh viện chỉ đứng phía trong khu vực cổng chính, cách xa khu vực bố trí hàng rào phong tỏa khoảng 200 m mà không ra phía ngoài. Việc không mở rộng người đến khám nhằm đảm bảo chất lượng khám chữa bệnh, tránh thiếu hụt nhân lực làm nhiệm vụ. Bởi hiện tại, nhiều y, bác sĩ của bệnh viện đang được bố trí cách ly hoặc làm công tác chăm sóc, điều trị bệnh nhân Covid-19. <img src=\"Assets/images/BVC_DANANG0014_zing.jpg\"> Điều này nhằm đảm bảo tính giãn cách nhất định giữa đội ngũ thầy thuốc và những người phía ngoài, trong bối cảnh dịch bệnh Covid-19 còn diễn biến phức tạp. Mọi người cùng hô lớn và thể hiện quyết tâm chống dịch Covid-19. <img src=\"Assets/images/BVC_DANANG00014_zing.jpg\"> Nhiều người đang cách ly tại các tòa nhà của bệnh viện vẫn mở đèn sáng lúc 0h và theo dõi khoảnh khắc nơi này được gỡ lệnh phong tỏa. Bệnh viện C là nơi đầu tiên phát hiện ca mắc Covid-19 ở Đà Nẵng, ngày 24/7. Sau đó, nơi này được phong tỏa với hơn 1.000 người bên trong. <img src=\"Assets/images/BVC_DANANG0011_zing.jpg\"> Khá đông phóng viên đến tác nghiệp ở khu vực phía ngoài rào chắn trước cổng bệnh viện. <img src=\"Assets/images/BVC_DANANG0003_zing.jpg\"> Ghi nhận của Zing sau 0h ngày 8/8, dù Bệnh viện C Đà Nẵng được dỡ lệnh cách ly, tuy nhiên để đảm bảo an toàn phòng dịch Covid-19, ngành chức năng vẫn chưa dỡ rào chắn trước cổng chính bệnh viện. Điều này nhằm tiếp tục kiểm soát số người đến bệnh viện. Chỉ những cán bộ, chiến sĩ làm công tác phòng chống dịch mới được ưu tiên di chuyển qua lại cổng rào.  ',0,'benh-vien-c-da-nang-go-phong-toa',NULL,1,52,324,'<img src=\"Assets/images/BVC_DANANG0002_zing.jpg\">'),(133,1,'Làm sao để Hà Nội xét nghiệm RT-PCR cho tất cả người từ Đà Nẵng về?','Theo GS Nguyễn Lân Hiếu, để xét nghiệm RT-PCR số lượng lớn cần có chiến lược xét nghiệm hiệu quả và đảm bảo chất lượng tốt nhất, ưu tiên xét nghiệm cho những người có nguy cơ cao.','Tại cuộc họp về phòng, chống dịch Covid-19 của Ban chỉ đạo thành phố hôm 6/8, Chủ tịch Hà Nội Nguyễn Đức Chung yêu cầu toàn bộ người về từ Đà Nẵng từ ngày 15/7, phải được xét nghiệm bằng phương pháp RT-PCR.\n\nÔng dự báo Hà Nội sẽ cần 60.000-65.000 xét nghiệm RT-PCR trong thời gian tới. Song để thực hiện xét nghiệm RT-PCR với số lượng người rất lớn là việc không đơn giản. <img src=\"Assets/images/testnhanhcovid1_VL_zing.jpg\"> Nhóm bác sĩ Bệnh viện Đại Học Y Hà Nội đã nghiên cứu và đưa ra góp ý về chiến lược lấy mẫu cho Hà Nội trong thực hiện nhiệm vụ này.\n\nChia sẻ với Zing về góp ý của nhóm bác sĩ Bệnh viện Đại học Y Hà Nội, GS Nguyễn Lân Hiếu, Giám đốc Bệnh viện này nhận định với sự hỗ trợ của các bệnh viện Trung ương và năng lực xét nghiệm hiện có của Hà Nội có thể làm xét nghiệm RT-PCR chẩn đoán Covid-19 với số lượng 5.000 mẫu/ngày.\n\nTheo GS Hiếu, để thực hiện xét nghiệm RT-PCR với số lượng lớn trong thời gian ngắn cần có chiến lược và phải đảm bảo chất lượng tốt nhất.\n\nNhóm bác sĩ Bệnh viện Đại học Y Hà Nội đưa ra 3 giải pháp để giúp giải quyết việc này.\n\nThứ nhất, thực hiện phương pháp xét nghiệm gộp các pool mẫu. GS Hiếu cho rằng đây có thể là một chiến lược tốt trong điều kiện nguồn lực giới hạn.\n\nTheo Giám đốc Bệnh viện Đại học Y Hà Nội, thành phố nên áp dụng phương pháp này nhưng phải có hướng dẫn cụ thể. Nếu được, có thể triển khai theo nhóm gia đình, văn phòng, cơ quan có cùng nguồn lây, tiếp xúc. Tuy nhiên, với những người có triệu chứng vẫn nên làm xét nghiệm riêng để đảm bảo khả năng phát hiện cao nhất.\n\nThứ hai, do số lượng người cần sàng lọc rất lớn nên phải chia thành hai nhóm: Nhóm cần làm ngay lập tức và nhóm làm theo kế hoạch.\n\nViệc lấy mẫu cần ưu tiên cho nhóm cần làm ngay lập tức, gồm những người có triệu chứng lâm sàng (sốt, ho, khó thở…) hoặc có tiếp xúc gần với nguồn lây nhiễm. Riêng với người đi từ Đà Nẵng về trên 14 ngày, cần làm thêm xét nghiệm phát hiện kháng thể để tăng độ nhạy cho việc sàng lọc và có thể đánh giá tình trạng miễn dịch phơi nhiễm của bệnh nhân.\n\nNhóm còn lại sẽ làm theo kế hoạch và phải căn cứ vào thời gian đi từ vùng dịch Đà Nẵng về. “Lúc này, chúng ta rất cần chậm lại một chút để có chiến lược rõ ràng phù hợp với nguồn lực hiện có”, GS Hiếu nói.\n\nThứ ba, nên có cơ chế để những người có triệu chứng được xét nghiệm RT-PCR ngay tại bệnh viện họ đến khám lần đầu. Bởi, việc lấy mẫu trên diện rộng có thể làm chậm việc tiếp cận xét nghiệm RT-PCR của những người có triệu chứng.\n\nGS Hiếu cũng nhấn mạnh cần cho các bệnh viện cơ chế để xét nghiệm RT-PCR tại chỗ nếu bệnh viện đủ năng lực xét nghiệm. Nhiều bệnh nhân có khả năng tự chi trả xét nghiệm hoặc đồng chi trả với bảo hiểm, vì vậy, cần ủng hộ điều đó và cho phép xã hội hóa xét nghiệm này để số người phơi nhiễm SARS-CoV-2 được xét nghiệm nhanh nhất.\n\nNgoài ra, cần tăng cường số lượng bệnh viện được xét nghiệm RT-PCR chẩn đoán Covid-19, để tránh tình trạng người có triệu chứng phải đi qua nhiều bệnh viện mới được làm xét nghiệm.  ',0,'lam-sao-de-ha-noi-xet-nghiem-rt-ppcr-cho-tat-ca-nguoi-tu-da-nang-ve',NULL,1,65,542,'<img src=\"Assets/images/testnhanhcovid1_VL_zing.jpg\">'),(134,1,'Khánh Hòa ra tối hậu thư về dự án 33 triệu đô lấn, lấp vịnh Nha Trang','Dự án Công viên văn hóa, giải trí, thể thao Nha Trang Sao khởi công năm 2014, với mức đầu tư 33 triệu USD, đến nay vẫn là bãi đất trống, cây dại mọc um tùm.','Lãnh đạo Sở Tài nguyên Môi trường Khánh Hòa cho biết vừa có thông báo gửi Công ty CP Nha Trang Sao - chủ đầu tự dự án Công viên văn hóa, giải trí, thể thao Nha Trang Sao - về việc bàn giao mặt bằng và nộp lại giấy chứng nhận quyền sử dụng đất của dự án, trước ngày 15/8. <img src=\"Assets/images/1_zing.jpg\"> Vị lãnh đạo này cũng cho biết sau ngay 15/8, Công ty CP Nha Trang Sao không phối hợp thì UBND TP Nha Trang sẽ cưỡng chế thực hiện quyết định thu hồi. Văn phòng đăng ký đất đai tỉnh Khánh Hòa sẽ hủy giấy chứng nhận quyền sử dụng đất của dự án theo quy định.\n\nDự án Công viên văn hóa, giải trí, thể thao Nha Trang Sao nằm bên vịnh Nha Trang, trên đường Phạm Văn Đồng, phường Vĩnh Phước, TP Nha Trang, được UBND tỉnh Khánh Hòa cấp giấy chứng nhận đầu tư tháng 3/2012. Dự án có diện tích hơn 10 ha với vốn đầu tư 33 triệu USD, được khởi công vào năm 2014.\n\nTuy nhiên, trong quá trình thi công chủ đầu tư dự án đã tự ý lấn, lấp trái phép gần 2,3 ha danh thắng vịnh Nha Trang. Dự án này còn chậm tiến độ; chậm hoàn thành hồ sơ phương án kiến trúc quy hoạch được gia hạn…\n\nUBND tỉnh Khánh Hòa và các Sở Kế hoạch Đầu tư, Tài nguyên Môi trường đã nhiều lần ra quyết định xử phạt hành chính. Đồng thời, yêu cầu chủ đầu tư hoàn thành hồ sơ phương án kiến trúc, đẩy nhanh tiến độ dự án.\n\nTuy nhiên, trong nhiều năm dự án vẫn là bãi đất trống, cỏ dại mọc um tùm và trở thành điểm đen về tệ nạn xã hội trên địa bàn.\n\nNgày 19/1/2018, Sở Kế hoạch Đầu tư Khánh Hòa ra quyết định chấm dứt hoạt động của dự án Công viên văn hóa, giải trí, thể thao Nha Trang Sao. Ngày 18/1/2019, UBND tỉnh Khánh Hòa có quyết định thu hồi đất do Công ty CP Nha Trang Sao, đang sử dụng tại đường Phạm Văn Đồng, phường Vĩnh Thọ và giao cho Trung tâm phát triển quỹ đất tỉnh quản lý.\n\nTừ tháng 2/2019, các cơ quan liên quan tỉnh Khánh Hòa và UBND TP Nha Trang nhiều lần yêu cầu Công ty CP Nha Trang Sao nộp lại giấy chứng nhận quyền sử dụng đất, bàn giao đất nhưng doanh nghiệp này không hợp tác.  ',0,'khanh-hoa-ra-toi-hau-thu-ve-du-an-33-trieu-do-lan-lap-vinh-nha-trang',NULL,1,63,125,'<img src=\"Assets/images/1_zing.jpg\">'),(135,1,'Phú Quốc sẽ đặt tượng đài Bác Hồ tại quảng trường rộng 8 ha','Sân bay cũ ở Phú Quốc được tỉnh Kiên Giang chọn làm nơi xây quảng trường có tượng đài Bác Hồ, sức chứa 20.000 người.','Chiều 7/8, UBND huyện Phú Quốc công bố quyết định của UBND tỉnh Kiên Giang về việc phê duyệt đồ án quy hoạch chi tiết quảng trường trung tâm và tượng đài Bác Hồ tại thị trấn Dương Đông, tỷ lệ 1/500, quy mô 8,29 ha. <img src=\"Assets/images/quang_truong.jpg\"> Quảng trường có sức chứa 20.000 người đặt tại trung tâm của đô thị Dương Đông, được tỉnh Kiên Giang chọn xây dựng ở khu vực sân bay cũ. Đây là nơi đặt tượng đài Bác Hồ, có các yếu tố về chính trị, văn hóa, du lịch và sinh hoạt cộng đồng…\n\nTheo đồ án, khu quảng trường chính bao gồm quảng trường nghi lễ và tượng đài Bác Hồ được bố trí ở khu vực trung tâm. Đây là nơi diễn ra các sự kiện, lễ hội và các hoạt động mang tính chất tập trung đông người.\n\nDự án có tổng kinh phí đầu tư khoảng 353 tỷ đồng, dự kiến triển khai vào năm 2021.  ',0,'phu-quoc-se-dat-tuong-dai-bac-ho-tai-quang-truong-rong-8-ha',NULL,1,23,123,'<img src=\"Assets/images/quang_truong.jpg\">'),(136,1,'Ông Tất Thành Cang bị phê bình vì sai phạm tại Thủ Thiêm','Ban Chấp hành Đảng bộ TP.HCM thống nhất phê bình ông Tất Thành Cang cùng 2 người khác vì đã hết thời hiệu xử lý kỷ luật Đảng','Chiều 7/8, Ủy ban Kiểm tra Thành ủy TP.HCM thông tin về kết quả xem xét, thi hành kỷ luật tổ chức Đảng, đảng viên đối với vụ việc tại Dự án Khu đô thị mới Thủ Thiêm. Theo đó, ông Tất Thành Cang được Ban Chấp hành Đảng bộ TP.HCM thống nhất kết luận phê bình.\n\n2 người khác bị đề nghị phê bình gồm bà Phan Thị Thắng, Thành ủy viên, Phó chủ tịch HĐND TP.HCM, nguyên Giám đốc Sở Tài chính và ông Bùi Xuân Cường, Thành ủy viên, Bí thư Đảng ủy, Trưởng ban Quản lý Đường sắt đô thị thành phố, nguyên Giám đốc Sở Giao thông Vận tải.\n\nBan Thường vụ Thành ủy TP.HCM cho biết những người nêu trên có khuyết điểm, vi phạm đến mức phải thi hành kỷ luật; tuy nhiên, đến nay đã hết thời hiệu xử lý kỷ luật về mặt Đảng nên Ban Chấp hành Đảng bộ TP.HCM thống nhất kết luận phê bình.\n\n <img src=\"Assets/images/z1965507845029_4a8fb28ceae00e517ea0eafdb7148f69_1_.jpg\"> Thực hiện thông báo kết luận của Ủy ban Kiểm tra Trung ương về kết quả kiểm tra khi có dấu hiệu vi phạm liên quan quá trình lãnh đạo, chỉ đạo, thực hiện Dự án Khu đô thị mới Thủ Thiêm, Ban Thường vụ Thành ủy đã chỉ đạo xem xét trách nhiệm của 66 đảng viên có khuyết điểm, vi phạm.\n\nTrong đó, Ban Thường vụ Thành ủy TP.HCM có văn bản báo cáo, đề nghị Ủy ban Kiểm tra Trung ương xem xét theo thẩm quyền đối với một đảng viên.\n\nỦy ban Kiểm tra Thành ủy cũng đã xem xét, thi hành kỷ luật đối với 3 đảng viên bằng hình thức cảnh cáo gồm bà Nguyễn Thị Hữu Hòa, nguyên Trưởng ban Quản lý Đầu tư Xây dựng Khu đô thị mới Thủ Thiêm; ông Tạ Quang Vinh, nguyên Phó giám đốc Sở Tài chính; ông Nguyễn Quốc Chiến, Chủ tịch Hội đồng Thành viên Công ty Xổ số Kiến thiết thành phố, nguyên Trưởng ban Vật giá, Sở Tài chính.\n\nLiên quan đến vụ việc, Ủy ban Kiểm tra Thành ủy đã phê bình đối với 38 người do cùng chịu trách nhiệm liên quan việc tham mưu, tổ chức thực hiện Dự án Khu đô thị mới Thủ Thiêm nhưng chưa đến mức thi hành kỷ luật. 13 trường hợp được thống nhất chưa xem xét hoặc không xem xét do lý do cụ thể hoặc do đang mắc bệnh hiểm nghèo hoặc không phải là đảng viên.  ',0,'ong-tat-thanh-cang-bi-phe-binh-vi-sai-pham-tai-thu-thiem',NULL,1,65,21,'<img src=\"Assets/images/z1965507845029_4a8fb28ceae00e517ea0eafdb7148f69_1_.jpg\">'),(137,1,'3 điểm bất thường trong trận mưa lớn ở TP.HCM tối 6/8','Chuyên gia cho rằng trận mưa lớn tối 6/8 tại TP.HCM có nhiều điểm bất thường cả về thời gian và vũ lượng. Từ nay đến hết mùa mưa, khu vực có thể hứng chịu những trận mưa tương tự.','Người dân tại TP.HCM vừa hứng chịu trận mưa lớn vào tối 6/8. Mưa trút xuống với cường độ lớn liên tục trong nhiều giờ đã khiến một số tuyến đường ngập sâu. Đài Khí tượng Thủy văn Nam Bộ nhận định đây là trận mưa lớn nhất kể từ đầu năm nay tại TP.HCM.\n\nTrao đổi với Zing, bà Lê Thị Xuân Lan, nguyên Phó phòng dự báo Đài Khí tượng Thủy văn Nam Bộ, cho biết trận mưa tối qua có nhiều điểm bất thường so với những đợt mưa khác từ trước đến nay ở TP.HCM và các tỉnh Nam Bộ. Một số khu vực đã ghi nhận lượng mưa lịch sử.\n\nĐặc biệt và bất thường\nTheo bà Lan, trận mưa lớn tối 6/8 tại TP.HCM có 3 điểm bất thường cần được phân tích. Thứ nhất, khu vực thường duy trì kiểu hình sáng nắng, chiều mưa từ tháng 5 đến tháng 10 nhưng với thời lượng mưa mỗi ngày chỉ kéo dài 15-30 phút, tối đa là 1 giờ.\n\n Tuy nhiên, trận mưa tối qua bắt đầu nặng hạt từ 19h và kéo dài đến 22h, sau đó giảm dần. Như vậy, TP.HCM đã mưa liên tục 3 giờ với cường độ lớn. Đây là điều hiếm khi xảy ra.\n\nĐường phố vẫn ngập nước sau trận mưa đêm qua Nước không kịp rút sau trận mưa lớn tối qua khiến giao thông qua đường Lương Định Của, Trần Não (quận 2, TP.HCM), ùn tắc nhiều giờ, xe cộ chết máy.\nThứ hai, chuyên gia cho biết lượng m Thứ hai, chuyên gia cho biết lượng mưa tối qua ghi nhận được ở các trạm quan trắc dao động 150-200 mm. Đặc biệt, trạm đo mưa ở Mạc Đĩnh Chi (quận 1) ghi nhận lượng mưa lên đến 212 mm. Đây có thể là lượng mưa lịch sử, chưa từng xuất hiện trong chuỗi số liệu quan trắc tại khu vực này.\n\n“Kiểu mưa trên toàn thành phố với lưu lượng 150-200 mm là rất đặc biệt và bất thường. Mưa không đồng đều ở các nơi, nhưng đều là mưa to”, bà Lan nói.\n\nThứ ba, thời gian mưa trễ hơn bình thường. Những trận mưa trong giai đoạn này thường trút xuống vào chiều hoặc chiều tối, thời gian người dân đi làm về. Nhưng hôm qua, mưa bắt đầu vào buổi tối.\n\nNgoài ra, khoảng thời gian có mưa lớn, sấm sét cũng liên tục xuất hiện tại các khu vực. Điều này ít khi xảy ra, đặc biệt trong giai đoạn chưa phải cao điểm của mùa mưa.\n\nBà Lan phân tích rằng trước đó một đêm, mưa lớn đã xuất hiện nhưng chủ yếu ở Đồng Nai và miền Tây. Nhưng tối 6/8, mặc dù mưa trên diện rộng ở Nam Bộ nhưng lại tập trung cao điểm ở quần đảo Trường Sa với lưu lượng gần 200 mm và TP.HCM.\n\n\"Các khu vực này mưa lớn kèm theo sấm sét liên tục 3 giờ là do tồn tại rất nhiều ổ mây dông kết nối với nhau, trút nước xuống liên tục\", bà Lan cho biết.\n\nTiếp tục hứng những trận mưa tương tự\nÔng Lê Đình Quyết, Phó phòng dự báo Đài Khí tượng Thủy văn Nam Bộ, lý giải đợt mưa này xuất hiện với cường độ lớn do rãnh áp thấp có trục tây bắc - đông nam nối với một vùng áp thấp ở Biển Đông đang hoạt động mạnh kết hợp với gió mùa tây nam đã tạo ra một tổ hợp gây mưa.\n\nNhững hình thái tác động khiến gió tây nam liên tục mang hơi ẩm vào đất liền và hình thành nhiều ổ mây dông gây ra mưa lớn.\n\nChuyên gia dự báo trong 2-3 ngày tới, rãnh áp thấp có xu hướng nâng trục lên phía bắc và suy yếu. Gió mùa tây nam hạ cường độ sẽ kéo giảm lượng mưa ở TP.HCM và Nam Bộ. Khu vực này chỉ còn mưa rào vào chiều tối, ban ngày có nắng. <img src=\"Assets/images/ngap_SG6_DH_zing.jpg\"> Cùng nhận định trên, chuyên gia Lê Thị Xuân Lan cho rằng các tổ hợp thời tiết tác động không chỉ gây ra mưa lớn mà còn mưa kéo dài suốt một tuần qua ở Tây Nguyên và Nam Bộ. Trong khi đó, tháng 8 chưa phải cao điểm mùa mưa.\n\nKhu vực sẽ hứng chịu cao điểm mưa vào tháng 9 và tháng 10. Lúc này, nhiều hình thái cực đoan trên Biển Đông có thể tác động khiến gió tây nam mang theo nhiều hơi ẩm vào đất liền, khu vực tiếp tục có mưa to đến rất to.\n\n\"Từ cuối tháng 8 đến tháng 10, tôi cho rằng TP.HCM và các tỉnh Nam Bộ nhiều khả năng hứng chịu thêm những trận mưa tương tự tối 6/8. Mưa có thể không tập trung ở quận 1 nữa mà sẽ xảy ra ở những nơi khác\", bà Lan nhận định.\n\nVị chuyên gia cũng lưu ý rằng trận mưa tối qua xảy ra không phải vào cao điểm của triều cường nhưng đã khiến nhiều tuyến phố ngập nặng. Trong khi đó, đỉnh triều trong năm ở TP.HCM thường rơi từ tháng 9 đến tháng 11.\n\nTổng hợp những phân tích trên, chuyên gia cảnh báo mưa lớn cực đoan kết hợp triều cường có thể khiến nhiều nơi ở TP.HCM tiếp tục ngập nặng nề thời gian tới.  ',0,'3-diem-bat-thuong-trong-tran-mua-lon-o-tp.hcm-toi-6/8',NULL,1,423,653,'<img src=\"Assets/images/ngap_SG6_DH_zing.jpg\">'),(138,2,'Tình báo Mỹ: Nga tiếp tục can thiệp, TQ muốn TT Trump thất bại bầu cử','Tình báo Mỹ cho biết bầu cử ở nước này đang đối mặt nguy cơ can thiệp từ Nga và Trung Quốc, thông qua các hoạt động cả công khai và bí mật.','Nga đang sử dụng hàng loạt biện pháp nhằm hạ thấp uy tín của cựu phó tổng thống Joe Biden, trong nỗ lực của Moscow can thiệp vào bầu cử tổng thống Mỹ năm 2020 theo hướng có lợi cho Tổng thống Trump.\n\nĐây là kết luận trong tuyên bố được đưa ra bởi William R. Evanina, Giám đốc Trung tâm Phản gián và an ninh quốc gia Mỹ hôm 7/8, theo New York Times.\n\nTrong khi đó, giới chức tình báo Mỹ cho biết Trung Quốc muốn ông Trump thất cử vào tháng 11 tới, và đang cân nhắc có nên hành động chủ động hơn để can thiệp vào tổng tuyển cử. <img src=\"Assets/images/Joe_Biden.jpg\"> Đe dọa can thiệp từ Nga và Trung Quốc\nGiới chức tình báo Mỹ đánh giá Nga là mối đe dọa nguy hiểm và cấp thiết hơn. Trong khi Trung Quốc tìm cách gia tăng ảnh hưởng lên nền chính trị Mỹ, các nhà lãnh đạo quốc gia Đông Á chưa quyết định trực tiếp can thiệp vào cuộc chạy đua tổng thống, dù rằng Bắc Kinh không muốn Tổng thống Trump có nhiệm kỳ cầm quyền thứ 2.\n\nĐánh giá của ông Evanina cho thấy cộng đồng tình báo Mỹ đang xử lý vấn đề hết sức thận trọng, phản ánh sức nóng chính trị đã được những phát hiện trước đó tạo ra.\n\nTrong khi Nhà Trắng phản đối kết luận Moscow đang tìm cách giúp đỡ Tổng thống Trump, phe Dân chủ ở Quốc hội Mỹ bày tỏ quan ngại rằng giới chức tình báo không đủ thẳng thắn về sự ủng hộ của Nga dành cho Tổng thống Trump, và rằng các cơ quan tình báo đã theo đuổi cách tiếp cận chống Bắc Kinh của Nhà Trắng để cân bằng mối đe dọa đến từ hai quốc gia đối địch với Mỹ.\n\nLực lượng thân Nga tại Ukraine đã có những nỗ lực nhằm hủy hoại danh tiếng của ông Biden, trong khi những đối tượng có quan hệ với Điện Kremlin \"cũng tìm cách thúc đẩy uy tín của Tổng thống Trump trên mạng xã hội và truyền hình Nga\", đánh giá của ông Evanina có đoạn.\n\nTrong khi đó, đánh giá của người đứng đầu Trung tâm Phản gián và tình báo Mỹ cho biết Trung Quốc hành động chủ yếu thông qua các chỉ trích công khai nhắm vào lập trường cứng rắn của chính quyền Washington đối với Bắc Kinh trong một loạt lĩnh vực.\n\nHiện chưa rõ mức độ can thiệp trực tiếp của Trung Quốc nhắm vào cuộc tổng tuyển cử tại Mỹ. Trong các cuộc làm việc với Quốc hội Mỹ vài ngày qua, giới chức tình báo cho biết mục tiêu của Bắc Kinh đa phần là các cuộc chạy đua ở cấp bang hoặc địa phương. Tuy nhiên, các nỗ lực can thiệp của Trung Quốc đang gia tăng, ông Evanina nhận xét.\n\n\"Dù Trung Quốc sẽ tiếp tục cân nhắc rủi ro và lợi ích của hành động can thiệp sâu hơn, luận điệu công khai trong vài tháng qua của họ chỉ trích ngày càng gay gắt cách chính quyền xử lý dịch Covid-19, việc đóng cửa Tổng lãnh sự quán ở Houston, và các vấn đề khác\", ông Evanina nói.\n\nÔng Evanina chỉ ra căng thẳng ngày càng gia tăng giữa Washington và Bắc Kinh xung quanh tuyên bố chủ quyền trái phép trên Biển Đông, quyền tự trị của Hong Kong, ứng dụng TikTok và một số vấn đề khác.\n\nQuan chức tình báo Mỹ không đi sâu vào chi tiết, bởi hiển nhiên các cơ quan an ninh cần bảo vệ các nguồn cung cấp thông tin.\n\n\"Ngài giám đốc về cơ bản đã cảnh báo người Mỹ rằng Nga, Trung Quốc và Iran đang tìm cách can thiệp vào cuộc bầu cử này, phá hoại hệ thống dân chủ của chúng ta\", Angus King, Thượng nghị sĩ Dân chủ, thành viên Ủy an Tình báo Thượng viện, nói.\n\nĐe dọa lớn hơn từ Moscow\nTrong khi cả Bắc Kinh và Moscow có lợi ích riêng đối với kết quả cuộc bầu cử tháng 11 tới đây tại Mỹ, cách thức gây ảnh hưởng của Nga và Trung Quốc đối với các cử tri Mỹ có sự khác biệt.\n\nPhe Dân chủ cho rằng, ngoài một số ví dụ đơn lẻ, rất khó để tìm ra bằng chứng cho thấy hoạt động của Bắc Kinh gây được tác động ở quy mô quốc gia. Đa phần những nỗ lực của Trung Quốc liên quan tới sử dụng sức mạnh kinh tế để tác động lên chính trị ở các địa phương của Mỹ, và đây không phải điều gì mới.\n\nTrung Quốc đang sử dụng nhiều biện pháp nhằm đảo ngược các chính sách của chính quyền Trump, bao gồm các loại thuế và lệnh cấm đối với các công ty công nghệ của Trung Quốc. Những nỗ lực này được tiến hành công khai và hiện chưa rõ khả năng tác động của chúng lên các chính sách của tổng thống.\n\nQuan chức tình báo Mỹ cho rằng, trong khi chính quyền Trump phần nào đó đẩy quá mức tính nguy cấp tới từ các đe dọa của Trung Quốc khi Nga đang \"chủ động gây ảnh hưởng\" tới kết quả của cuộc bầu cử năm 2020.\n\nPhe Dân chủ cho rằng chiến dịch can thiệp của Nga tới thời điểm hiện tại gây ra mối đe dọa trực tiếp và cấp thiết hơn nhiều. <img src=\"Assets/images/Evanina.jpg\"> \"Việc các đối thủ như Trung Quốc và Iran không ưa gì các chính sách của Mỹ là điều bình thường. Điều bất thường, nguy hiểm và gây nhức nhối là một đối thủ như Nga đang hành động sốt sắng để giúp ông Trump tái đắc cử\", Jeremy Bash, quan chức trong chính quyền cựu tổng thống Obama, nói.\n\nÔng Evanina cho biết các quốc gia thù địch với Mỹ sẽ gặp nhiều khó khăn nếu muốn thao túng kết quả bỏ phiếu trên quy mô lớn. Dù vậy, những nước này có thể tìm cách can thiệp trong quá trình bỏ phiếu hoặc có các bước đi nhằm \"đặt ra câu hỏi về tính hợp lệ của kết quả bầu cử\".\n\n\"Trước thềm bầu cử năm 2020, các quốc gia nước ngoài sẽ tiếp tục thực hiện các biện pháp công khai và bí mật nhằm chi phối quan điểm và lựa chọn của cử tri Mỹ, thay đổi chính sách của Mỹ, làm gia tăng bất hòa tại Mỹ, gây xói mòn niềm tin của người dân mỹ vào quy trình dân chủ của chúng ta\", ông Evanina nói.\n\nPhe Dân chủ đã thúc giục các quan chức tình báo công bố thêm thông tin đến công chúng, cho rằng cần giải mật thông tin rộng rãi về các âm mưu can thiệp của nước ngoài nhằm giúp bảo vệ cử tri Mỹ khỏi các âm mưu can thiệp của Nga, Trung Quốc hoặc các nước khác.\n\nThời gian qua, nhà chức trách Mỹ đã đẩy mạnh công bố thông tin về các âm mưu can thiệp của nước ngoài đối với các cử tri Mỹ trước thềm tổng tuyển cử.\n\nBộ Ngoại giao Mỹ cũng đã gửi tin nhắn tới nhiều thuê bao di động trên toàn cầu, treo thưởng 10 triệu USD cho thông tin khả tín về các âm mưu can thiệp bầu cử.\n\n\"Tuyên bố của ông Evanina nhấn mạnh một số đe dọa nghiêm trọng đang diễn ra đến từ Trung Quốc, Nga, Iran đối với cuộc bầu cử của chúng ta. Tất cả mọi người, từ các cử tri, quan chức địa phương, thành viên Quốc hội, cần hiểu rõ những đe dọa này\", các lãnh đạo của Ủy ban Tình báo Thượng viện Mỹ nhận xét.  ',0,'tinh-bao-my-nga-tiep-tuc-can-thiep-tq-muon-tt-trump-that-bai-bau-cu',NULL,1,64,315,'<img src=\"Assets/images/Joe_Biden.jpg\">'),(139,2,'Pháp, Đức từ bỏ đàm phán về WHO giữa căng thẳng với Mỹ','Pháp và Đức rút lui khỏi đàm phán về cải tổ Tổ chức Y tế Thế giới (WHO) trong bực dọc khi Mỹ vẫn cố giành vai trò lãnh đạo dù đã quyết định rút khỏi WHO.','Động thái này là bước lùi đối với Tổng thống Mỹ Donald Trump khi Washington, vốn đang giữ ghế chủ tịch luân phiên của G7, hy vọng đưa ra được lộ trình thống nhất cho cải cách sâu rộng WHO vào tháng 9, hai tháng trước bầu cử tổng thống.\n\n <img src=\"Assets/images/2017_05_26T141434Z_1842057053_RC15A9EC4080_RTRMADP_3_USA_TRUMP_G7.jpg\"> Mỹ muốn chi phối, châu Âu khó chịu\nMỹ thông báo cho WHO vào tháng 7 rằng sẽ rút khỏi cơ quan của Liên Hợp Quốc sau một năm nữa, giữa lúc ông Trump cáo buộc cơ quan này quá thân thiết với Trung Quốc và sai lầm trong xử lý đại dịch Covid-19.\n\nWHO bác bỏ những cáo buộc của tổng thống Mỹ. Các chính phủ châu Âu cũng chỉ trích WHO nhưng không đi xa như Mỹ. Paris và Berlin quyết định rời cuộc đàm phán sau căng thẳng về những gì họ cho là nỗ lực của Washington nhằm chi phối quá trình này, theo Reuters.\n\n\n\"Không ai muốn bị lôi kéo vào quá trình cải tổ và nhận kế hoạch cho việc này từ một quốc gia mà chính họ vừa rời khỏi WHO\", một quan chức cấp cao của châu Âu tham gia vào cuộc đàm phán nói.\n\nBộ Y tế Đức và Pháp xác nhận với Reuters rằng hai nước phản đối việc Mỹ dẫn dắt cuộc đàm phán sau khi công bố ý định rút khỏi WHO.\n\nPhát ngôn viên của Bộ Y tế Italy cho biết rằng lộ trình cải cách vẫn đang được thảo luận, nhưng lập trường của Rome giống với Paris và Berlin.\n\nKhi được hỏi về lập trường của Pháp và Đức, quan chức cấp cao chính quyền Trump cho biết: \"Tất cả thành viên của G7 đều ủng hộ một cách rõ ràng thực chất của các ý tưởng cải tổ WHO\".\n\n\"Mặc dù vậy, thật đáng tiếc là Đức và Pháp cuối cùng đã chọn không tham gia nhóm ủng hộ lộ trình này\", ông nói.\n\nNgười phát ngôn của chính phủ Anh từ chối bình luận về những diễn biến mới nhất nhưng nói London ủng hộ WHO và thúc giục cải tổ \"để đảm bảo cơ quan này vẫn linh hoạt và nhanh nhạy”.\n\nCác cuộc đàm phán về cải tổ WHO bắt đầu khoảng 4 tháng trước. Đã có gần 20 cuộc trao đổi qua điện thoại giữa bộ trưởng y tế các nước G7, và hàng chục cuộc gặp của các nhà ngoại giao và quan chức khác.\n\nMột thỏa thuận của G7, nhóm bao gồm Nhật Bản và Canada, sẽ là nền tảng cho các cuộc đàm phán trong G20 và tại Liên Hợp Quốc, nơi mọi thay đổi sẽ phải được Trung Quốc, Nga và các nước lớn khác không thuộc G7 nhất trí.\n\nKhông rõ liệu hội nghị thượng đỉnh G7 ở Mỹ, nơi ông Trump hy vọng các nhà lãnh đạo sẽ tán thành lộ trình, có thể diễn ra vào tháng 9 như kế hoạch hay không.\n\nKế hoạch \"thô lỗ\"\nCác quan chức Mỹ chưa cho biết Washington mong muốn cải tổ thế nào. Tuy nhiên, lộ trình cải cách ban đầu do Washington đề xuất đã bị nhiều đồng minh của họ coi là quá đáng, bao gồm một quan chức châu Âu nói Mỹ \"thô lỗ\".\n\nCác nguồn tin nắm rõ quá trình đàm phán cho biết dù kế hoạch này đã được chỉnh sửa, nỗ lực của Washington vẫn không thể chấp nhận được, chủ yếu là với Đức.\n\n <img src=\"Assets/images/download_2020_08_08T071902.027.jpeg\"> Trong những tuần trước khi cuộc đàm phán sụp đổ, những người tham gia nói với Reuters rằng lập trường các bên đang xích lại gần nhau hơn khi Washington tỏ ra mềm mỏng hơn trong cách tiếp cận và các nhà đàm phán châu Âu bắt đầu coi quá trình cải tổ là cách để giúp WHO độc lập hơn trước áp lực chính trị.\n\nCác chính phủ châu Âu cũng bắt đầu có những nhận xét mang tính hoài nghi WHO trước công chúng, trong đó bộ trưởng y tế Đức thúc giục WHO đẩy nhanh việc xem xét lại nỗ lực ứng phó với đại dịch.\n\nMột số nước châu Âu ủng hộ đường lối cứng rắn hơn, chỉ trích ông Tedros Adhanom Ghebreyesus, người đứng đầu WHO, và những gì họ coi là chính trị hóa việc ứng phó với đại dịch.\n\nMột người trong chính phủ Đức nói: \"Cần phải đảm bảo rằng trong tương lai WHO có thể phản ứng một cách trung lập và dựa trên sự thật trong các sự vụ về sức khỏe toàn cầu\".\n\nSong các chính phủ châu Âu muốn làm cho WHO mạnh mẽ hơn, có ngân sách lớn hơn và độc lập hơn, trong khi việc Mỹ ngừng đóng góp kinh phí có khả năng làm suy yếu tổ chức này. Washington là bên đóng góp lớn nhất, chiếm khoảng 15% ngân sách của WHO.\n\nMột số nước châu Âu coi những chỉ trích của ông Trump với WHO là nỗ lực trước cuộc bầu cử Mỹ nhằm đánh lạc hướng chú ý đối với việc ứng phó với đại dịch của ông. Quan hệ giữa Berlin với Washington cũng đã căng thẳng vì ông Trump quyết định rút hàng nghìn lính Mỹ khỏi Đức.\n\nCác kế hoạch cải tổ WHO khó có thể bị gác lại, đặc biệt nếu ông Trump bị đánh bại trong cuộc bầu cử tháng 11. Các chính phủ châu Âu muốn Washington tiếp tục là thành viên của WHO và hỗ trợ tài chính cho tổ chức.  ',0,'phap-duc-tu-bo-dam-phan-ve-ho-giua-cang-thang-voi-my',NULL,1,65,523,'<img src=\"Assets/images/2017_05_26T141434Z_1842057053_RC15A9EC4080_RTRMADP_3_USA_TRUMP_G7.jpg\">'),(140,2,'17 người chết trên máy bay Ấn Độ gãy đôi khi hạ cánh','Máy bay đưa công dân Ấn Độ mắc kẹt ở Dubai về nước đã đi quá đường băng khi hạ cánh và gãy đôi, khiến ít nhất 17 người thiệt mạng, 123 người bị thương.','Chuyến bay được thực hiện bởi máy bay Boeing 737-800 của hãng hàng không Air India Express hôm 7/8, với điểm đến là sân bay Kozhikode thuộc bang Kerala ở phía nam Ấn Độ, theo AP.\n\nAbdul Karim, sĩ quan cảnh sát bang Kerala, cho biết những người thiệt mạng bao gồm một phi công. Ông nói ít nhất 15 người đang trong tình trạng nguy kịch và các hoạt động cứu hộ đã kết thúc. <img src=\"Assets/images/1000_2020_08_08T061201.536.jpeg\"> Hãng Air India Express đã tránh được thảm kịch tương tự trong gang tấc tại cùng sân bay cách đây một năm, khi một chuyến bay của hãng gặp sự cố ở đuôi lúc hạ cánh. Không ai trong số 180 hành khách trên chuyến bay đó bị thương.\n\nĐường băng dài 2.850 mét ở sân bay Kozhikode nằm trên đỉnh đồi phẳng với các hẻm núi sâu ở hai bên cao 34 mét.\n\nBộ trưởng Hàng không Dân dụng Hardeep S. Puri cho biết trong một tuyên bố rằng máy bay \"đi quá đường băng trong trời mưa và lao xuống\" sườn dốc, gãy làm đôi do lực va chạm. Ông nói Cục Điều tra Tai nạn Máy bay của bộ đang điều tra vụ việc.\n\nKhu vực an toàn cuối đường băng của sân bay đã được mở rộng vào năm 2018 để các máy bay thân rộng có thể cất hạ cánh.\n\nTheo Harro Ranter, Giám đốc điều hành cơ sở dữ liệu trực tuyến của Mạng lưới An toàn Hàng không, khu vực này đáp ứng các yêu cầu hàng không dân dụng quốc tế của Liên Hợp Quốc, nhưng cơ quan của Liên Hợp Quốc khuyến nghị thiết lập vùng đệm dài hơn 150 mét so với những gì đang có ở sân bay Kozhikode.\n\nNhà tư vấn hàng không Mark Martin ở Dubai nói dù còn quá sớm để khẳng định nguyên nhân tai nạn, mùa mưa thường niên dường như là một yếu tố.\n\n\"Tầm nhìn kém, đường băng ướt, tầng mây thấp, tất cả dẫn đến việc phanh rất kém là những gì có vẻ như đã gây ra vụ tai nạn này\", Martin nói, kêu gọi Cơ quan An toàn Hàng không Châu Âu và Cơ quan Hàng không Liên bang Mỹ hỗ trợ chính phủ Ấn Độ điều tra. <img src=\"Assets/images/1000_2020_08_08T061213.242.jpeg\"> Các quan chức cho biết chuyến bay nằm trong chương trình đặc biệt của chính phủ Ấn Độ đưa công dân hồi hương. Toàn bộ hành khách trên chuyến bay đều trở về từ Vùng Vịnh.\n\nHoạt động bay thương mại thông thường đã bị tạm dừng ở Ấn Độ cũng như nhiều nước khác vì sự bùng phát của virus corona.\n\nHành khách bao gồm 174 người lớn, 10 trẻ sơ sinh, đi cùng 2 phi công và 4 tiếp viên. Air India Express là công ty con của Air India.\n\nTai nạn hàng không tồi tệ nhất ở Ấn Độ xảy ra vào ngày 12/11/1996, khi một chuyến bay của Saudi Arabian Airlines va chạm giữa không trung với một chuyến bay của Kazakhastan Airlines gần Charki Dadri ở bang Haryana, giết chết toàn bộ 349 người trên 2 máy bay.  ',0,'17-nguoi-chet-tren-may-bay-an-do-gay-doi-khi-ha-canh',NULL,1,43,543,'<img src=\"Assets/images/1000_2020_08_08T061201.536.jpeg\">'),(141,2,'Máy bay Pakistan rơi làm 97 người chết do phi công mải nói chuyện','Nhà chức trách cho biết các phi công và kiểm soát viên không lưu có lỗi trong vụ tai nạn máy bay làm 97 người chết do không tuân thủ các quy định bay tiêu chuẩn.','Theo South China Morning Post, nhà chức trách Pakistan hôm 24/6 đã công bố báo cáo điều tra ban đầu vụ tai nạn máy bay Airbus A320 của hãng hàng không Pakistan International khiến 97 người thiệt mạng hôm 22/5.\n\nBộ trưởng Hàng không Pakistan Ghulam Sarwar Khan cho biết các thủ tục vận hành bay tiêu chuẩn đã không được các phi công cũng như nhân viên kiểm soát không lưu tuân thủ. <img src=\"Assets/images/tai_nan_may_bay.jpg\"> \"Phi công cũng như kiểm soát không lưu đã không tuân thủ luật lệ tiêu chuẩn. Rất đáng tiếc là phi công đã quá tự tin (khi hạ cánh)\", Bộ trưởng Khan phát biểu trước Quốc hội.\n\nÔng Khan cho biết các phi công đã trò chuyện về đại dịch Covid-19 khi cố gắng hạ cánh chiếc máy bay và không kiểm soát hệ thống lái tự động. Khi chuyển sang giai đoạn hạ cánh, chiếc máy bay ở độ cao gấp đôi so với tiêu chuẩn, Bộ trưởng Khan cho biết thêm.\n\nNói về chiếc Airbus A320, ông Khan khẳng định chiếc máy bay ở trạng thái \"100% phù hợp cho chuyến bay, không tồn tại lỗi kỹ thuật\".\n\nMáy bay dân dụng số hiệu 8303 của hãng hàng không Pakistan International chở 99 người đã gặp nạn và lao xuống một khu dân cư gần sân bay Jinnah ở thành phố cảng Karachi của Pakistan hôm 22/5. Chỉ 2 trong tổng số 99 người có mặt trên chuyến bay sống sót trong vụ tai nạn.\n\nDữ liệu thu thập được cho thấy phi công đã tìm cách hạ cánh nhưng không thành công, và phải bay vòng vòng trên không trung để tìm cách tiếp đất thêm lần nữa. Máy bay đã lao xuống mặt đất trong lần hạ cánh thứ hai.\n\nTai nạn hàng không lớn gần đây nhất ở Pakistan là vụ máy bay trực thăng của quân đội rơi ở một thung lũng phía bắc vào năm 2015, giết chết 8 người. Vụ tai nạn máy bay nghiêm trọng nhất nước này cho đến nay xảy ra vào năm 2010 khi một chiếc Airbus 321 rơi ở vùng ngoại vi thủ đô Islamabad khiến 152 người thiệt mạng.  ',0,'may-bay-pakistan-rơi-lam-97-nguoi-chet-do-phi-cong-mai-noi-chuyen',NULL,1,65,125,'<img src=\"Assets/images/tai_nan_may_bay.jpg\">'),(142,2,'Nguy cơ phát nổ được cảnh báo ít nhất 10 lần trước thảm họa ở Beirut','Nguy cơ cháy nổ lô ammonium nitrate tại cảng ở thành phố Beirut đã được nhiều cơ quan chức năng Lebanon cảnh báo trong thời gian dài trước khi vụ nổ xảy ra.','Theo AP, giới chức hải quan, quân đội, an ninh và tư pháp Lebanon cảnh báo ít nhất 10 lần trong 6 năm qua về nguy cơ từ hóa chất dễ cháy nổ được lưu giữ mà gần như không có sự bảo vệ tại cảng Beirut, tài liệu mới tiết lộ cho thấy.\n\nCảnh báo đầu tiên được đưa ra ngày 21/2/2014, khoảng 3 tháng sau khi con tàu Rhosus chở 2.750 tấn ammonium nitrate cập bến Beirut. Đại tá Joseph Skaff, quan chức hải quan cấp cao, đã gửi thư tới đơn vị chống buôn lậu của cơ quan Hải quan Lebanon, cảnh báo về việc lô hóa chất, khi đó vẫn ở trên tàu Rhosus, là \"cực kỳ nguy hiểm và đe dọa tới an toàn công cộng\".\n\nTới ngày 27/6/2014, thẩm phán Jad Maalouf cảnh báo Bộ Lao động và Giao thông công cộng Lebanon về con tàu chở hóa chất nguy hiểm có thể chìm. Ông Maalouf đề nghị Bộ này nhanh chóng xử lý con tàu, dỡ hóa chất lên mặt đất và \"bảo quản tại địa điểm phù hợp, và được bảo vệ\". <img src=\"Assets/images/Beirut_4.jpg\"> Không lâu sau đó, lô hóa chất được chuyển tới Nhà kho số 12 tại cảng Beirut và được bảo quản ở địa điểm này cho tới khi phát nổ. Hiện chưa rõ số hóa chất có nằm dưới sự quản lý của Bộ Lao động và Giao thông công cộng Lebanon hay không.\n\nTheo hãng tin LBC TV, vào tháng 10/2015, quân đội Lebanon đã can thiệp khi việc xử lý tàu Rhosus bị trì hoãn. Cơ quan tình báo quân đội đã cử chuyên gia tới kiểm tra lô hóa chất, kết quả cho thấy nitrogen ở mức 34,7%, được coi là mức có nguy cơ cháy nổ cao.\n\nQuân đội Lebanon đã thông báo tới cơ quan Hải quan, cho biết lô hóa chất cần được nhanh chóng di dời, đồng thời cảnh báo về nguy cơ cháy nổ. Cơ quan Hải quan sau đó chuyển thông tin này cho thẩm phán phụ trách các vấn đề khẩn cấp.\n\nBadri Daher, Giám đốc cơ quan Hải quan, cho biết cơ quan này đã 6 lần gửi thư tới thẩm phán trong giai đoạn 2014-2017 để cảnh báo về mối đe dọa tiềm tàng từ số hóa chất, và đề nghị cơ quan tư pháp Lebanon cho phép có phương án xử lý.\n\nDaher cho biết, tới thời điểm xảy ra vụ nổ, không có phản hồi nào từ cơ quan Tư pháp về cách thức xử lý số hóa chất.\n\nĐầu năm 2020, sau quá trình điều tra, cơ quan an ninh quốc gia Lebanon đã có báo cáo dài 5 trang nhấn mạnh cần nhanh chóng xử lý số hóa chất.\n\nCơ quan an ninh cảnh báo ammonium nitrate có nguy cơ bị khủng bố đánh cắp, đồng thời lưu ý trên tường của nhà kho lưu giữ hóa chất có một lỗ thủng, trong khi cửa nhà kho không được đóng kín.  ',0,'guy-cơ-phat-no-duoc-canh-bao-it-nhat-10-lan-truoc-tham-hoa-o-beirut',NULL,1,76,525,'<img src=\"Assets/images/Beirut_4.jpg\">'),(143,2,'CUỘC SĂN NGỌC CHẾT CHÓC Ở MYANMAR ĐỂ ĐÁP ỨNG CƠN KHÁT TỪ TQ','Vụ sập mỏ ngọc ngày 2/7 ở Myanmar đã khiến khoảng 200 người thiệt mạng. Chỉ 1 tháng sau thảm họa, khu mỏ đã nhộn nhịp trở lại. Người dân ở đây phải bất chấp tính mạng để kiếm sống.','Si Thu Phyo đang cố gắng nhặt nhạnh những viên đá quý thì cảm thấy mặt đất quanh mình rung chuyển.\n\nChàng trai 21 tuổi này đã làm việc chăm chỉ tại một trong những mỏ ngọc lớn nhất thế giới ở bang Kachin, miền bắc Myanmar. Anh chỉ là một trong hàng trăm thợ mỏ trong hố khai thác ngày 2/7. Si Thu cố gắng chạy khi đất đổ xuống, nhưng trước khi thoát được, anh bị nhấn chìm bởi một đợt sóng nước, bùn và đá. <img src=\"Assets/images/113688659_sitthu.jpg\"> Si Thu chới với trong dòng nước. “Miệng tôi đầy bùn, những hòn đá đập vào tôi và sóng đẩy tôi xuống hết lần này đến lần khác”, anh nói với BBC. “Tôi nghĩ rằng tôi sẽ chết”.\n\nNhưng Si Thu đã thoát được. Nằm trong bệnh viện, anh nghe tin 7 người bạn thân nhất của mình không may mắn như vậy. Họ nằm trong số khoảng 200 nạn nhân trong vụ lở đất mỏ ngọc tồi tệ nhất của Myanmar.\n\n“Tôi cảm thấy có lỗi vì là người sống sót”, Si Thu nói. “Tôi muốn bi kịch này chỉ là ác mộng và khi thức dậy, không có vụ sạt lở nào và bạn bè của tôi vẫn ở đây”.\n\nSạt lở xảy ra gần như hàng năm trong mùa mưa tại các mỏ ngọc ở bang Kachin. Các mỏ ở đây sản xuất khoảng 70% lượng ngọc bích trên thế giới - một loại đá quý được người Trung Quốc ưa thích và giao dịch của đá này trị giá hàng tỷ USD mỗi năm.\n\nTrận lở đất tháng trước là vụ việc có số người tử vong nhiều nhất. Không giống như những lần trước, vụ lở đất này đã được quay lại bằng điện thoại. “Mạng xã hội đã khiến mọi người biết đến vụ việc”, Si Thu nói. “Khi Internet và điện thoại chưa có ở đây, chính quyền và các công ty có thể nhắm mắt làm ngơ”.\n\nDưới áp lực của người dân, chính phủ Myanmar đã phải chỉ định một cơ quan điều tra dẫn đầu bởi Bộ trưởng Bộ Bảo tồn Tài nguyên và Môi trường Ohn Win để xác định trách nhiệm và bồi thường cho nạn nhân.\n\nTổng thống Myanmar đã nhận được kết quả điều tra và kết quả vẫn chưa được công khai. Tuy nhiên, ông Ohn Win đã khiến nhiều người đãi ngọc phẫn nộ khi ám chỉ những người đã chết là do “người tham lam”.\n\nÔng Ohn Win nói rằng các mỏ bị đóng cửa trong mùa mưa và chính phủ đã đưa ra cảnh báo về mưa lớn. “Lòng tham là lý do”, ông nói.\n\nTrong khi đó, bà Aung San Suu Kyi, chính trị gia nổi tiếng của Myanmar, cho rằng lý do nằm ở tỷ lệ thất nghiệp.\n\nYan Naing Myo cũng ở trong mỏ ngọc hôm đó và sống sót bằng cách ôm một thùng dầu rỗng. Anh tổn thương sâu sắc với những lời của bộ trưởng môi trường.\n\n“Chính phủ đã cử họ đi điều tra, nhưng họ chỉ đổ lỗi cho chúng tôi, những người phải đi đãi ngọc kiếm sống”, anh Yan Naing nói. “Điều này làm cho chúng tôi không vui giữa lúc chúng vẫn đang chịu nỗi đau mất mát”. <img src=\"Assets/images/113688773_yannaing.jpg\"> Yan Naing, 23 tuổi, có 14 mũi khâu trên đầu và nhiều vết bầm tím trên cơ thể. Anh có bằng Cử nhân về văn học Myanmar và ban đầu chỉ đãi ngọc trong những ngày nghỉ để kiếm “tiền tiêu vặt”. Sau khi tốt nghiệp, anh cảm thấy không có động lực và quay lại mỏ ngọc. Bây giờ đó là công việc duy nhất tôi có thể làm, Yan Naing nói.\n\nSi Thu không học cao như vậy. Anh buộc phải thôi học lúc 10 tuổi, sau khi bão Nargis phá hủy ruộng lúa và sinh kế của gia đình anh vào năm 2008.\n\nGiờ đây, 10 thành viên của gia đình Si Thu sống cùng nhau trong gần khu mỏ. Si Thu thường thức dậy vào khoảng 5h và đôi khi làm việc cả ngày để nhặt nhạnh đá quý.\n\nKhu đất không có luật lệ\nNhững thợ mỏ phải đi đãi ngọc vì thất nghiệp và nghèo đói. Các nhà vận động cho biết thợ mỏ có thể hoạt động ở khu vực nguy hiểm xung quanh mỏ vì chính phủ Myanmar có ít quyền kiểm soát khu vực này.\n\n“Đổ lỗi cho nạn nhân không giải quyết được nguyên nhân họ đến đó. Nguyên nhân thực sự là xung đột, các nhóm vũ trang và thiếu luật pháp”, ông Paul Donowitz, nhà vận động chính của Global Witness, một tổ chức theo dõi việc khai thác tài nguyên thiên nhiên, nói.\n\nCác công ty có liên hệ với quân đội Myanmar, hay Tatmadaw, kiểm soát phần lớn ngành công nghiệp và hoạt động gần như bí mật, theo các nhà vận động. <img src=\"Assets/images/113688869_praying.jpg\"> Các nhóm phiến quân vũ trang đang đòi quyền tự trị trong khu vực và các trùm ma túy cũng có phần trong mỏ ngọc.\n\n“Chúng tôi đã tìm thấy bằng chứng cho thấy các nhóm vũ trang và quân đội đang hợp tác ở mỏ ngọc trong khi xung đột vũ trang với nhau trên chiến trường”, ông Donowitz cho biết.\n\nÔng Donowitz cũng nói thỏa thuận ngừng bắn trên toàn quốc mà chính phủ Myanmar đang cố gắng sắp xếp có thể “phá hủy tất cả lợi ích kinh tế của các bên ở mỏ ngọc”.\n\nNăm 2019, Viện Quản trị Tài nguyên thiên nhiên đã ước tính giá trị của ngọc được khai thác ở Myanmar ở khoảng 15 tỷ USD hàng năm, nhưng phần lớn số ngọc này được khai thác bất hợp pháp.\n\nHầu hết ngọc khai thác ở bang Kachin được mang sang Trung Quốc. Khi Trung Quốc trở nên giàu có hơn, nhu cầu ngọc bích của nước này cũng tăng lên và loại ngọc đắt nhất có thể cao giá hơn vàng.\n\nKhi Si Thu tìm thấy một viên ngọc bích lớn, công ty khai thác hoặc các nhóm vũ trang, hoặc cả hai bên, sẽ lấy phần hoặc chiếm cả viên ngọc, anh nói.\n\nMột trong bốn công ty hoạt động trong khu vực xảy ra vụ lở đất là Kyauk Myat Shwe Pyi, hay công ty khai thác Triple One. <img src=\"Assets/images/113689063_childminers.jpg\"> U Min Thu, một quan chức cấp cao của Bộ Khai thác Mỏ và thư ký của nhóm điều tra, xác nhận với BBC rằng công ty này là một liên doanh giữa Trung Quốc và Myanmar có sự liên kết với nhóm vũ trang dân tộc Wa.\n\n“Các nhóm vũ trang có thỏa thuận hòa bình với chính phủ được phép đầu tư vào nước này”, ông nói.\n\nTrong nỗ lực tạo ra sự minh bạch, chính phủ Myanmar đã công bố thông tin về quyền sở hữu các công ty khai thác và xung đột lợi ích tiềm năng. Tuy nhiên, Global Witness cho biết chỉ 8 trong số 163 công ty tham gia nói chủ sở hữu công ty có quan hệ chặt chẽ với các quan chức quân sự cấp cao hoặc lãnh đạo các nhóm vũ trang dân tộc.\n\nTheo báo cáo của Global Witness, con số công ty có liên quan đến quân đội Myanmar là không hoàn chỉnh và không chính xác. Công ty khai thác Triple One thậm chí không có trong danh sách.\n\nHy vọng tắt dần\nTại nơi xảy ra vụ lở đất khiến bạn của Si Thu thiệt mạng, hoa và nhang đã được đặt xuống vào cuối tháng trước trong một buổi lễ tưởng niệm người chết và người mất tích.\n\nTrong đám đông dự buỗi lễ là bà Daw Mu Mu, người đã đi 20 giờ từ Mandalay để tìm Ko Yarzar, đứa con trai 37 tuổi bị mất tích của mình.\n\n“Mỗi khi tôi nghe nói rằng họ tìm thấy thi thể mới, tôi chạy xuống hố để xem”, bà nói. Đầu gối của bà sưng lên khi leo lên leo xuống. Bà cần tìm thi thể con trai để nhận khoản bồi thường 2.500 USD từ chính phủ và các cơ quan viện trợ. <img src=\"Assets/images/113688661_landslide.jpg\">  ',0,'cuoc-san-ngoc-chet-choc-o-myanmar-de-dap-ung-con-khat-tu-tq',NULL,1,4,123,'<img src=\"Assets/images/113688659_sitthu.jpg\">'),(144,3,'Giá vàng hạ nhiệt sẽ giúp kéo dài xu hướng tăng','Giá vàng thế giới hạ nhiệt khi kinh tế Mỹ phát đi dấu hiệu phục hồi tích cực. Tuy nhiên, giới chuyên gia nhận định việc giá vàng điều chỉnh giảm chỉ giúp kéo dài xu hướng tăng.','Trong phiên giao dịch sáng ngày 7/8 (theo giờ New York), giá vàng giao ngay trên sàn New York sụt giảm mạnh xuống còn gần 2.020 USD/ounce. Giá vàng tương lai giao tháng 10 cũng giảm liên tục từ ngưỡng cao 2.078 USD/ounce đêm qua xuống còn 2.028,2 USD/ounce.\n\nNguyên nhân là giá kim loại quý đang trải qua đợt điều chỉnh mạnh sau khi tăng phi mã trong những ngày qua. Cùng với đó là các tín hiệu phục hồi khả quan của kinh tế Mỹ, đồng USD cũng tăng giá từ mức thấp nhất trong vòng 2 năm. <img src=\"Assets/images/178171.jpg\"> Kéo dài xu hướng tăng\nTheo dữ liệu được Cục Thống kê Lao động Mỹ (BLS) công bố hôm 7/8, tỷ lệ thất nghiệp tại Mỹ đã giảm xuống 10,2% do tổng số việc làm phi nông nghiệp tăng 1,8 triệu trong tháng 7. Tỷ lệ thất nghiệp giảm cùng với những dữ liệu mới được công bố khác, như chỉ số quản lý thu mua PMI (đạt 58,1% trong tháng 7), cho thấy nền kinh tế bắt đầu bước vào giai đoạn phục hồi.\n\nTuy nhiên, trao đổi với Zing, chuyên gia Edward Moya, nhà phân tích cao cấp tại Oanda, nhận định giá vàng sẽ tiếp tục đi lên trong dài hạn do những căng thẳng gia tăng giữa Mỹ và Trung Quốc.\n\n\"Giá kim loại quý đã sẵn sàng cho tuần tăng thứ 9 liên tiếp và sẽ tiếp tục tăng cao hơn khi đà phục hồi của đồng USD kết thúc. Giá vàng có thể chạm ngưỡng 2.300 USD vào cuối năm nay, thậm chí cao hơn nếu nền kinh tế tiếp tục trật bánh\", ông Moya bình luận. <img src=\"Assets/images/361641007_555x370.jpg\"> \"Đừng quên rằng mức tăng của giá vàng càng lớn thì các đợt điều chỉnh giảm càng mạnh. Trong một thị trường giá tăng, việc điều chỉnh giá sẽ kéo dài xu hướng tăng\", chuyên gia Jim Wyckoff của Kitco News bình luận.\n\nTheo chuyên gia tại Oanda, vàng và chứng khoán đều xanh bởi niềm tin rằng các quan chức Mỹ sẵn sàng làm bất cứ điều gì để phục hồi nền kinh tế. \"Một bong bóng sẽ được hình thành. Tuy nhiên, ở thời điểm hiện tại, các nhà đầu tư vẫn tiếp tục đổ tiền vào những tài sản rủi ro do thị trường thu nhập cố định sẽ duy trì lãi suất thấp trong một thời gian dài\", ông Moya giải thích với Zing.\n\n\"Mối lo ngại lạm phát cuối cùng sẽ chấm dứt tình trạng mua tài sản rủi ro một cách mù quáng của các nhà đầu tư\", ông dự đoán.\n\nKích thích kinh tế bằng bất cứ giá nào\nBình luận về cuộc bầu cử tổng thống Mỹ sắp tới, chuyên gia tại Moya cho rằng bất cứ ai vào Nhà Trắng cũng sẽ tìm mọi cách để kích thích nền kinh tế. \"Cựu Phó tổng thống Mỹ Joe Biden có thể tạo ra một môi trường thuế khắc nghiệt hơn đối với Phố Wall, nhưng chính sách của ông này vẫn hỗ trợ cho người Mỹ và tập trung vào đầu tư cơ sở hạ tầng\", ông nói thêm.\n\nCác lệnh phong tỏa nhằm ngăn chặn dịch Covid-19 đã gây ra một cú sốc đối với hệ thống kinh tế Mỹ, buộc giới chức trách phải tìm mọi cách để cứu hàng triệu việc làm. Điều này tạo nên làn sóng chuyển từ tài sản thu nhập cố định sang tài sản rủi ro có lợi nhuận cao hơn, chẳng hạn như cổ phiếu và vàng.\n\n\"Các động thái của Cục Dự trữ Liên bang (FED) nhằm đối phó với đại dịch khiến lãi suất thực rơi tự do. Cả thế giới đang tranh giành lợi suất, trong khi các nhà hoạch định sẽ chậm chạp hơn trong việc loại bỏ gói kích thích kinh tế\", ông Moya bình luận. <img src=\"Assets/images/Gold_with_USDD.jpg\"> Trong khi đó, các chuyên gia kinh tế của Ngân hàng đầu tư Citi cho rằng giá vàng tăng không báo hiệu lạm phát. Trợ lực chính của giá kim loại quý là lãi suất thực âm. Điều này có nghĩa là lợi nhuận mà người đầu tư nhận được từ trái phiếu sẽ bằng hoặc thấp hơn tỷ lệ lạm phát. Chi phí cơ hội của việc nắm giữ vàng giảm đi dẫn đến quyết định mua vào.\n\n\"Tuy nhiên, đà tăng của giá vàng không báo hiệu một cuộc bùng nổ lạm phát, hay đồng USD mất đi vị thế đồng tiền dự trữ hàng đầu của thế giới\", các chuyên gia của Citi viết trong một ghi chú.\n\n\"Không đồng tiền nào có thể đảm nhận vai trò của đồng USD. Ngay cả khi giá trị của đồng USD so với vàng thấp hơn thì tất cả đồng tiền khác đều như vậy\", Citi nói thêm.\n\nCác chuyên gia tại Citi cũng cho rằng giá kim loại quý sẽ vượt mức 2.100 USD/ounce vào quý này và chạm ngưỡng 2.300 USD/ounce từ 6 đến 12 tháng tới. Tuy nhiên, việc giá vàng xuyên thủng ngưỡng 3.000 USD/ounce trong năm nay là khá mong manh.  ',0,'gia-vang-ha-nhiet-se-giup-keo-dai-xu-huong-tang',NULL,1,65,533,'<img src=\"Assets/images/Gold_with_USDD.jpg\">'),(145,3,'Vàng bị chốt lời, người mua trong nước lỗ 2,6 triệu sau một đêm','Đúng với dự báo trước đó của các chuyên gia, giá vàng thế giới phiên 7/8 (giờ Mỹ) chịu áp lực chốt lời lớn từ giới đầu tư và đảo chiều giảm mạnh.','Dù đã phục hồi vào cuối ngày, giá vàng thế giới phiên 7/8 (giờ Mỹ) chủ yếu nằm trong xu hướng đi xuống khi kim quý có phiên điều chỉnh mạnh đầu tiên sau nhiều ngày tăng liên tiếp.\n\nVàng trên sàn New York đêm qua (giờ Việt Nam) có lúc rơi xuống mốc 2.015 USD/ounce, giảm gần 50 USD so với mức đỉnh trong phiên. Áp lực chốt lời chỉ giảm về cuối phiên giúp giá vàng đóng cửa ở mức 2.034,8 USD/ounce, giảm 28,4 USD so với phiên liền trước. Đây cũng là phiên giảm thứ 2 của vàng trong tuần này và là phiên giảm mạnh nhất.\n\nGiá vàng trên sàn Kitco đêm qua cũng chứng kiến biến động mạnh với biên độ gần 50 USD trong ngày, vàng giao ngay tại đây đóng cửa ở mức 2.034,8 USD/ounce, giảm tương ứng 1,4% so với cuối ngày 6/7.\n\nMua vàng trong nước lỗ nặng sau một đêm\nGiá đã giảm mạnh nhưng so với cuối tuần trước, vàng giao ngay vẫn tăng gần 60 USD, tương đương gần 3% giá trị. Tính trong 1 tháng gần nhất, kim loại quý thế giới đã tăng tới 212 USD/ounce, tương đương 11,7%, lợi suất đầu tư vượt mọi loại tài sản thông thường.\n\nTuy vậy, đà giảm của vàng thế giới đêm qua cũng kéo vàng trong nước giảm phiên cuối tuần sáng nay.\n\nSau khi chốt giá giao dịch hôm qua (7/8) ở mức 60,75 triệu/lượng (mua) và 62,4 triệu/lượng (bán), Công ty TNHH MTV Vàng bạc Đá quý Sài Gòn (SJC) sáng nay đã điều chỉnh giá mua tại TP.HCM xuống 59,8 triệu đồng, thấp hơn 950.000 đồng/lượng so với chiều qua. Giá bán ra cũng bị SJC giảm 700.000 đồng xuống 61,7 triệu/lượng.\n\nĐà giảm mạnh tương tự cũng được áp dụng với các cửa hàng tại Hà Nội khi giá bán ra đã giảm xuống mức 61,72 triệu đồng. <img src=\"Assets/images/VANG_QUAY_DAU_GIAM.jpg\"> Mức giảm nói trên đã khiến những nhà đầu tư mua vàng hôm qua đến sáng nay chịu khoản lỗ ròng 2,6 triệu đồng (bao gồm cả chênh lệch giá mua bán), mức rủi ro đã được nhiều chuyên gia trong nước cảnh báo. Đà giảm cũng khiến hầu hết nhà đầu tư mua vàng từ giữa tuần đến nay thua lỗ tiền triệu.\n\nCũng trong sáng nay, Công ty CP Vàng bạc Đá quý Phú Nhuận (PNJ) niêm yết giá vàng miếng ở mức 59,8 - 61,2 triệu đồng/lượng (mua vào - bán ra), giảm lần lượt 300.000 đồng và 700.000 đồng so với chiều qua. Đây cũng là phiên giảm mạnh nhất mà PNJ điều chỉnh với giá vàng miếng tuần này.\n\nTrong khi đó, cả Bảo Tín Minh Châu và Tập đoàn Vàng bạc Phú Quý sáng nay đều giảm mạnh giá bán vàng về mốc 61 triệu đồng/lượng, thấp hơn cả triệu đồng so với chiều qua. Số giảm nói trên cũng khiến người mua vàng tại các doanh nghiệp này đang chịu khoản lỗ hơn 2,5 triệu đồng/lượng sau một đêm.\n\nTuy nhiên, tính trong cả tuần này, vàng miếng vẫn tăng gần 3,8 triệu/lượng, tương đương 6,3%, mang lại cho nhà đầu tư mức lãi ròng gần 1,9 triệu/lượng, tương đương 3,2%/tuần.\n\nCòn giảm trong tuần tới?\nVàng thế giới đêm qua giảm mạnh chủ yếu do nhà đầu tư chốt lời sau khi đạt mốc lợi nhuận kỳ vọng. Trước đó, dữ liệu của các sàn giao dịch vàng chỉ ra đã có lượng lớn nhà đầu tư tham gia thị trường tại vùng giá 1.950 USD/ounce và mốc 2.060 USD/ounce (trước khi vàng giảm mạnh) là điểm chốt lời lý tưởng với các nhà đầu tư này.\n\nTuy vậy, việc vàng bị điều chỉnh giảm cũng phù hợp với dự báo trước đó của cả chuyên gia trong nước và quốc tế khi mặt hàng này đã tăng quá \"nóng\" từ tháng 7 đến nay.\n\n <img src=\"Assets/images/Chi_Hung11.jpg\"> Trên thực tế, các đợt điều chỉnh là dấu hiệu tốt cho thị trường vì nó cho vàng thêm thời gian để củng cố một vùng giá trước khi tạo ra các đợt tăng giá mới, đồng thời cũng là cơ hội tham gia thị trường của các nhà đầu tư đã bỏ lỡ trước đó.\n\n\"Dù các trợ lực cơ bản cho vàng vẫn tích cực, nhưng về mặt kỹ thuật, vàng đã tăng mạnh trong khi khối lượng mua giảm và đã quá mức chỉ báo trên biểu đồ. Vàng có thể phải kiểm tra lại mốc 2.000 USD/ounce hoặc 1.925 USD là mốc đột phá trước đó”, ông Colin Cieszynski, chiến lược gia thị trường trưởng của SIA Wealth Management cho biết.\n\nTrong khi đó, nhà phân thị trường cấp cao của Kitco, ông Jim Wyckoff cho biết giá vàng cuối tuần đã giảm sau khi báo cáo việc làm có phần lạc quan của Mỹ được phát đi và chỉ số đồng USD phục hồi từ mức thấp nhất 2 năm vào ngày thứ năm.\n\nVị chuyên gia nhấn mạnh phiên giảm cuối tuần này chỉ là đợt điều chỉnh trong xu hướng tăng của kim loại quý. Tuy nhiên, những nhà đầu cơ giá sẽ lo lắng khi các đợt điều chỉnh giảm lớn xảy ra. “Điều quan trọng là bất kỳ sự điều chỉnh giảm lớn nào đều không vi phạm vào xu hướng tăng trên biểu đồ”, ông nói.\n\nJim Wyckoff cũng cho biết thêm bất kỳ sự phục hồi nào của kim loại quý tuần tới không có nghĩa là đợt tăng giá đã kết thúc. Ngược lại, nếu tiếp tục điều chỉnh, đà giảm giá dự báo sẽ thấp hơn trong tuần tới vì có rất nhiều nhà đầu tư đứng ngoài chờ đợi để nhảy vào thị trường ngay khi thấy cơ hội.  ',0,'vang-bi-chot-loi-nguoi-mua-trong-nuoc-lo-26-trieu-sau-mot-dem',NULL,1,2,431,'<img src=\"Assets/images/Chi_Hung11.jpg\">'),(146,3,'Vì sao Mỹ quyết diệt TikTok?','Tổng thống Donald Trump khẳng định sẽ cấm TikTok hoạt động ở Mỹ ngay cuối tuần này. Ứng dụng video đến từ ByteDance trở thành biểu tượng của ','Trên chuyên cơ Air Force One tới Florida hôm 31/7, Tổng thống Trump cho biết sẽ ra sắc lệnh hành pháp để cấm TikTok tại Mỹ ngay từ ngày 1/8. Trước đó, Ngoại trưởng Mỹ Mike Pompeo cũng nhiều lần cảnh báo về nguy cơ an ninh từ ứng dụng chia sẻ video của Trung Quốc.\n\n\"Người dân Mỹ cần lo ngại với TikTok, bởi đó là công cụ giám sát của chính quyền Trung Quốc. Đó là con ngựa gỗ thành Troy trong điện thoại của mọi người\", The Atlantic dẫn lời Thượng nghị sĩ Josh Hawley khẳng định.\n\nTại sao một ứng dụng chia sẻ video có thể đe dọa an ninh nước Mỹ? Theo The Atlantic, ở Washington, đây là mối lo ngại thực sự lớn và trong những tuần gần đây, các chính trị gia Mỹ ngày càng tỏ ra cứng rắn với TikTok. Ứng dụng của ByteDance trở thành biểu tượng của mối đe dọa Trung Quốc với xã hội và ngành công nghệ Mỹ. <img src=\"Assets/images/camtik1.jpg\"> Trong nhiều năm qua, mạng Internet trở thành \"lãnh địa\" của các tập đoàn Mỹ như Alphabet (công ty mẹ của Google), Amazon, Microsoft và Facebook. TikTok là công ty Trung Quốc đầu tiên đạt bước đột phá trên thị trường toàn cầu theo cách mà Alibaba, Baidu hay Tencent chưa thể làm nổi.\n\nChính quyền Washington lo ngại Bắc Kinh sẽ sử dụng TikTok để thu thập hình và thông tin của người Mỹ trong các chiến dịch tình báo. Mối quan hệ Mỹ - Trung đang xấu đi nghiêm trọng vì cuộc thương chiến và dịch Covid-19, và TikTok trở thành mặt trận mới trong cuộc đối đầu này.\n\nTheo hãng nghiên cứu Sensor Tower, đến nay TikTok được tải 165 triệu lần tại Mỹ và hơn 2 tỷ lần trên phạm vi toàn cầu. Nhận thấy mối quan hệ Mỹ - Trung ngày càng xấu đi, CEO ByteDance Trương Nhất Minh xác định TikTok là một nền tảng quốc tế. TikTok chưa bao giờ hoạt động tại Trung Quốc, ByteDance có một phiên bản nội địa mang tên Douyin.\n\nTrong thời gian qua, CEO Trương nỗ lực tìm cách xoa dịu những lo ngại của Mỹ. Đội ngũ quản lý thị trường Mỹ của TikTok được đưa ra khỏi Trung Quốc và đến Mỹ. Hồi tháng 3, ông Trương thuê nhà vận động hành lang nổi tiếng Michael Beckerman để tiếp cận các quan chức Washington. Đến tháng 5, cựu lãnh đạo Dinsey Kevin Mayer được bổ nhiệm làm CEO TikTok. <img src=\"Assets/images/zhang.jpg\"> Tuy nhiên, chừng đó là không đủ để thuyết phục các quan chức Mỹ. Thượng nghị sĩ Josh Hawley mô tả những nỗ lực tu sửa của TikTok là \"lố bịch\" và nhấn mạnh \"đó vẫn luôn là một công ty Trung Quốc\". Đồng quan điểm, luật sư Dan Harris của hãng Harris Bricken bình luận: \"Có lẽ nhân sự TikTok chỉ muốn kinh doanh để sinh lãi. Nhưng nếu hiểu cách Trung Quốc hành động, không ai có thể quả quyết rằng TikTok là an toàn\".\n\nHồi tháng 3, Thượng nghị sĩ Hawley trình dự luật cấm nhân viên chính phủ Mỹ sử dụng TikTok. Bộ Quốc phòng Mỹ đã yêu cầu quân nhân xóa bỏ ứng dụng này khỏi điện thoại. Ủy ban Đầu tư Nước ngoài tại Mỹ đã mở cuộc điều tra vụ ByteDance mua ứng dụng Musical.ly.\n\n\"Các tốt nhất với ByteDance là bán TikTok cho một công ty Mỹ\", Thượng nghị sĩ Hawley khẳng định. Nguồn tin Bloomberg cho biết Microsoft đang đàm phán để mua lại TikTok. Ước tính định giá của TikTok hiện vào khoảng 50 tỷ USD, cao gấp 50 lần so với doanh thu 1 tỷ USD dự kiến năm nay.  ',0,'vi-sao-my-quyet-diet-tiktok',NULL,1,65,5425,'<img src=\"Assets/images/zhang.jpg\">'),(147,3,'Dinh thự xa xỉ của các trùm tội phạm khét tiếng thế giới','Nhiều ông trùm tội phạm sống trong những căn biệt thự xa hoa được dát vàng, chứa các tác phẩm nghệ thuật đắt tiền và nội thất siêu xa xỉ.','<img src=\"Assets/images/1_1.jpeg\"> Pablo Escobar: Có tài sản ước tính trị giá tới 30 tỷ USD trước khi qua đời vào thập niên 1990, trùm ma túy khét tiếng người Colombia là một trong những tên tội phạm giàu có nhất lịch sử. Dù sống chủ yếu tại Colombia, Escobar sở hữu một dinh thự nghỉ dưỡng tại Tulum, Mexico (trong ảnh). <img src=\"Assets/images/1_2.jpeg\"> Đây là nơi gã trùm ma túy trú ẩn. Nằm tách biệt, dinh thự trước biển này còn được xây tường chống đạn và được bảo vệ nghiêm ngặt. Dù chi không ít tiền để bảo vệ an toàn cho bản thân, Escobar bị bắt chết tại quê nhà Medellín, Colombia vào tháng 12/1993. <img src=\"Assets/images/1_3.jpeg\"> Sau khi Escobar qua đời, dinh thự \"thiên đường\" tại Tulum bị bỏ hoang. Tới tận năm 2012, nó mới được nhà sưu tầm nghệ thuật Lio Malca tại New York mua lại và xây dựng thành khách sạn 5 sao Casa Malca với 71 phòng. Hiện, nơi đây được xem là một trong những điểm lưu trú hàng đầu tại Mexico. Điểm nhấn của khách sạn này là bể bơi dưới lòng đất, được cho là nơi Escobar dùng để cất giấu tiền mặt kiếm được từ hoạt động buôn ma túy. <img src=\"Assets/images/2_1.jpeg\"> Jho Low: Năm 2012, doanh nhân người Malaysia Jho Low chi 39 triệu USD mua dinh thự theo phong cách đương đại này Los Angeles, Mỹ. Không lâu sau đó, Low bị Cục Điều tra Liên bang Mỹ (FBI) truy nã bởi có liên quan tới bê bối tham nhũng hàng tỷ USD tại quỹ đầu tư 1MDB của Malaysia. <img src=\"Assets/images/2_2.jpeg\"> Theo cáo trạng, Low bị buộc tội rửa tiền hàng tỷ USD qua các hoạt động phi pháp. Sau khi Low trốn khỏi Mỹ, chính quyền liên bang đã tịch thu và rao bán dinh thự. Bất động sản có 6 phòng ngủ, 7 phòng tắm được bán vào năm ngoái với giá 18,5 triệu USD, thấp hơn nhiều so với mức giá đề nghị 24,5 triệu USD và chưa bằng một nửa số tiền Low trả để mua nó vào năm 2012. <img src=\"Assets/images/2_3.jpeg\"> Vì không có thời gian tháo dỡ đồ đạc, Low đã để lại hầu hết mọi thứ trong dinh thự của mình. <img src=\"Assets/images/6_1.jpeg\"> Bernie Madoff: Nhà đầu tư người Mỹ Bernie Madoff lừa 4.800 khách hàng và thu lợi bất chính 64,8 tỷ USD trong vụ lừa đảo tài chính lớn nhất trong lịch sử nước Mỹ. Đây cũng là vụ lừa đảo theo mô hình Ponzi lớn nhất thế giới. Ông ta bị kết án 150 năm tù giam vào năm 2009. Trước khi bị bắt, Madoff sở hữu danh mục bất động sản đồ sộ, trong đó dinh thự biển tại Hamptons (ảnh). <img src=\"Assets/images/2_5.jpeg\"> Dinh thự có thiết kế mái vòm độc đáo. Trong ảnh là phòng khách với lò sưởi bằng đá cao 10 m. <img src=\"Assets/images/6_3.jpeg\"> Dinh thự được trang bị hệ thống tự động hiện đại và thông minh, được rao bán với giá 21 triệu USD vào năm 2018. <img src=\"Assets/images/1_5.jpeg\"> Charles Ponzi: Là cha đẻ của mô hình lừa đảo đa cấp nổi tiếng, Charles Ponzi sở hữu khối tài sản khổng lồ. Ông ta mua căn nhà theo phong cách thời Phục Hưng (ảnh) tại Lexington, Massachusetts vào năm 1920, chỉ vài tháng trước khi bị bắt vì thực hiện 86 vụ lừa đảo. Tại thời điểm mua căn nhà, Ponzi được cho là kiếm hơn 250.000 USD/ngày - tương đương 3,2 triệu USD ngày nay - từ các mạng lưới đầu tư theo kiểu đa cấp. <img src=\"Assets/images/2_5.jpeg\"> Kiếm tiền dễ dàng từ mạng lưới lừa đảo, Ponzi thuê nhiều nhân viên phục vụ trong căn nhà và không tiếc tiền chi cho nội thất xa xỉ, từ giấy dán tường bằng lụa cho tới điều hòa không khí, bể bơi nước nóng. Tuy nhiên, Ponzi chẳng có nhiều cơ hội tận hưởng sự xa hoa của căn nhà bởi không lâu sau khi mua, ông đã bị tuyên án 14 năm tù vì tội lừa đảo. <img src=\"Assets/images/3_6.jpeg\"> Phòng ăn sang trọng với bàn lớn. Căn nhà với 7 phòng ngủ, 5 phòng tắm hiện được rao bán với giá 4 triệu USD.  ',0,'dinh-thu-xa-xi-cua-cac-trum-toi-pham-khet-tieng-the-gioi',NULL,1,76,132,'<img src=\"Assets/images/3_6.jpeg\">'),(148,3,'Bên trong biệt thự 24 triệu USD của ông bầu Simon Cowell','Simon Cowell là một trong những ông bầu nổi tiếng nhất ngành giải trí toàn cầu. Ông sở hữu một căn biệt thự hào nhoáng, có giá 24 triệu USD ở bang California, Mỹ.','<img src=\"Assets/images/simon.jpg\"> Bất động sản của Simon Cowell rộng gần 6.600 m2, tọa lạc tại thành phố nghỉ dưỡng Malibu, bao quanh bởi cảnh sắc núi đồi, đại dương. Ông mua nó vào tháng 12/2017 <img src=\"Assets/images/2.jpg\"> Căn biệt thự chính có diện tích xấp xỉ 800 m2 với 6 phòng ngủ, 7 phòng tắm, được hoàn thiện vào năm 1988 với phong cách kiến trúc đương đại <img src=\"Assets/images/3.jpg\"> Hầu hết không gian sống bên trong ngôi nhà được lát sàn gỗ để tạo nét tương phản với những bức tường thạch cao. Phòng khách sáng sủa, thoáng đãng với những ô cửa mở ra khung cảnh thiên nhiên phong phú. <img src=\"Assets/images/4.jpg\"> Nhà bếp sang trọng, mang những vẻ đẹp mộc mạc khi các công cụ nấu nướng hiện đại được tích hợp với những chiếc tủ gỗ cỡ lớn. <img src=\"Assets/images/5.jpg\"> Phòng ăn rộng rãi với những nội thất không quá cầu kỳ nhưng được bài trí tinh tế và thanh lịch. <img src=\"Assets/images/6.jpg\"> Phòng ngủ chính rộng lớn, thông thoáng, được thiết kế theo phong cách cởi mở. Căn phòng được trang bị một khu vực tiếp khách và một ban công để ngắm cảnh. <img src=\"Assets/images/7.jpg\"> Phòng tắm chính độc đáo, có đầy đủ những tiện nghi cao cấp cần thiết. <img src=\"Assets/images/8.jpg\"> Hồ bơi và bồn tắm nước nóng ngoài trời là địa điểm lý tưởng để gia chủ vừa ngâm mình, vừa thưởng ngoạn cảnh vật xung quanh. <img src=\"Assets/images/9.jpg\"> Một số tiện ích thư giãn, giải trí nổi bật khác của căn biệt thự bao gồm khu vực ăn uống ngoài trời, sân tennis tối tân, hồ cá, và bãi đậu xe rộng rãi.  ',0,'ben-trong-biet-thu-24-trieu-usd-cua-ong-bau-simon-cowell',NULL,1,86,5346,'<img src=\"Assets/images/9.jpg\">'),(149,4,'Bi kịch của Ronaldo','Dù giúp Juventus thắng 2-1 trước Lyon ở trận lượt về vào rạng sáng 8/8 (giờ Hà Nội), Ronaldo vẫn phải ngậm ngùi chia tay Champions League.','Thời khắc kết thúc trận đấu giữa Juventus và Lyon, khi đồng hồ chuyển sang phút bù giờ thứ 7, cũng là lúc đánh dấu bi kịch của một cầu thủ số 7 ở trên sân. Anh đã ghi bàn, thậm chí là ghi 2 bàn để giúp Juventus chiến thắng. Nhưng thế là chưa đủ, Juventus đã bị loại vì luật bàn thắng trên sân khách.\n\nNăm thứ 2 liên tiếp, \"Mr. Champions League\" Cristiano Ronaldo dừng bước ở vòng knock-out Champions League. Anh mang theo giấc mộng dang dở của bản thân, và của chính Juventus về nỗi thất vọng và bi kịch của chính anh khi đặt chân đến Italy. <img src=\"Assets/images/ronaldo.jpg\"> Ra đi với tham vọng lớn\nMùa hè 2018, khi trái bóng Telstar 18 còn lăn trên các thảm cỏ nước Nga, một thông tin gây rúng động làng túc cầu giáo đã xuất hiện: Ronaldo sẽ rời Real Madrid sau 9 năm gắn bó để đến với Juventus. Cả Ronaldo lẫn người đại diện Jorge Mendes đều quyết định chọn thời điểm kết thúc World Cup 2018 để ra mắt chính thức.\n\nNgày Ronaldo đến Turin, hơn 20.000 người hâm mộ Juventus đã tràn ra sân bay để chờ đợi sự xuất hiện của siêu sao người Bồ Đào Nha. Lúc đó Ronaldo khiến cả thành phố lên cơn sốt, các cửa hiệu ra mắt các sản phẩm mới đón chào anh, và tờ Tutto Sport – một trong những tờ báo thể thao quan trọng nhất nước Ý dành mọi thông tin cho Ronaldo, trong khi chuyện về đội tuyển Pháp giành chức vô địch World Cup chỉ chiếm một góc khiêm tốn.\n\nNăm ấy, Ronaldo 33 tuổi với múi cơ của người 26 tuổi cùng bản hợp đồng có giá trị 100 triệu euro giúp cho cổ phiếu của Juventus tăng vọt. Anh đến Juventus với 2 hy vọng, một giấc mộng phục hưng của cả Serie A, điều những tifosi hoài cổ trông ngóng, và một giấc mộng có tên Champions League, điều các Juventini khao khát nhất.\n\nNhưng cuộc đời nào có phải là giấc mơ, Ronaldo cũng nào phải ông phải là ông tiên, ông bụt để biến mọi thứ thành sự thật. Serie A vẫn phải ngước nhìn La Liga và Premier League sau 2 năm Ronaldo đặt chân đến đây, còn Juventus vẫn \"nghìn năm thương hoài\" chiếc cup tai to.\n\nBi kịch ở chỗ, những sự thất bại đó nên cần nói sao đây? Ai dám bảo Ronaldo không cố gắng, ai bảo Ronaldo không chiến đấu? Anh đã làm tất cả, và đó là bi kịch của anh, của hôm nay, và có thể là của ngày mai.\n\nKhi Ronaldo đến Serie A, anh giúp giải đấu này tăng giá bản quyền phát sóng, khiến các sân bóng kín đặc khán giả ở mỗi trận đấu anh góp mặt. Ngày 14/03/2019, khi CR7 lập cú hat-trick giúp Juventus vượt qua Atletico Madrid với tỷ số 3-0 ở Champions League mùa giải 2018/19, chàng trai này đã đưa cổ phiếu của Juventus tăng vọt 24% so với phiên giao dịch hôm trước.\n\nĐúng như Chủ tịch Florentino Perez của Real từng phát biểu: \"Các siêu sao trả tiền mua họ\". Ba bàn thắng hôm ấy chính là hiện thân của hình ảnh rất biểu tượng sau này, có một Ronaldo gồng gánh một tập thể mất dấu ADN Champions League, một tập thể tuy là \"ngáo ộp\" tại giải quốc nội nhưng lại đầy yếm thế ở châu Âu, và không thể tới được khải hoàn môn.\n\nTừ trận lượt về với Atletico Madrid năm ngoái, đến trận lượt về với Lyon rạng sáng 8/8, khi Juve bị thất thế sau lượt đi và cần lội ngược dòng, cả đội bóng vẫn chỉ biết trông chờ vào Ronaldo, gọi tên Ronaldo. Và Ronaldo – con người quá quen với áp lực, thoải mái trước áp lực vẫn biết cách tỏa sáng.\n\n2 mùa giải, Juventus đá 6 trận knock-out Champions League, ghi được 7 bàn. Oái ăm thay, cả 7 bàn đó đều do Cristiano Ronaldo ghi. Bi kịch chính ở chỗ, từng đó nào có đủ.\n\nHai bàn thắng vào lưới Lyon đêm qua đã giúp Ronaldo cán mốc 130 bàn thắng tại Champions League, đạt được 67 bàn ở các vòng loại trực tiếp. Đấy đều là những con số không tưởng, là những kỷ lục vô cùng khó phá.\n\n <img src=\"Assets/images/cr7.jpg\"> Nhưng không thắng được kẻ thù thời gian\nVà khi ta chứng kiến một con người vĩ đại dường ấy, ở tuổi 35 vẫn lập đỉnh ở các cột mốc, vẫn biết cách khiến người ta nhắc đến tên, nhớ đến mặt, mà lại thất bại, còn đội bóng bị loại. Đó thật sự là một tiếc nuối vơ bờ, bởi cái chất vĩ đại hệt một hoàng đế trên khúc nhạc hiệu Champions League, bởi cách anh đã ghi bàn, đã cống hiến. Anh xứng đáng cho những điều tốt đẹp hơn là 2 lần cúi đầu sau những bàn thắng anh ghi, sau những pha bóng anh làm, sau những kỷ lục anh tạo dựng.\n\nBi kịch của Ronaldo còn vì anh đã 35 tuổi. Ở độ tuổi đó, thời gian không còn chờ đợi anh nữa. Những năm từ 2009 đến 2012, khi anh mất những Quả bóng vàng vào tay \"đại kình địch\" Lionel Messi, khi anh phải chịu đựng những khổ ải gian lao của trập trùng thử thách, thì anh mới 25 tuổi.\n\nThời gian còn đủ dài để anh đuổi theo và đuổi kịp. Nhưng còn giờ đây, ai cho anh thêm thời gian nữa? Chuyên nghiệp bao nhiêu, ăn uống sinh hoạt điều độ bao nhiêu, há có thể đua tài cũng tuế nguyệt? Nữ hoàng của thể loại tiểu thuyết trinh thám Agatha Christie từng có một câu nói nổi tiếng: \"Thời gian là kẻ sát nhân giỏi nhất\".\n\nVới con số hơn 200 thủ môn bị anh đánh bại trong sự nghiệp, Ronaldo là sát thủ của mọi thủ môn trên gầm trời này. Nhưng thời gian lại là kẻ sát nhân không đối thủ. Bi kịch của Ronaldo trong đêm Turin là anh không còn thời gian nữa. Dù khát vọng anh vẫn cháy bỏng, anh muốn có 7 quả bóng vàng, anh muốn có 7 Champions League.\n\nKhi cơn mưa đến, các loài chim núp dưới tán cây, nhưng đại bàng bay trên những đám mây. Ai dám chế giễu khát vọng của người khổng lồ?\n\nĐêm qua, có một tiếng bi ca ngân dài.  ',0,'bi-kich-cua-ronaldo',NULL,1,76,534,'<img src=\"Assets/images/cr7.jpg\">'),(150,4,'Thái Lan định vay tiền FIFA để phục vụ đội tuyển quốc gia','Hiệp hội Bóng đá Thái Lan (FAT) tính mượn tiền FIFA để trang trải kinh phí cho thầy trò ông Akira Nishino thi đấu quốc tế.','Nguồn tài chính eo hẹp đang là vấn đề với bóng đá Thái Lan. Chủ tịch FAT Somyot Poompunmuang thừa nhận tổ chức này cần vay tiền để giải quyết khủng hoảng. Vấn đề này được ông Somyot chia sẻ trên tờ Bangkok Post. <img src=\"Assets/images/somyot_fat_thai_lan_muon_tien.jpg\"> \"Các giải đấu quốc nội đang được lên kế hoạch diễn ra trong thời gian tới. Nhưng mọi thứ sẽ không thể tiến hành nếu chúng tôi không xoay được tiền từ nhà tài trợ. Thời gian còn lại quá ít. FAT quyết định sẽ vay không lãi suất 6 tháng đến một năm từ các nguồn trong và ngoài nước\", ông nói.\n\n\"Chúng tôi cũng cần tiền mặt ngay lúc này để lo cho đội tuyển quốc gia tại các giải đấu quốc tế sắp tới. Với mối quan hệ tốt từ FIFA và AFC, FAT tự tin sẽ vay được tiền mặt. Bóng đá Thái Lan cần tiền để tiến về phía trước\", lãnh đạo cao nhất của FAT trình bày.\n\nTrang SMM Sport của Thái Lan cho rằng đây là lần đầu tiên sau 104 năm, FAT phải đi vay tiền để trang trải hoạt động. FAT thành lập năm 1916, trở thành thành viên của FIFA năm 1925, là thành viên AFC năm 1954 và gia nhập AFF vào năm 1984.\n\nTrước đó, Liên đoàn Bóng đá Châu Á (AFC) đã chính thức chốt lại lịch thi đấu Vòng loại thứ hai World Cup 2022 vào hôm 6/8. Đoàn quân của ông Nishino còn 3 trận, gồm 2 trận trên sân nhà gặp Indonesia (8/10) và Malaysia (17/11), cùng một chuyến hành quân đến UAE (12/11). Trước đó, tuyển Thái cần phải tập huấn và thi đấu giao hữu quốc tế.\n\nNếu lịch thi đấu diễn ra đúng kế hoạch, FAT phải xoay sở tài chính ngay từ bây giờ. Ảnh hưởng của dịch Covid-19 đã khiến tất cả giải đấu ở Thái Lan phải dừng lại. Thai League 1 đổi lịch thi đấu từ tháng 9/2020 đến 5/2021. Thay đổi quan trọng này tác động mạnh đến các hợp đồng tài trợ và quảng cáo.\n\nTheo Bangkok Post, đơn vị giữ bản quyền Thai League 1 là TrueVisions đã từ chối phát sóng tiếp các trận đấu sau ngày 25/10 vì hết hợp đồng. Hãng xe tài trợ cho Thai League Cup cũng đã rút lui.\n\nCăng thẳng ở các giải quốc nội có thể ảnh hưởng tới đội tuyển Thái. \"Voi chiến\" đang đứng thứ 3 với 8 điểm ở bảng G. Chanathip Songkrasin và đồng đội thua Malaysia 1 điểm và kém Việt Nam 2 điểm trên bảng xếp hạng. <img src=\"Assets/images/bang_xep_hang.jpg\">  ',0,'thai-lan-dinh-vay-tien-fifa-de-phuc-vu-doi-tuyen-quoc-gia',NULL,1,34,542,'<img src=\"Assets/images/bang_xep_hang.jpg\">'),(151,4,'Huyền thoại 53 tuổi đi vào lịch sử bóng đá thế giới','Kazuyoshi Miura tiếp tục chinh phục những cột mốc mới sau khi ra sân thi đấu hơn 60 phút trong màu áo Yokohama FC.','Trang chủ J.League đưa tin Miura (ở tuổi 53, 5 tháng và 10 ngày) trở thành cầu thủ lớn tuổi nhất lịch sử bóng đá thế giới ra sân trong khuôn khổ giải đấu cúp chính thức. <img src=\"Assets/images/miuranhat.jpeg\"> Huyền thoại bóng đá sinh năm 1967 cũng trở thành duy nhất trên thế giới chơi bóng chuyên nghiệp vắt qua 5 thập niên từ những năm 1980 của thế kỷ trước cho đến năm 2020.\n\nMiura ra sân ngay từ đầu trong trận đấu của Yokohama FC với Sagan Tosu ở vòng bảng Cúp Quốc gia Nhật Bản (Levain Cup) 2020. “King Kazu” chơi trọn 60 phút thi đấu, dìu dắt các đàn em trong chiến thắng 1-0 của Yokohama FC.\n\nĐội bóng từng chiêu mộ tiền vệ Tuấn Anh của HAGL vừa thăng hạng J.League 2020. Tuy nhiên, Miura chưa có cơ hội ra sân tại giải đấu hạng cao nhất của bóng đá xứ sở mặt trời mọc.\n\nTrận đấu ở Levain Cup là lần đầu tiên mùa này “King Kazu” được chơi chính thức. Vào tháng 1 năm nay, Yokohama FC gia hạn hợp đồng một năm với Miura, tiền đạo đã bước sang tuổi 53 vào tháng 2. \"King Kazu\" từng được sách kỷ lục Guinness ghi nhận là cầu thủ chuyên nghiệp già nhất lịch sử bóng đá.\n\nĐến tháng 2 năm nay, cầu thủ Ezzeldin Bahader (75 tuổi) được một đội bóng ở giải hạng Ba Ai Cập chiêu mộ và có cơ hội trở thành cầu thủ lớn tuổi nhất thế giới được sách kỷ lục Guinness công nhận.\n\nTuy nhiên, Bahader là tay ngang bước vào nghiệp cầu thủ. Còn Miura lại là huyền thoại về sự bền bỉ của môn thể thao vua.\n\nNăm 2017, Kazu Miura trở thành cầu thủ già nhất lịch sử bóng đá thế giới chơi bóng chuyên nghiệp và ghi bàn. Anh lập công cho CLB Yokohama FC trong chiến thắng 1-0 trước Thespakusatsu Gunma tại J2 League khi 50 tuổi và 14 ngày, phá kỷ lục của Stanley Matthews (Anh) ghi bàn lúc 50 tuổi 5 ngày.\n\n <img src=\"Assets/images/00057478.jpg\"> Mùa giải trước tại J2 League, Kazu Miura ra sân 5 lần cho Yokohama FC với tổng cộng 233 phút thi đấu. Cho dù không ghi được bàn thắng hay kiến tạo nào ở mùa giải vừa qua, nhưng Kazu Miura vẫn nhận được sự quan tâm từ nhiều CLB của J.League.\n\nTại Yokohama FC lúc này, Kazu Miura còn nhiều tuổi hơn HLV trưởng Takahiro Shimotaira, người năm nay 48 tuổi.\n\nTrở lại trận đấu giữa Yokohama FC và Sagan Tosu, Miura chơi hơn 60 phút và gây ấn tượng khi sẵn sàng tranh chấp, làm tường cho các đồng đội trong vai trò trung phong. Lão tướng sinh năm 1967 còn thực hiện vài pha dứt điểm về cầu môn đối phương, cho thấy mình vẫn đủ khả năng chơi bóng chuyên nghiệp.  ',0,'huyen-thoai-53-tuoi-di-vao-lich-su-bong-da-the-gioi',NULL,1,75,231,'<img src=\"Assets/images/00057478.jpg\">'),(152,4,'HLV Zidane lập kỷ lục buồn trong ngày đối đầu Guardiola','Zinedine Zidane lần đầu tiên nếm trái đắng tại các trận đấu loại trực tiếp Champions League trong vai trò huấn luyện viên.','Rạng sáng 8/8 (giờ Hà Nội), Real Madrid nhận trận thua 1-2 trước Man City trong trận lượt về vòng 1/8 Champions League, qua đó dừng bước với tổng tỷ số 2-4.\n\nTheo Opta, chuỗi 12 chiến thắng của Zidane cùng học trò tại các trận đấu loại trực tiếp Champions League bị chặn đứng bởi thầy trò HLV Guardiola. Ngoài ra, Zidane kém duyên trong các cuộc đối đầu với đội bóng Anh tại Champions League khi chỉ thắng 2 trong tổng số 7 trận. <img src=\"Assets/images/2020_08_07T205744Z_1229198105_UP1EG871M88WI_RTRMADP_3_SOCCER_CHAMPIONS_MCI_MAD_REPORT.jpg\"> Trong khi đó, Pep Guardiola trở thành nhà cầm quân thứ 3 có nhiều hơn một lần khiến Real Madrid ôm hận tại các vòng loại trực tiếp của Champions League. Marcello Lippi (mùa 1995/96, 2002/03) và Ottmar Hitzfeld (mùa 2000/01, 2006/07) là những người làm được điều này trước đó.\n\nThất bại của Real trận này phần lớn đến từ sai lầm hàng thủ. Varane có ngày thi đấu tệ hại khi hai lần để mất bóng ngay trong vùng cấm, dẫn đến các bàn thua của nhà vô địch La Liga.\n\nSau trận, hai nhà cầm quân của Real và Man City nán lại trên sân Etihad để có cuộc nói chuyện trong không khí vui vẻ. Chia sẻ trong buổi họp báo, Guardiola tiết lộ: \"Tôi chúc mừng Zidane khi giành danh hiệu tại La Liga. Hy vọng một ngày nào đó, chúng tôi có thể ăn tối cùng nhau\".\n\nMan City giành quyền vào tứ kết và đối đầu với Lyon. Đội bóng nước Pháp vượt qua Juventus nhờ luật bàn thắng sân khách sau khi hòa nhau với tổng tỷ số 2-2.  ',0,'hlv-zidane-lap-ky-luc-buon-trong-ngay-doi-dau-guardiola',NULL,1,74,523,'<img src=\"Assets/images/2020_08_07T205744Z_1229198105_UP1EG871M88WI_RTRMADP_3_SOCCER_CHAMPIONS_MCI_MAD_REPORT.jpg\">'),(153,4,'Varane nhận lỗi sau màn trình diễn thảm họa trước Man City','Hậu vệ người Pháp khẳng định anh có trách nhiệm trong trận thua 1-2 của Real Madrid trước Man City, khiến đội bóng thành Madrid dừng bước tại Champions League.','Chia sẻ với Marca, Raphael Varane thừa nhận: \"Trận thua này là lỗi của tôi. Tôi phải chấp nhận nó. Tôi nhận trách nhiệm của mình trong trận thua này. Real Madrid đã chuẩn bị tốt nhưng phải trả giá vì sai lầm. Tôi cảm thấy đáng tiếc thay cho các đồng đội của mình\".\n\n\"Tôi thấy khá ổn trong suốt trận đấu. Có lẽ chúng tôi đã mạo hiểm quá nhiều ở đầu trận và phải trả giá đắt. Tôi đánh giá cao sự hỗ trợ từ các đồng đội. Những sai lầm thế này không thường xuyên xảy ra trong sự nghiệp của tôi. Có những sai lầm phải trả giá đắt. Cuộc sống là vậy. Tôi không có lời giải thích nào thêm\", anh cho biết. <img src=\"Assets/images/varane.jpg\"> Varane được HLV Zinedine Zidane xếp đá chính ngay từ đầu, bên cạnh Eder Militao, trong ngày thủ quân Sergio Ramos vắng mặt vì án treo giò. Hậu vệ người Pháp mắc sai lầm trong cả hai bàn thua của Real Madrid.\n\nAnh có pha xử lý lóng ngóng và để mất bóng vào chân Gabriel Jesus ở phút thứ 9. Tiền đạo người Brazil chuyền bóng cho Raheem Sterling dứt điểm đơn giản ghi bàn mở tỷ số cho Man City. Sang hiệp hai, Varane tiếp tục có tình huống sai lầm khi đánh đầu về khung thành của thủ môn Thibaut Courtois. Tuy nhiên, pha bóng đi thiếu lực, tạo điều kiện để Jesus chớp thời cơ ấn định chiến thắng 2-1 cho đội nhà.\n\n\"Bạn không cần phải nói nhiều. Mọi người đều biết tôi rất buồn và tôi phải chấp nhận điều đó. Bạn phải có bản lĩnh để trở lại mạnh mẽ hơn. Tôi đã suy nghĩ về điều này. Đêm nay sẽ là khoảng thời gian khó khăn. Real Madrid đã thua, còn tôi thất bại. Rõ ràng tôi không vui chút nào\", Varane chia sẻ.\n\nNói về sai lầm của cậu học trò, HLV Zidane khẳng định: \"Tôi không nói với cậu ấy bất cứ điều gì. Quan trọng nhất là phải đứng dậy sau vấp ngã. 95% những gì các cầu thủ đã làm là rất ngoạn mục. Đó là những gì tôi nói với họ. Rõ ràng, Real Madrid không thể vui vì thất bại. Song, toàn đội phải tự hào vì những gì làm được trong cả mùa giải\". <img src=\"Assets/images/ucl.jpg\">  ',0,'varane-nhan-loi-sau-man-trinh-dien-tham-hoa-truoc-man-city',NULL,1,65,123,'<img src=\"Assets/images/ucl.jpg\">'),(154,5,'Kanye West khó cứu vãn hôn nhân với Kim Kardashian','Hàn gắn tình cảm chưa được lâu, Kim Kardashian - Kanye West lại mâu thuẫn khiến hôn nhân tiếp tục đứng trên bờ vực đổ vỡ.','Theo The Sun, vợ chồng Kanye West đang du lịch ở Cộng hòa Dominica với hy vọng cứu vãn cuộc hôn nhân của họ. Tuy nhiên, bộ đôi tiếp tục bất đồng quan điểm.\n\nNguồn tin nói Kim \"siêu vòng ba\" ủng hộ chồng tuyệt đối nếu West tranh cử tổng thống Mỹ vào năm 2024. Trong khi đó, rapper tài năng quyết tâm thực hiện ý định vào năm nay. <img src=\"Assets/images/kim_kardashian_kanye_west.jpg\"> \"Kim muốn chồng nhận thêm sự giúp đỡ và lắng nghe từ những chính trị gia trước khi tranh cử 4 năm nữa. Song, thay vì thỏa hiệp với vợ, Kanye vẫn đi con đường riêng. Anh ấy khẳng định mọi chuyện diễn ra đều là kế hoạch của Chúa\", nguồn tin cho biết.\n\nTinh thần của West được tiết lộ không ổn định. Anh căng thẳng chuyện tranh cử lẫn hôn nhân đang rơi vào ngõ cụt với Kardashian. \"Bạn bè của họ sẽ không ngạc nhiên hoặc đổ lỗi cho Kim nếu cô ấy lặng lẽ kết thúc hôn nhân vào Giáng sinh\".\n\n\"Kim nghĩ thời gian du lịch cùng gia đình sẽ nhắc nhở Kanye về tầm quan trọng của một người cha. Nhưng thay vào đó, nam rapper vẫn chú tâm việc chạy đua vào Nhà Trắng\", nguồn tin nói thêm. <img src=\"Assets/images/kim11_4277_1595907173.jpg\"> Mâu thuẫn giữa West - Kardashian xảy ra sau khi rapper khóc lóc kể chuyện anh từng muốn vợ phá thai. Khi được gia đình khuyên đi điều trị chứng rối loạn lưỡng cực, anh tức giận và cho rằng nhà Kardashian cố đẩy anh vào viện tâm thần.\n\nTừ lúc ấy, West chuyển đến sống tại điền trang vùng Wyoming. Đến ngày 27/7, Kardashian xuất hiện ở Wyoming, trong một chiếc xe hơi cùng Kanye West. Cặp vợ chồng nói chuyện khá lâu và kết thúc khi chị gái Kylie bật khóc nức nở.\n\n\"Trước chuyến đi, Kim đã chuẩn bị sẵn tâm lý để ly hôn chồng. Khi đến nơi, Kanye đã bày tỏ thái độ lắng nghe, tôn trọng Kim khiến cô thay đổi ý định. Sau khi rời Cody, cô ấy và Kanye đã trò chuyện nhiều hơn”, nguồn tin thân cận kể thời điểm đó.\n\nTrên Twitter, bà mẹ 4 con hy vọng dư luận đừng đào sâu chuyện gia đình cô. Những gì ngôi sao truyền hình thực tế muốn là giúp chồng chữa bệnh và mong được công chúng thông cảm.  ',0,'kanye-west-kho-cuu-van-hon-nhan-voi-kim-kardashian',NULL,1,2,1215,'<img src=\"Assets/images/kim11_4277_1595907173.jpg\">'),(155,5,'Trần Tư Thành muốn hàn gắn với Đồng Lệ Á','Trần Tư Thành và Đồng Lệ Á nhiều lần bị đồn thổi hôn nhân trục trặc. Hôm 8/8, nam diễn viên ngọt ngào chúc mừng sinh nhật bà xã, chứng minh cả hai vẫn mặn nồng.','Sina đưa tin 8/8 là sinh nhật tuổi 37 của Đồng Lệ Á. Vẫn như mọi năm, Trần Tư Thành là người đầu tiên đăng bài chúc mừng sinh nhật nữ diễn viên Cung tỏa tâm ngọc. “Mẹ Đóa Đóa sinh nhật vui vẻ nhé”, nam diễn viên viết.\n\nNgay sau đó, Đồng Lệ Á đáp lại lời chúc của ông xã với một biểu tượng đáng yêu. Được biết, đã 8 năm liên tiếp Trần Tư Thành “đúng hẹn lại lên” chúc mừng sinh nhật Đồng Lệ Á. Sina tiết lộ sau những rắc rối đời tư, Trần Tư Thành đang cố gắng lấy lòng vợ. Đạo diễn họ Trần không hề muốn đối diện với khả năng ly hôn. <img src=\"Assets/images/4af9831bly1ghip97uzxxj22802801kx.jpg\"> Đồng Lệ Á là mỹ nữ Tân Cương nổi tiếng với nhan sắc xinh đẹp. Cô được biết đến nhờ vai diễn Niên Tố Ngôn trong Cung tỏa tâm ngọc (2011). Còn Trần Tư Thành nổi tiếng qua vai diễn La Hải Đào trong Xuân phong trầm tuý đích dạ vãn (2009).\n\nTrần Tư Thành và Đồng Lệ Á nảy sinh tình cảm sau khi cùng hợp tác trong Chuyện tình Bắc Kinh. Cả hai về chung một nhà vào tháng 1/2014 và đón con trai đầu lòng vào tháng 1/2016 sau 2 năm kết hôn.\n\nTuy nhiên, cuộc sống hôn nhân của người đẹp lại không mấy êm ả. Sau khi kết hôn, Trần Tư Thành vẫn không bỏ được thói trăng hoa. Nam diễn viên vướng tin đồn ngoại tình với hơn 10 người phụ nữ khác nhau. Việc Đồng Lệ Á liên tục tha thứ cho Trần Tư Thành khiến cô bị người hâm mộ quay lưng.\n\nBáo chí Trung Quốc không ít lần đưa tin hôn nhân của Trần Tư Thành và Đồng Lệ Á đã đổ vỡ. Ngày 23/4, nhiều nguồn tin cho hay Đồng Lệ Á đã chủ động nói lời chia tay sau quá nhiều bê bối đời tư của chồng. Ngay sau đó, ngôi sao Chuyện tình Bắc Kinh đã lập tức lên tiếng phủ nhận chuyện bị bà xã bỏ rơi.  ',0,'tran-tu-thanh-muon-han-gan-voi-dong-le-a',NULL,1,123,5422,'<img src=\"Assets/images/4af9831bly1ghip97uzxxj22802801kx.jpg\">'),(156,6,'Triệu tập 16 người liên quan vụ Phú Lê đánh 2 phụ nữ','Cục Cảnh sát hình sự vừa triệu tập thêm 16 người được xác định liên quan vụ Phú Lê cử đàn em hành hung 2 phụ nữ lớn tuổi.','Chiều 8/8, Cục Cảnh sát hình sự Bộ Công an cho biết cơ quan chức năng vừa triệu tập thêm 16 người trong vụ 2 phụ nữ lớn tuổi bị hành hung mà Lê Văn Phú (tức Phú Lê, 40 tuổi, ở Hà Nội) và vợ là Lã Thúy Kiều (35 tuổi, quê Hà Nam) đã thừa nhận có liên quan.\n\nVợ chồng Phú Lê và 16 người trên bị điều tra về hành vi cố ý gây thương tích và gây rối trật tự công cộng. <img src=\"Assets/images/d03a9738a3c85f9606d9.jpg\"> Ngày 5/8, Lê Văn Phú và vợ bị bắt để điều tra về cáo buộc hành hung 2 phụ nữ ở huyện Đan Phượng, Hà Nội. Chị Trần Thị Đào (còn gọi là Đào Chi Lê, người đứng đơn tố cáo) nói quen vợ chồng Phú Lê quay phim hài Tết 2017. Sau đó, vợ Phú mời Đào về hợp tác quảng cáo thực phẩm chức năng.\n\nMột thời gian sau, Đào đề nghị ngừng làm việc chung thì bị vợ chồng Phú nói xấu. Ngày 18/7, Đào lên mạng xã hội kể lại việc chấm dứt làm ăn với Kiều. Tối cùng ngày, Kiều kéo đàn em đến đe dọa chị Đào và treo thưởng 20 triệu cho ai tìm thấy người phụ nữ này.\n\nTrong đơn trình bày, Đào Chi Lê tố ngày 3/8, nhóm thanh niên lạ mặt liên quan vợ chồng Phú đã tìm về Đan Phượng. Tại đây, họ dùng hung khí đánh đập mẹ và dì ruột của chị Đào khiến 2 nạn nhân phải nhập viện.\n\nMột cán bộ điều tra Cục Cảnh sát hình sự Bộ Công an cho biết vợ chồng Phú Lê đã thừa nhận có liên quan đến đơn tố cáo trên.\n\n\"Giang hồ mạng\" Phú Lê thường xuất hiện trong số sự kiện về âm nhạc, đóng MV ca nhạc hay phim ngắn về chủ đề giang hồ. Một số MV ca nhạc do Phú Lê hát và diễn xuất thu hút hàng triệu lượt xem trên YouTube.\n\nNgoài ra, Phú Lê còn tham gia đóng phim. Người đàn ông này xăm trổ, thường xuyên đeo dây chuyền màu vàng gắn huy hiệu đại bàng, tay đeo nhẫn hình rồng.\n\nCòn Lã Thúy Kiều thường livestream để quảng cáo các sản phẩm có tác dụng như thuốc chữa bệnh. Bà Kiều từng bị cơ quan chức năng ở Hà Nội xử phạt hành chính do bán hàng không rõ nguồn gốc.  ',0,'trieu-tap-16-nguoi-lien-quan-vu-phu-le-danh-2-phu-nu',NULL,1,3,4134,'<img src=\"Assets/images/d03a9738a3c85f9606d9.jpg\">'),(157,6,'Công an xác định tiến sĩ Bùi Quang Tín tự rơi từ tầng 14','Cơ quan cảnh sát điều tra nhận định việc tiến sĩ Bùi Quang Tín tử vong không có dấu hiệu tội phạm nên không khởi tố vụ án.','Ngày 8/8, nguồn tin của Zing cho biết Công an TP.HCM đã ra quyết định không khởi tố vụ án hình sự vụ tiến sĩ Bùi Quang Tín tử vong tại chung cư New Sài Gòn ở huyện Nhà Bè. Cảnh sát đưa ra khi ra quyết định trên vì không phát hiện dấu hiệu tội phạm.\n\nSau 2 tháng gia hạn điều tra, cơ quan chức năng xác định tiến sĩ Bùi Quang Tín tử vong do tự rơi từ tầng 14 chung cư New Sài Gòn. \"Nếu gia đình tiếp tục có kiến nghị, cơ quan chức năng vẫn sẽ tiến hành giải quyết trình báo theo đúng trình tự, quy định\", nguồn tin cho hay. <img src=\"Assets/images/tin.jpg\"> Trưa 5/4, ông Trần Việt Dũng (Viện trưởng Viện Đào tạo quốc tế Đại học Ngân hàng TP.HCM) mời ông Tín và một số đồng nghiệp đến nhà ăn cơm. Khoảng 16h, nhiều người ra về. Ông Tín và ông Trung (Phó hiệu trưởng Đại học Ngân hàng TP.HCM) ở lại cùng chủ nhà.\n\nĐến 17h, ông Dũng có hẹn với bạn nên ra ngoài. Ông Tín và ông Trung ở lại trò chuyện. 20 phút sau, ông Dũng nhận được cuộc gọi từ ông Trung nên quay lại và phát hiện ông Tín tử vong.\n\nÔng Trung khi đó khai thời điểm ông Dũng ra khỏi nhà, ông và ông Tín nằm trên ghế trò chuyện. Một lúc sau, ông Tín đứng dậy nói ra về. Lúc này, ông Trung khuyên nạn nhân nằm nghỉ để gọi người nhà đến đón nhưng ông Tín nói tự về được. <img src=\"Assets/images/Bui_Tin_1.jpg\"> Ông Trung sau đó nghe tiếng động ở bên ngoài nên chạy ra xem. Tuy nhiên, ông này tường trình đã uống rượu bia và bị cận nên không nhìn thấy phía dưới. Linh cảm chuyện không lành, ông Trung đã gọi ông Dũng quay về.\n\nCơ quan công an khám nghiệm pháp y và xác định nạn nhân tử vong do ngã từ trên cao. Tuy nhiên, người thân nạn nhân cho rằng cái chết của ông Tín có nhiều uẩn khúc.\n\nVợ ông Tín còn nghi đây là vụ án mạng. Người vợ cho hay đầu năm 2019, ông Tín được bổ nhiệm phụ trách phòng truyền thông của 1 trường đại học. Từ đó, ông chịu sức ép từ nhiều phía.\n\nNgười thân cũng chia sẻ ông Tín nhiều lần bị nhắn tin hăm dọa, ép phải từ chức. Đến trước Tết Canh Tý 2020, ông xin từ chức nhưng vẫn làm giảng viên.  ',0,'cong-an-xac-dinh-tien-si-bui-quang-tin-tu-rơi-tu-tang-14',NULL,1,23,531,'<img src=\"Assets/images/Bui_Tin_1.jpg\">'),(158,6,'Đâm chết người khi đòi món nợ 700.000 đồng','Mâu thuẫn khi đòi tiền công sơn nhà, Huy cầm bóng đèn tuýp đâm chết người đàn ông 30 tuổi.','Ngày 8/8, Công an TP Đà Lạt, tỉnh Lâm Đồng tạm giữ hình sự Trần Quốc Huy (35 tuổi, người địa phương) để điều tra về hành vi giết người. <img src=\"Assets/images/d11.jpg\"> Chiều 7/8, Huy đến gặp anh Trần Đăng Hoàng (30 tuổi, cùng ở TP Đà Lạt) để đòi 700.000 đồng là tiền công sơn nhà.\n\nTại đây, họ xảy ra mâu thuẫn. Sau hồi cãi vã, Huy cầm bóng đèn tuýp tấn công anh Hoàng.\n\nNạn nhân được đưa đến bệnh viện cấp cứu nhưng không qua khỏi.  ',0,'dam-chet-nguoi-khi-doi-mon-no-700.000-dong',NULL,1,65,5421,'<img src=\"Assets/images/d11.jpg\">'),(159,6,'Bắt kẻ cầm đầu vụ giết người ở Quy Nhơn','Sau 3 tháng truy lùng, cảnh sát đã bắt kẻ trốn truy nã đặc biệt nguy hiểm cầm đầu vụ án mạng ở TP Quy Nhơn.','Ngày 6/8, Công an Bình Định bắt Huỳnh Phương Đông (thường gọi Cu Đỏ, 33 tuổi) theo quyết định truy nã về tội Giết người.\n\nTối 15/5, do mâu thuẫn trong việc mua bán cỏ Mỹ, Đông cùng đồng phạm sử dụng hung khí đánh nhau với nhóm Huỳnh Văn Hậu tại TP Quy Nhơn.\n\nCảnh sát xác định nhóm của Đông đã chém anh L.Đ.Đ. (27 tuổi, người địa phương) tử vong. <img src=\"Assets/images/Anh_toi_pham_den.jpg\"> Qua điều tra, Công an tỉnh Bình Định đã khởi tố vụ án giết người, gây rối trật tự công cộng. 12 người thuộc nhóm của Đông bị khởi tố về tội Giết người, 7 thanh niên khác bị khởi tố tội Gây rối trật tự công cộng. Ngoài ra, cơ quan điều tra đã truy nã 7 bị can về tội Giết người.\n\nĐến nay, cảnh sát đã truy bắt, vận động đầu thú 6/7 bị can bị truy nã. Người còn lại đang bỏ trốn là Nguyễn Văn Thanh Phong (thường gọi Phong Đú, ngụ TP Quy Nhơn).  ',0,'bat-ke-cam-dau-vu-giet-nguoi-o-quy-nhơn',NULL,1,76,522,'<img src=\"Assets/images/Anh_toi_pham_den.jpg\">'),(160,6,'Bắt chủ quán karaoke cùng khách ‘bay lắc’','Chủ quán karaoke cùng nhóm khách đang ','Ngày 8/8, Công an TP Long Xuyên, tỉnh An Giang, lập hồ sơ xử lý 18 người có hành vi sử dụng trái phép chất ma túy.\n\nKhuya 7/8, cảnh sát kiểm tra hành chính quán karaoke trên đường Lê Văn Nhung, phường Mỹ Bình, TP Long Xuyên. <img src=\"Assets/images/MT_1_.jpg\"> Cảnh sát phát hiện bên trong các phòng 301, 401 và 403 có 12 khách trong độ tuổi từ 16 đến 37 nghi vấn sử dụng trái phép chất ma túy. Cảnh sát thu giữ nhiều viên nén màu hồng và bọc nylon chứa tinh thể, chất bột màu trắng.\n\nQua kiểm tra, 12 khách cùng 6 người của quán dương tính với chất ma túy. Trong 18 người này, cảnh sát phát hiện có Huỳnh Chí Nhân (36 tuổi, chủ quán) dương tính với ma túy.\n\nTại trụ sở cảnh sát, những người này thừa nhận tổ sử dụng trái phép chất ma túy thì bị cảnh sát bắt quả tang.  ',0,'bat-chu-quan-karaoke-cung-khach-bay-lac',NULL,1,43,523,'<img src=\"Assets/images/MT_1_.jpg\">'),(161,7,'Một cô giáo liên quan bệnh nhân Covid-19, Quảng Ngãi dừng thi 382 em','Một cô giáo của trường THPT Sơn Mỹ (thành phố Quảng Ngãi) được xác định là F1 khiến 382 học sinh của trường này phải dừng thi, chuyển sang đợt hai.','Thông tin trên do ông Đỗ Văn Phu, Giám đốc Sở GD&amp;ĐT Quảng Ngãi, trao đổi với Zing chiều 8/8.\n\nCụ thể, ông Phu cho biết chồng của nữ giáo viên trường THPT Sơn Mỹ là bệnh nhân số 786. Ông này là giáo viên Trung tâm GDTX huyện Sơn Tịnh, từng tiếp xúc đồng nghiệp tại trường THPT Sơn Mỹ, trước khi Bộ Y tế công bố nhiễm virus SARS-CoV-2.\n\nBệnh nhân này cũng có vợ là giáo viên trường THPT Sơn Mỹ. Vì vậy, tất cả thí sinh của trường này buộc phải dừng thi để đảm bảo an toàn. <img src=\"Assets/images/Cach_ly_khu_dan_cu.jpg\"> Thông tin trên do ông Đỗ Văn Phu, Giám đốc Sở GD&amp;ĐT Quảng Ngãi, trao đổi với Zing chiều 8/8.\n\nCụ thể, ông Phu cho biết chồng của nữ giáo viên trường THPT Sơn Mỹ là bệnh nhân số 786. Ông này là giáo viên Trung tâm GDTX huyện Sơn Tịnh, từng tiếp xúc đồng nghiệp tại trường THPT Sơn Mỹ, trước khi Bộ Y tế công bố nhiễm virus SARS-CoV-2.\n\nBệnh nhân này cũng có vợ là giáo viên trường THPT Sơn Mỹ. Vì vậy, tất cả thí sinh của trường này buộc phải dừng thi để đảm bảo an toàn. <img src=\"Assets/images/lich_thi_thpt_2020.jpg\"> \"Ban đầu, Ban chỉ đạo kỳ thi của tỉnh xác định một số thí sinh của trường này có tiếp xúc cô giáo thuộc diện F1, dự định cho các em thi riêng. Sau khi tìm hiểu kỹ, cô giáo này đã tiếp xúc nhiều thí sinh, giáo viên khác của trường. Để đảm bảo an toàn tuyệt đối, Ban chỉ đạo kỳ thi quyết định tạm dừng thi tốt nghiệp đối với điểm thi này\", ông Phu nói.\n\nNgoài điểm thi THPT Sơn Mỹ, Quảng Ngãi có khoảng 30 thí sinh được xác định là F1, F2. Như vậy, 382 thí sinh của Quảng Ngãi sẽ dự thi tốt nghiệp THPT đợt 2 cùng học sinh Đà Nẵng, thành phố Buôn Mê Thuột và một số địa phương của tỉnh Quảng Nam.\n\nChiều 8/8, thí sinh trên cả nước đã đến các điểm trường làm thủ tục dự thi. Sáng mai, các em sẽ bước vào môn thi đầu tiên Ngữ văn.  ',0,'mot-co-giao-lien-quan-benh-nhan-covid-19-quang-ngai-dung-thi-382-em',NULL,1,76,531,'<img src=\"Assets/images/lich_thi_thpt_2020.jpg\">'),(162,7,'Học sinh TP.HCM đội mưa làm thủ tục thi tốt nghiệp THPT','Chiều 8/8, hơn 70.000 thí sinh dự thi tốt nghiệp THPT ở TP.HCM có mặt tại 115 điểm thi để làm thủ tục. Cơn mưa bất chợt lúc 14h30 khiến nhiều em phải chạy tìm chỗ trú.','<img src=\"Assets/images/zz1.jpg\"> THPT Trưng Vương là một trong 115 điểm thi tại TP.HCM. 15h ngày 8/8, sau khi làm thủ tục, học sinh đứng trú dưới hiên nhà vì trời bắt đầu đổ mưa. <img src=\"Assets/images/zz2.jpg\"> Trước đó, thí sinh được phổ biến quy chế, kiểm tra các thông tin trên thẻ dự thi. Năm nay, TP.HCM có 3.164 phòng thi. Tổng số thí sinh tham dự là 74.718 em, trong đó, 69.131 học sinh giáo dục THPT và 5.587 học sinh hệ giáo dục thường xuyên. <img src=\"Assets/images/zz3.jpg\">THPT Trưng Vương là một trong 115 điểm thi tại TP.HCM. 15h ngày 8/8, sau khi làm thủ tục, học sinh đứng trú dưới hiên nhà vì trời bắt đầu đổ mưa. C:fakepath3 (2).jpg Trước đó, thí sinh được phổ biến quy chế, kiểm tra các thông tin trên thẻ dự thi. Năm nay, TP.HCM có 3.164 phòng thi. Tổng số thí sinh tham dự là 74.718 em, trong đó, 69.131 học sinh giáo dục THPT và 5.587 học sinh hệ giáo dục thường xuyên. <img src=\"Assets/images/DSC_4767_zing.jpg\"> Các điểm thi đều bố trí máy đo thân nhiệt, nước rửa tay ngay từ cửa ra vào cho thí sinh và giám thị. <img src=\"Assets/images/DSC_4884_zing.jpg\"> Thầy Nguyễn Hữu Trí, Trưởng điểm thi THPT chuyên Trần Đại Nghĩa, cho biết nhà trường bố trí 28 phòng thi. Ngoài ra, 4 phòng riêng được chuẩn bị cho những thầy, cô hoặc học sinh có diễn biến bất thường về sức khỏe. <img src=\"Assets/images/DSC_5005_zing.jpg\"> Hoàng Vy và Tường Vy (trường Quốc tế Á Châu) vừa làm thủ tục dự thi xong thì trời mưa. Hai bạn cho biết tinh thần khá thoải mái trước kỳ thi sắp tới. \"Chỉ mong kỳ thi diễn ra thuận lợi, không phải hoãn thêm lần nào nữa\", Tường Vy chia sẻ. <img src=\"Assets/images/3_zing.jpg\"> Quỳnh Anh cùng bạn ngồi đợi mẹ đón trong lúc trời mưa. Nữ sinh cho biết: \"Nếu phải đeo khẩu trang trong lúc làm bài khoảng 3 giờ, em nghĩ sẽ rất khó thở. Khi biết thông tin có thể cởi khẩu trang lúc thi, em thấy rất thoải mái. Hy vọng mai em sẽ làm bài tốt\". <img src=\"Assets/images/2_zing.jpg\"> Thuỳ Trang lại khá hồi hộp, dù hôm nay mới làm thủ tục thi. Nhà cách điểm thi 2 km nên Trang không quá lo lắng về nguy cơ đường ngập khi di chuyển. <img src=\"Assets/images/DSC_4968_zing.jpg\"> Phía ngoài cổng trường THPT chuyên Trần Đại Nghĩa, nhiều phụ huynh đội mưa đứng chờ thí sinh làm thủ tục dự thi. <img src=\"Assets/images/DSC_4982_2.jpg\"> Cơn mưa bất chợt khiến các thí sinh tại trường THPT chuyên Trần Đại Nghĩa vội vàng chạy tìm chỗ trú ẩn. <img src=\"Assets/images/5_zing.jpg\"> Hầu hết phụ huynh đã chuẩn bị sẵn áo mưa để đảm bảo sức khoẻ cho con em mình. <img src=\"Assets/images/DSC_4986_zing.jpg\"> Ngày mai, 9/8, kỳ thi tốt nghiệp THPT 2020 diễn ra trên cả nước (trừ Đà Nẵng, một số địa phương của Quảng Nam và thành phố Buôn Ma Thuột của Đắk Lắk thi đợt hai).  ',0,'hoc-sinh-tp.hcm-doi-mua-lam-thu-tuc-thi-tot-nghiep-thpt',NULL,1,4,125,'<img src=\"Assets/images/DSC_4986_zing.jpg\">'),(163,7,'Thí sinh đeo khẩu trang, đo thân nhiệt làm thủ tục thi tốt nghiệp THPT','Chiều 8/8, thí sinh cả nước (trừ các địa phương giãn cách xã hội) làm thủ tục dự thi tốt nghiệp. Lãnh đạo ngành giáo dục các địa phương cho biết công tác chuẩn bị đã hoàn tất.','Kỳ thi năm nay, TP.HCM có 115 điểm thi với 3.164 phòng thi. Tổng số thí sinh tham dự kỳ thi là 74.718 em, trong đó, 69.131 học sinh giáo dục THPT và 5.587 học sinh hệ giáo dục thường xuyên.\n\nTại TP.HCM, ông Nguyễn Văn Hiếu, Phó giám đốc Sở GD&amp;ĐT, cho hay 115 điểm thi đã hoàn tất việc bố trí giáo viên coi thi. Khẩu trang, máy đo thân nhiệt, nước rửa tay, phòng thi dự phòng, phòng chờ đã được chuẩn bị đầy đủ. <img src=\"Assets/images/lam_thu_tuc_1_zing.jpg\"> Tại điểm thi THPT Lê Quý Đôn (quận 3, TP.HCM), hội đồng thi đã chuẩn bị phòng cách ly trong trường hợp có thí sinh sốt cao và 2 phòng thi dự phòng.\n\nCó mặt làm thủ tục tại điểm thi này, hai bạn Đoàn Ngọc Anh Thư, Nguyễn Khánh Phương, trường THPT Lê Thị Hồng Gấm, cho biết các em đã ôn tập rất kỹ và khá tự tin vào môn Toán.\n\n\"Trong kỳ thi, chúng em sẽ cố gắng và cần có thêm chút may mắn để hoàn thành tốt bài thi”, Anh Thư chia sẻ.\n\nBà Huỳnh Lệ Giang, Giám đốc Sở GD&amp;ĐT Đồng Nai, cho biết địa phương này có 6 thí sinh thuộc diện F1, F2 không được dự thi trong đợt này.\n\nNgoài ra, công tác chuẩn bị cho kỳ thi như phun khử khuẩn, nước rửa tay, khẩu trang, phòng cách ly, phòng thi dự phòng ở mỗi điểm thi, đều đã sẵn sàng. <img src=\"Assets/images/lam_thu_tuc_2_zing.jpg\"> Trong khi đó, tại Lạng Sơn, Theo Ban chỉ đạo cấp tỉnh kỳ thi tốt nghiệp THPT năm 2020, điểm thi huyện Đình Lập có 247 thí sinh, đã được xếp lịch thi đợt một. Tuy nhiên, địa phương này vừa có 4 trường hợp mắc Covid-19. Điểm thi THPT Đình Lập sẽ lùi thời gian tổ chức thi vào đợt 2 với 12 phòng thi.\n\nTại Thừa Thiên - Huế, ông Nguyễn Tân, Giám đốc Sở GD&amp;ĐT, thông tin đến chiều 8/8, địa phương này chưa ghi nhận trường hợp F1, F2 là thí sinh hoặc giáo viên coi thi.\n\nSở yêu cầu các trường phổ thông huy động máy đo thân nhiệt, đảm bảo mỗi phòng thi có một máy đo và thông báo cho thí sinh đến sớm hơn kế hoạch 30 phút để kiểm tra thân nhiệt.\n\nCông tác lưu trú, sinh hoạt, ăn uống đối với giáo viên, nhân viên làm nhiệm vụ tại các điểm coi thi tập trung cũng được lưu ý, yêu cầu thực hiện biện pháp đảm bảo an toàn.\n\nChiều cùng ngày, hơn 6.000 thí sinh tại Đắk Nông đến 13 điểm thi để làm thủ tục. Thời gian qua, tỉnh này có dịch bạch hầu. Ngay từ cổng vào các điểm thi, cán bộ y tế đã tiến hành đo thân nhiệt thí sinh đến làm thủ tục, phòng, chống dịch Covid-19 và bạch hầu.\n\nTại điểm thi huyện biên giới Mường Lát, tỉnh Thanh Hóa, chiều nay, 365 thí sinh làm thủ tục dự thi. Qua kiểm tra thân nhiệt, một thí sinh có biểu hiện sốt nên được nhân viên y tế kiểm tra, đưa vào phòng riêng theo dõi.\n\nCuối giờ chiều, ông Lê Văn Tùng, Phó điểm thi trường THPT Mường Lát, cho biết sau khi kiểm tra lại, nam sinh này có sức khỏe bình thường.\n\n\"Có lẽ do thời tiết nóng, em ấy chạy vội đến điểm thi nên bị mệt”, ông Tùng nói.\n\nNăm nay, điểm thi này có 12 phòng thi chính thức và 2 phòng đặc biệt dành cho trường hợp có biểu hiện ho, sốt.\n\nNgày mai, 9/8, kỳ thi tốt nghiệp THPT 2020 diễn ra trên cả nước (trừ Đà Nẵng, một số địa phương của Quảng Nam và thành phố Buôn Ma Thuột của Đắk Lắk thi đợt hai).\n\nHơn 900.000 thí sinh sẽ thi để xét tốt nghiệp và là cơ sở tuyển sinh đại học, cao đẳng.  ',0,'thi-sinh-deo-khau-trang-do-than-nhiet-lam-thu-tuc-thi-tot-nghiep-thpt',NULL,1,76,513,'<img src=\"Assets/images/lam_thu_tuc_2_zing.jpg\">'),(164,8,'Otto tặng 10.000 suất bánh ăn sáng hỗ trợ tuyến đầu chống dịch','Những phần ăn sáng giàu dinh dưỡng góp phần tiếp thêm sức mạnh cho đội ngũ tuyến đầu chống dịch tại Đà Nẵng.','Thông tin về tình hình dịch bệnh tại tâm điểm Đà Nẵng được người dân cả nước quan tâm. Hưởng ứng lời kêu gọi góp sức hướng về vùng tâm dịch Đà Nẵng, nhiều cá nhân, tổ chức quyên góp phần quà thiết thực nhằm chia sẻ khó khăn, thúc đẩy tinh thần lạc quan của các “chiến sĩ” tuyến đầu chống dịch. Với tinh thần tương thân tương ái, nhãn hàng bánh ăn sáng Otto sợi thịt gà thuộc Công ty TNHH Chế biến thực phẩm và Bánh kẹo Phạm Nguyên đã trao 10.000 phần bánh ăn sáng dinh dưỡng đến y bác sĩ. <img src=\"Assets/images/image001.jpg\"> Trong giai đoạn này, dinh dưỡng đóng vai trò quan trọng với y bác sĩ tuyến đầu chống dịch. Do lượng công việc dày đặc, quỹ thời gian hạn hẹp, những giải pháp dinh dưỡng nhanh gọn được ưu tiên.\n\nĐại diện Otto chia sẻ: “Trước tình hình dịch bệnh diễn biến phức tạp, chúng tôi muốn đóng góp một phần khả năng để giúp đỡ đội ngũ tuyến đầu phòng dịch. Với những phần bánh ăn sáng giàu dưỡng chất và nhanh gọn của nhãn hàng Otto, chúng tôi hy vọng các y bác sĩ có thể nhanh chóng nạp năng lượng, dinh dưỡng cho cơ thể, tiết kiệm thời gian để hoàn thành tốt công việc”. <img src=\"Assets/images/image002.jpg\"> Vị đại diện cũng bày tỏ sự tự hào trước hy sinh của cán bộ tuyền đầu. Họ không ngại khó khăn, ngày đêm túc trực nhằm kiểm soát tình hình dịch bệnh một cách tốt nhất. Những món quà thiết thực nhãn hàng Otto mang đến có thể giảm bớt phần nào gánh nặng thành phố Đà Nẵng đang gánh chịu. <img src=\"Assets/images/image003.png\"> Cuộc chiến với dịch Covid-19 khó dự đoán trước, nhưng những đóng góp, sẻ chia sẽ góp phần kiểm soát tình hình dịch. “Chúng ta sẽ sớm vượt qua được làn sóng bùng phát dịch thứ hai này”, đại diện nhãn hàng Otto bày tỏ hy vọng.  ',0,'otto-tang-10.000-suat-banh-an-sang-ho-tro-tuyen-dau-chong-dich',NULL,1,312,432,'<img src=\"Assets/images/image003.png\">'),(165,8,'Bộ Y tế hỗ trợ toàn bộ xét nghiệm rRT-PCR tại Hà Nội','Bộ Y tế đảm bảo đủ sinh phẩm phục vụ công tác xét nghiệm Covid-19 tại Hà Nội. Trung ương đặt niềm tin lớn vào Hà Nội về kiểm soát dịch bệnh.','Đó là nhấn mạnh của quyền Bộ trưởng Y tế Nguyễn Thanh Long tại buổi làm việc với UBND TP. Hà Nội về công tác phòng chống dịch Covid-19 trên địa bàn vào sáng nay (8/8).\n\n\"Với Hà Nội, xét nghiệm là điểm mấu chốt quan trọng trong kiểm soát dịch. Chỉ có xét nghiệm mới phát hiện sớm các trường hợp mắc bệnh, khoanh vùng, dập tắt ổ dịch\", ông Long cho hay.\n\nTheo ông Long, từ đầu đợt dịch này, Ban Chỉ đạo Quốc gia và Bộ Y tế luôn xác định Hà Nội là địa bàn có nguy cơ cao. Bởi thành phố có nhiều người (gần 100.000) trở về từ Đà Nẵng. Riêng từ 15/7 đến nay, con số thống kê là 75.000 người.\n\n\"Chúng tôi coi Đà Nẵng là vùng dịch, tâm dịch nên những người về từ Đà Nẵng đều có nguy cơ lây nhiễm nhất định\", quyền Bộ trưởng Y tế cho hay. Hà Nội phải tiến hành nhanh việc lấy mẫu, xét nghiệm rRT-PCR.\n\nBộ Y tế đã thảo luận, giao 4 đơn vị chính sẽ tiến hành xét nghiệm cho khoảng 70.000 người trở về từ Đà Nẵng trên địa bàn Hà Nội theo phương pháp rRT-PCR. Trong đó, Bệnh viện Bạch Mai đảm nhiệm 40.000 mẫu; Bệnh viện Nhi Trung ương, Bệnh viện Bệnh Nhiệt đới và Viện Vệ sinh dịch tễ Trung ương mỗi đơn vị 10.000 mẫu.\n\n\"Ngay chiều nay, Hà Nội sẽ gửi mẫu tới các cơ quan này. Bộ Y tế hỗ trợ toàn bộ cho TP. Hà Nội về kỹ thuật này\", ông Long khẳng định. <img src=\"Assets/images/xetnghiemhaloi2.jpg\"> Ngoài xét nghiệm rRT-PCR, Bộ Y tế kiến nghị Hà Nội lấy mẫu máu để xét nghiệm nhanh cho tất cả người từng ở Đà Nẵng về từ ngày 7 đến 15/7, khoảng 22.000 mẫu. Việc này cũng được trung ương đảm nhận.\n\n\"Trước mắt, việc xét nghiệm rRT-PCR phải làm thật nhanh. Nếu Hà Nội lấy mẫu trong 3 ngày, trung ương cũng đảm bảo làm xét nghiệm trong 3 ngày\", ông Long khẳng định.\n\nQuyền Bộ trưởng Y tế yêu cầu tất cả cơ sở khám chữa bệnh có ký hợp đồng với BHXH đều phải thực hiện việc lấy mẫu hoặc xét nghiệm để đảm bảo không lây nhiễm trong cơ sở y tế.\n\nHiện nay, Việt Nam có khoảng 120 cơ sở xét nghiệm trên toàn quốc. Sắp tới, dự kiến khoảng 2.000 cơ sở đủ khả năng xét nghiệm, sàng lọc bệnh nhân Covid-19.\n\nBộ Y tế cũng sẵn sàng tiếp nhận điều trị tất cả bệnh nhân cho Hà Nội. Trong giai đoạn này, Bộ Y tế giao Bệnh viện Bệnh Nhiệt đới Trung ương đảm nhận nhiệm vụ này. Nếu số lượng bệnh nhân tăng lên, Bộ Y tế sẽ có kịch bản tiếp theo để đảm bảo điều trị.\n\n\"Quan điểm là giữ bằng được an toàn cho thủ đô\", quyền Bộ trưởng Nguyễn Thanh Long nói.\n\nTheo ông Ngô Văn Quý, Phó chủ tịch UBND TP. Hà Nội, việc xét nghiệm rRT-PCR của thành phố đang tiến hành theo thứ tự ưu tiên: F1, người từ Đà Nẵng về có triệu chứng, người từng đi qua ổ dịch và các điểm theo thông báo của Bộ Y tế, người từ Đà Nẵng về chưa qua 14 ngày (kể từ ngày 28 trở về trước theo thứ tự ưu tiên) và số còn lại đi Đà Nẵng về.  ',0,'bo-y-te-ho-tro-toan-bo-xet-nghiem-rrt-pcr-tai-ha-noi',NULL,1,7,131,'<img src=\"Assets/images/xetnghiemhaloi2.jpg\">'),(166,8,'Thêm phương pháp xét nghiệm SARS-CoV-2','PGS.TS Nguyễn Viết Nhung, Giám đốc Bệnh viện Phổi Trung ương, cho hay phương pháp Gene Xpert đã được Bộ Y tế cấp phép để xét nghiệm Covid-19 bên cạnh test nhanh và rRT-PCR.','Dự kiến ngày 15/8, Bệnh viện Phổi Trung ương sẽ nhập 16.000 test từ Thụy Điển để sử dụng cho hệ thống Gene Xpert, sau đó phân bổ ưu tiên cho các điểm nóng đang có dịch để xét nghiệm Covid-19. Các địa phương khác cũng được tập huấn để thực hiện xét nghiệm này.\n\nBác sĩ Nhung cho biết bản chất của Gene Xpert cũng là xét nghiệm rRT-PCR nhưng nhanh hơn và hoạt động tự động, con người can thiệp vào rất ít, chủ yếu ở khâu lấy mẫu. Kết quả sẽ có trong vòng 35-45 phút.\n\nĐây là phương pháp sử dụng trong xét nghiệm vi khuẩn lao với độ đặc hiệu và nhạy rất cao tại Việt Nam từ năm 2012.\n\n\"Cùng với phát hiện nhanh bệnh nhân Covid-19, chúng ta có thể phát hiện ra những người mắc lao phổi, góp phần sớm chấm dứt bệnh lao trong cộng đồng\", bác sĩ Nhung chia sẻ.\n\nHiện nay, Việt Nam có khoảng 200 máy xét nghiệm Gene Xpert, trong đó, 28 phòng xét nghiệm đạt tiêu chuẩn an toàn sinh học cấp hai theo yêu cầu của nhà sản xuất. Đà Nẵng hiện có 3 máy xét nghiệm Gene Xpert, trong trường hợp cần thiết, có thể đem máy từ Hà Nội vào hỗ trợ thực hiện công tác xét nghiệm.\n\nĐể đối phó dịch Covid-19 đang bùng phát, nhiệm vụ số một là phải phát hiện kháng nguyên, tác nhân gây bệnh, tức virus SARS-CoV-2 ở những người đi về từ Đà Nẵng. Vì thế, kỹ thuật cần dùng duy nhất là rRT-PCR.\n\nMột trong những giải pháp được đưa ra hiện nay và Đà Nẵng đang triển khai là thực hiện trộn (pool) mẫu để làm xét nghiệm rRT-PCR. Cách làm này tiết kiệm chi phí và thời gian.\n\nHôm qua (7/8), tại cuộc họp giao ban với các bệnh viện, quyền Bộ trưởng Y tế Nguyễn Thanh Long đã yêu cầu Cục Y tế dự phòng khẩn trương hoàn thành hướng dẫn trộn mẫu (pool) đúng quy trình.\n\nTheo ông Long, các đơn vị cần chuẩn bị sẵn nhân lực lấy mẫu xét nghiệm. Bộ Y tế đã chỉ đạo Cục Khoa học Công nghệ yêu cầu các trường y dược trên toàn quốc phải tập huấn về cách thức lấy mẫu và phương thức dự phòng lây nhiễm. <img src=\"Assets/images/Thong_bao_khan_25.jpg\">  ',0,'them-phuơng-phap-xet-nghiem-sars-cov-2',NULL,1,6,213,'<img src=\"Assets/images/Thong_bao_khan_25.jpg\">'),(167,8,'Gộp mẫu xét nghiệm Covid-19 được tiến hành như thế nào?','Việc gộp mẫu xét nghiệm không được áp dụng với trường hợp F1 hoặc bệnh nhân Covid-19 đang điều trị.','Bộ Y tế vừa ban hành Hướng dẫn tạm thời việc gộp mẫu xét nghiệm SARS-CoV-2. Đây là phương pháp đã được nhiều nơi trên thế giới áp dụng khi ca mắc Covid-19 tăng cao, số người cần kiểm tra nhiều.\n\nMẫu bệnh phẩm sau khi thu thập riêng biệt sẽ được lập danh sách để gộp 5 mẫu thành một nhóm (nhóm mẫu gộp). Mỗi nhóm được mã hoá riêng. Sau đó, những nhóm này được xét nghiệm bằng kỹ thuật rRT-PCR.\n\nNếu nhóm có kết quả dương tính, từng mẫu bệnh phẩm trong đó sẽ được làm xét nghiệm riêng để tìm ra người mắc Covid-19. <img src=\"Assets/images/690e8fe21531e96fb020_1_.jpg\"> Quy trình này được áp dụng trong thời gian có dịch Covid-19, thực hiện tại các phòng xét nghiệm đủ năng lực.\n\nHiện nay, Việt Nam có 121 phòng xét nghiệm có năng lực xét nghiệm phát hiện SARS-CoV2 bằng kỹ thuật rRT-PCR. Các đơn vị này đã xét nghiệm được hơn 537.000 mẫu, trong đó, kết quả xác định 747 mẫu dương tính với SARS-CoV-2.\n\nTheo Bộ Y tế, xét nghiệm gộp nhóm có một số hạn chế như phải lấy lại mẫu với nhóm có kết quả dương tính. Nếu tỷ lệ dương tính của cộng động thấp, điều này không gây ảnh hưởng nhiều.\n\nTheo hướng dẫn vừa ban hành, việc gộp mẫu xét nghiệm không được áp dụng với trường hợp F1, người đã xét nghiệm chẩn đoán SARS-CoV-2 nhưng chưa có kết quả xác định hoặc bệnh nhân Covid-19 đang điều trị.  ',0,'gop-mau-xet-nghiem-covid-19-duoc-tien-hanh-nhu-the-nao',NULL,1,234,412,'<img src=\"Assets/images/690e8fe21531e96fb020_1_.jpg\">'),(168,8,'Thêm 21 người mắc Covid-19, Việt Nam vượt mốc 800 ca','Trong 21 bệnh nhân Covid-19 vừa được công bố, 20 người liên quan tâm dịch Đà Nẵng, đặc biệt, 16 trường hợp là F1.','18h ngày 8/8, Bộ Y tế công bố các bệnh nhân Covid-19 từ 780 đến 810. Trong đó, 16 ca là F1 của các bệnh nhân được công bố trước đó. Các ca được ghi nhận ở Đà Nẵng (15), Quảng Nam (2), Bắc Giang (2), Khánh Hoà (1) và một người nhập cảnh. Khánh Hòa là địa phương thứ 15 ghi nhận dịch Covid-19 trong đợt bùng phát này.\n\nBN790 là ca mắc nhập cảnh. Nam bệnh nhân 17 tuổi có địa chỉ tại Hưng Long, Bình Chánh, Bình Dương. Ngày 6/8, bệnh nhân từ Mỹ về sân bay Nội Bài trên chuyến bay VN2, cách ly ngay sau nhập cảnh tại Hà Nội.\n\nTrung tâm Kiểm soát Bệnh tật TP. Hà Nội lấy mẫu xét nghiệm. Kết quả bệnh nhân dương tính với SARS-CoV-2. Hiện BN790 được cách ly, điều trị tại Bệnh viện Bệnh Nhiệt đới Trung ương cơ sở 2, Hà Nội. <img src=\"Assets/images/Covid_19_DN_0008_zing.jpg\"> BN791 (nam, 45 tuổi, trú tại Cam Nghĩa, Cam Ranh, Khánh Hoà) là F1 của BN751.\n\nBN792 và BN796 trú tại Quảng Nam. Trong đó, một người từng tới thăm người thân tại khoa Nội thận - Nội tiết, Bệnh viện Đà Nẵng. Người còn lại là bệnh nhân của khoa này. Hiện hai bệnh nhân được cách ly, điều trị tại Bệnh viện Đa khoa Trung ương Quảng Nam.\n\nBN793, 794 ở Bắc Giang. Họ đều là F1, cùng nhóm gia đình đi du lịch Đà Nẵng ngày 21-24/7. Hiện các bệnh nhân được cách ly, điều trị tại Bệnh viện Bệnh Nhiệt đới Trung ương cơ sở 2.\n\n15 trường hợp còn lại (BN795 và BN797-810), trú tại Đà Nẵng, gồm một nhân viên y tế, một bệnh nhân và 13 F1 của các ca mắc Covid-19 đã được công bố.\n\nĐến nay, Việt Nam có tổng cộng 810 ca mắc Covid-19, trong đó, 317 ca nhiễm nhập cảnh được cách ly ngay. Số lượng ca mắc mới liên quan đến Đà Nẵng tính từ ngày 25/7 đến nay là 353.\n\nCác tỉnh, thành phố tại Việt Nam đang có dịch gồm: Đà Nẵng, Quảng Ngãi, Quảng Nam, Hà Nội, TP.HCM, Đắk Lắk, Thái Bình, Đồng Nai, Hà Nam, Bắc Giang, Lạng Sơn, Quảng Trị, Thanh Hóa, Hải Dương, Khánh Hòa.  ',0,'them-21-nguoi-mac-covid-19-viet-nam-vuot-moc-800-ca',NULL,1,76,514,'<img src=\"Assets/images/Covid_19_DN_0008_zing.jpg\">'),(169,8,'Bệnh nhân Covid-19 ở Hà Nam hết sốt','BN620 điều trị tại Bệnh viện Đa khoa tỉnh Hà Nam tiến triển khả quan. Hiện đây là ca bệnh duy nhất của Hà Nam được ghi nhận trong đợt dịch này.','<img src=\"Assets/images/37042ff3e7021b5c4213.jpg\"> PGS.TS Lương Ngọc Khuê cho biết dịch bệnh diễn biến phức tạp. 4 bệnh viện của Đà Nẵng phải đóng cửa, 10 nhân viên y tế mắc Covid-19 và 10 bệnh nhân tử vong.\n\nBệnh viện là nơi ghi nhận và phát hiện người mắc Covid-19. Việc phát hiện sớm, phân luồng và cách ly người bệnh đóng vai trò quan trọng đối với sự an toàn của bệnh viện. Theo PGS Khuê, cơ sở y tế càng cảnh giác càng bớt khó khăn bởi chỉ cần một bệnh nhân mắc Covid-19 bị \"lọt\" và đi khắp, nguy cơ bệnh viện phải đóng cửa sẽ cao.\n\nMặt khác, cán bộ y tế là những người không thể thay thế trong cuộc chiến chống Covid-19. Do đó, bệnh viện cần cảnh giác và tập trung chống dịch.\n\nSau khi kiểm tra, đánh giá, Bệnh viện Đa khoa tỉnh Hà Nam đạt 89 điểm, ở mức 59% an toàn. Đoàn kiểm tra của Bộ Y tế đề nghị bệnh viện phải khắc phục các khâu chưa đạt trong 2-3 ngày. Tuần tới, Sở Y tế Hà Nam sẽ triển khai đánh giá các bệnh viện trong toàn tỉnh.  ',0,'benh-nhan-covid-19-o-ha-nam-het-sot',NULL,1,45,14,'<img src=\"Assets/images/37042ff3e7021b5c4213.jpg\">'),(170,9,'Khách tố resort không hoàn cọc 52 triệu đồng vì Covid-19','Chi phí phòng villa trong 4 ngày 3 đêm của nữ du khách có thể bị mất trắng vì một resort ở vịnh Ninh Vân (Khánh Hòa) không đồng ý hoàn tiền cũng như dời ngày lưu trú.','Mới đây, trên hội nhóm du lịch, tài khoản Lily Nguyen chia sẻ trải nghiệm không mấy vui vẻ của bản thân khi đặt phòng lưu trú tại Khánh Hòa trong mùa dịch Covid-19.\n\nCụ thể, gia đình Lily Nguyen chi hơn 52 triệu đồng đặt căn villa tại Six Senses Ninh Van Bay, resort 5 sao được đánh giá hàng đầu vịnh Ninh Vân. Chuyến đi kéo dài 4 ngày 3 đêm, du khách đã thanh toán 100% qua ứng dụng đặt phòng trung gian.\n\nTuy nhiên, dịch Covid-19 bùng phát, lo ảnh hưởng đến sức khỏe các thành viên, gia đình quyết định tạm hoãn chuyến đi. Khách hàng này tìm cách liên lạc, thương lượng với trang web đặt phòng và resort nhưng không nhận được câu trả lời thỏa đáng.\n\n\"Mình đã gọi nhiều lần, cố gắng bày tỏ mong muốn dời ngày và chịu phí phạt chứ không hủy phòng với nhân viên resort nhưng chỉ nhận câu trả lời là không. Mình thật sự rất buồn và thất vọng vì cách làm việc của resort. Điểm lưu trú này không thông cảm và xử lý quá cứng nhắc, nhất là trong tình hình dịch Covid-19 lây lan phức tạp\", Lily Nguyen bộc bạch trong bài viết.\n\nChia sẻ nhanh chóng thu hút sự chú ý và nhận ý kiến trái chiều từ cộng đồng mạng. Nhiều người dùng bày tỏ sự cảm thông với tình cảnh éo le của Lily Nguyen nhưng vẫn mong khách hàng này hiểu được khó khăn của cơ sở lưu trú trong mùa dịch. <img src=\"Assets/images/1_travellersgoal.jpg\"> Tài khoản Tony Mạnh để lại bình luận: \"Mỗi khách sạn có một chính sách riêng. Đa số phòng đặt theo chương trình khuyến mại đều không hoàn, hủy hay đổi ngoại trừ lý do bất khả kháng như dịch bệnh, thiên tai. Hiện tại, Nha Trang không có dịch và chính phủ chưa cấm, bạn bỏ cọc là lỗi của bạn\".\n\nLeo Tran viết: \"Resort càng nổi tiếng thì lịch book phòng càng kín, giải quyết dời ngày cũng khó. Các điểm lưu trú dời ngày được đa số vắng khách. Họ vừa tạo được tiếng thơm và làm thương hiệu\".\n\nNgười này cho rằng trong hợp đồng đã có điều khoản hoàn, hủy, đổi rõ ràng, du khách cứ thế mà áp dụng. Nếu ai cũng đòi linh động thì bài toán kinh doanh resort sẽ đi về đâu.\n\nTuy nhiên, không ít ý kiến cho rằng trong khi khách có thiện chí dời ngày chứ không hủy phòng, hoàn tiền thì cách giải quyết của resort lại quá cứng nhắc.\n\nThành viên Long Phượng chia sẻ quan điểm: \"Đây là resort khá có tiếng tại Khánh Hòa, giờ xử lý thế này thì chịu. Không sai nhưng quá cứng nhắc, dễ làm mất lòng khách hàng\".\n\nĐồng quan điểm, Nguyen Linh Chi chia sẻ: \"Mình không hiểu sao các bạn vẫn bênh resort được. Mình cũng làm khách sạn và cũng là người đặt dịch vụ nên hiểu dịch bệnh là điều không ai muốn. Thời điểm này, khách sạn ở khắp nơi đều đang hỗ trợ khách. Du lịch là để tìm niềm vui, sự thoải mái chứ không phải gò bó giữa tình hình dịch thế này\".\n\nĐức Vũ chia sẻ quan điểm dịch bệnh ảnh hưởng đến tâm lý của nhiều du khách. Họ e ngại khi phải di chuyển đến điểm du lịch trong tình trạng không an toàn. Theo du khách này, đôi khi, cách cư xử của điểm lưu trú quyết định việc du khách có quay trở lại nơi đó hay không.\n\nLiên hệ resort, phóng viên nhận được câu trả lời hai bên đã đạt được thỏa thuận mong muốn. Khách hàng sẽ sớm đăng bài giải thích rõ sự việc và hướng giải quyết.\n\nSáng 8/8, trao đổi với Zing, phía tài khoản đăng bài tố cho biết resort trên đã liên hệ trực tiếp với cô và chấp nhận phương án dời ngày hoặc hoàn tiền đặt cọc.  ',0,'khach-to-resort-khong-hoan-coc-52-trieu-dong-vi-covid-19',NULL,1,34,31,'<img src=\"Assets/images/1_travellersgoal.jpg\">'),(171,9,'Tránh dịch, người Hà Nội tận hưởng kỳ nghỉ hè ở biển tại nhà','Trong khi nhiều kế hoạch du lịch bị trì hoãn vì dịch, cư dân hơn 9.000 căn hộ đã nhận nhà tại Vinhomes Ocean Park vẫn được tận hưởng kỳ nghỉ dưỡng ngay tại nơi ở.','Quyết định chuyển từ phố cổ về “thành phố biển hồ” để các con được thỏa sức vui chơi như mơ ước, anh Bá Khánh (cư dân tòa S2.02) chia sẻ: “Tình hình dịch bệnh phức tạp, gia đình tôi may mắn hơn rất nhiều người vì dù không đi biển miền Trung, miền Nam thì vẫn có thể đi biển tại gia ngay trong Ocean Park. Hai con tôi rất sung sướng vì ngày nào cũng được thỏa sức chơi đùa trong không gian thoáng đãng”. <img src=\"Assets/images/anh_1.jpg\"> Nhiều cư dân sự phấn khởi khi được tận hưởng cảnh quan, tiện ích hoàn thiện vượt xa mong đợi. Họ luôn có kỳ nghỉ ở biển ngay tại nhà, không cần đi đâu xa.\n\n“Lúc quyết định mua nhà ở Vinhomes Ocean Park, mình không nghĩ khi hoàn thiện biển lại đẹp đến thế. Mặt nước xanh biếc, cát trắng mịn và những hàng dừa nhìn cứ như ở Nha Trang, Phú Quốc”, chị P.L (cư dân tòa S2.18) chia sẻ. <img src=\"Assets/images/Anh_5.png\"> Từng có nhiều năm sinh sống và làm việc tại Roma (Italy), nhà báo - bình luận viên thể thao Trương Anh Ngọc luôn hoài niệm không gian sống đáng nhớ tại trời Âu, nơi có công viên sau nhà cho cả gia đình anh luyện tập thể thao mỗi ngày.\n\nTrong 10 năm qua, vợ chồng anh đã có đến 4 lần chuyển nơi ở. Trong đó, lần chuyển nhà mới nhất về thành phố biển hồ Vinhomes Ocean Park, anh hoàn toàn hài lòng về cảnh quan và tiện ích cao cấp, phù hợp với quan niệm về không gian sống gắn với hoạt động thể thao của anh. <img src=\"Assets/images/Anh_7_1.png\"> Với quy mô 420 ha, mật độ xây dựng chỉ gần 19%, Vinhomes Ocean Park có tới hơn 117 ha diện tích công viên - cây xanh - mặt nước. Không gian sống khoáng đạt với các tổ hợp sân chơi nội khu, sân chơi ven hồ mang tới đa dạng hoạt động cho trẻ nhỏ. Đây cũng là một trong những trải nghiệm hài lòng nhất của các gia đình trẻ khi chuyển về đây sống.\n\nTừ khi chính thức bàn giao, tràn ngập trên các hội, nhóm cộng đồng cư dân và mạng xã hội là những hình ảnh check-in, chia sẻ cảm xúc háo hức của cư dân về môi trường cảnh quan, dịch vụ và tiện ích trong đại đô thị. Ngoài ra, cư dân cũng dành nhiều lời khen cho chất lượng xây dựng, hoàn thiện căn hộ.\n\nChị Thanh Nhàn (cư dân tòa S2.01) cho biết: “Kể từ khi về đây, mình thấy các bạn nhỏ thay đổi rõ rệt. Các con có thêm nhiều bạn mới, được tham gia nhiều câu lạc bộ như bóng đá, bơi lội… nên bạo dạn hơn hẳn”. <img src=\"Assets/images/Anh_11.jpg\"> Đón hàng nghìn cư dân về ở, Vinhomes Ocean Park đưa vào sử dụng nhiều tiện ích, cảnh quan quy mô, tạo nên nhiều thay đổi tích cực trong cuộc sống của cộng đồng cư dân. Thay vì có dịch bệnh mới lo đến sức khỏe, tại đây, cộng đồng cư dân có điều kiện rèn luyện, nâng cao sức khỏe thể chất và tinh thần mỗi ngày.\n\nDự kiến đến cuối năm, chủ đầu tư sẽ tiếp tục bàn giao các căn hộ tại phân khu Sapphire 1 - trái tim của dự án. Hiện nay, S1.02 - tòa căn hộ cuối cùng của Sapphire 1 đang được mở bán, đồng thời là toà căn hộ đầu tiên ứng dụng công nghệ Vsmart Smart Home tại dự án, với nhiều tiện ích như nhà để xe thông minh kết hợp thương mại dịch vụ, kế cận tổ hợp công viên thể thao liên hoàn, sân chơi nước dành cho trẻ em ngay trong nội khu, liền kề trường PTLC Vinschool…  ',0,'tranh-dich-nguoi-ha-noi-tan-huong-ky-nghi-he-o-bien-tai-nha',NULL,1,6,515,'<img src=\"Assets/images/Anh_11.jpg\">'),(172,9,'Thưởng thức ẩm thực Thái fusion tại trung tâm TP.HCM','Tọa lạc tại trung tâm TP.HCM, nhà hàng ẩm thực Thái fusion được thực khách yêu thích nhờ những món ăn sáng tạo, hương vị mới lạ khó quên.','Việt Nam hiện có rất nhiều nơi kinh doanh ẩm thực Thái với đa dạng quy mô từ các quán ăn nhỏ, vừa đến lớn. Một trong những cái tên được nhiều người ưa chuộng là Greyhound Café, một thương hiệu nổi tiếng của Thái Lan được nhượng quyền về Việt Nam.\n\nKhông chỉ được ưa chuộng tại Thái Lan, xuất hiện tại các quốc gia khác, Greyhound Café cũng thu hút được sự quan tâm đáng kể của thực khách. Tính đến nay, thương hiệu đã có mặt tại 8 nơi trên thế giới bao gồm Thái Lan, Trung Quốc, Malaysia, Hong Kong (Trung Quốc), Singapore, Indonesia, Anh Quốc và Việt Nam. <img src=\"Assets/images/image001_3.jpg\"> Tọa lạc tại trung tâm TP.HCM, Greyhound Café mang đến không gian hiện đại, sang trọng khác với nhiều nhà hàng Thái khác. Đến đây, thực khách sẽ được thưởng thức nhiều món ăn ngon, được chế biến theo công thức đặc biệt của nhà hàng, đúng với slogan “Thai food with a twist” (tạm dịch: Đồ ăn Thái được biến tấu). <img src=\"Assets/images/image003_4.jpg\"> Các món Thái theo phong cách fusion tuy lạ mà quen, hứa hẹn làm hài lòng cả những thực khách khó tính. Không gian nhà hàng hiện đại nhưng phù hợp văn hoá ăn uống của người Việt, mang đến bữa ăn ấm cúng cho mỗi gia đình. Các món ăn có thể được gọi theo phần riêng cho mỗi người, cũng có thể ăn theo kiểu sharing style - gọi chung nhiều món và chia sẻ cùng nhau. <img src=\"Assets/images/image007_3.jpg\"> Greyhound Café mở cửa 8h-22h theo mô hình “all-day dining” - nghĩa là nhà hàng phục vụ tất cả món ăn dành cho các bữa trong ngày. Menu nhà hàng đa dạng và phù hợp cho mọi lứa tuổi, gồm các món Thái theo phong cách fusion. Đây là các món ăn được kết hợp từ nguyên liệu, phương pháp chế biến giữa nhiều quốc gia, nền ẩm thực khác nhau để tạo thành một món ăn mới. Các món ăn được \"twist\" để tạo nên hương vị mới mẻ, khó quên. <img src=\"Assets/images/image009_3.jpg\"> Menu bữa sáng, brunch, bữa trưa, bữa tối đều phong phú, gồm khai vị, món chính đến tráng miệng. Không chỉ dừng lại ở đó, menu thức uống của Greyhound Café cũng đa dạng với nhiều loại trà, cà phê, nước trái cây, cocktail và rượu vang.\n\nGreyhound Café là một nơi rất lý tưởng để dùng bữa sau một ngày hoặc một tuần làm việc căng thẳng; tận hưởng một bữa tối thật “chill” bên cạnh những người thân thiết và cùng nhâm nhi một ly rượu vang.  ',0,'thuong-thuc-am-thuc-thai-fusion-tai-trung-tam-tp.hcm',NULL,1,23,5315,'<img src=\"Assets/images/image009_3.jpg\">'),(173,11,'Nhiều mẫu iPhone chính hãng đang đồng loạt giảm giá tại Việt Nam','Bên cạnh iPhone 11 Pro, một số smartphone Apple phân phối chính hãng khác như SE 2020 hay 11 cũng đang được giảm giá tại Việt Nam.','<img src=\"Assets/images/16a2d280_8424_11ea_bed5_deedf4f1f73d.jpg\"> Mẫu iPhone SE 2020 chính hãng 64 GB liên tục giảm giá trong thời gian gần đây. Máy được bán với giá 11,8 triệu đồng vào tháng 6. Đến tháng 7, sản phẩm còn 11,4 triệu đồng. Hiện tại, model này được một số cửa hàng bán với giá 10,8 triệu đồng. Ảnh: Engadget. <img src=\"Assets/images/16ab36f0_8424_11ea_b67d_2a78e5288c95.jpg\"> iPhone SE 2020 thực tế là một chiếc iPhone 11 bên trong vỏ máy của iPhone 8 với màn hình 4,7 inch, tỷ lệ 16:9. Sản phẩm vẫn được dùng chip Apple A13 Bionic, RAM 3 GB. Tuy vậy, iPhone SE 2020 không được cải tiến về thời lượng sử dụng pin. Apple cho biết iPhone SE mới có pin tương đương iPhone 8, cụ thể theo China Telecom là 1.821 mAh. Ảnh: Engadget. <img src=\"Assets/images/5df10433fd9db22a525cfde4.jpg\"> iPhone 11 chính hãng 64 GB đang được một số chuỗi đại lý bán với giá 17,6 triệu đồng, giảm 800.000 đồng so với tháng trước. Máy được lên kệ tại Việt Nam vào đầu tháng 11/2019, giá từ 22 triệu đồng. Như vậy, sau hơn nửa năm bán ra, iPhone 11 chính hãng đã giảm 4,4 triệu đồng. Ảnh: Business insider. <img src=\"Assets/images/5df12924fd9db25aad1b1833.jpg\"> iPhone 11 là phiên bản nâng cấp của iPhone XR. So với phiên bản tiền nhiệm, iPhone 11 không có quá nhiều đột phá về thiết kế. Máy chủ yếu được nâng cấp về cấu hình với chip Apple A13 Bionic. Sản phẩm trang bị camera kép đều có độ phân giải 12 MP, trong đó có camera chính khẩu độ f/1.8 và ống kính góc siêu rộng. Ảnh: Business insider.\nnhung mau iPhone chinh hang dang giam gia anh 5\nCác hệ thống lớn cũng điều chỉnh giá iPhone 11 Pro chính hãng 64 GB. Sản phẩm đang được giảm 2,1 triệu đồng, còn 24,8 triệu đồng. iPhone 11 Pro sở hữu thiết kế giống như iPhone 11 với màn hình notch. Tuy nhiên, model này có mặt lưng kính được làm mờ, mang đến cảm giác cao cấp hơn. <img src=\"Assets/images/ZeSeBrAF8CTw3ztqvYj24S.jpg\"> Ngoài ra, máy được trang bị 3 camera sau độ phân giải giống nhau 12 MP. Bên cạnh một ống kính chính có khẩu độ f/1.8, camera góc siêu rộng, sản phẩm còn có một camera tele. iPhone 11 Pro có màn hình 5,8 inch, chip Apple A13 Bionic và RAM 4 GB. Cấu hình của máy vẫn đủ sức vượt qua nhiều flagship của thế giới Android 2020. Sự ổn định này đến từ việc Apple có hệ sinh thái ứng dụng và thiết bị riêng. Ảnh: Tomguide. <img src=\"Assets/images/3de7d216_fde6_41e2_8c86_58f6ffb50971.png\"> iPhone 8 Plus chính hãng bản 128 GB được giảm 700.000 đồng còn 13,5 triệu đồng. Máy được ra mắt vào tháng 9/2017 cùng với iPhone X. Ngoại hình của iPhone 8 Plus giống như iPhone 7 Plus với viền trên và dưới khá dày. Ảnh: Pulse Ghana. <img src=\"Assets/images/iphone_8_and_iphone_8_plus_A.jpg\"> Cấu hình của máy bao gồm chip Apple A11 Bionic, RAM 3 GB. Cấu hình này vẫn có thể đáp ứng tốt những tác vụ thường ngày nhưng không còn quá mạnh. Sản phẩm có màn hình 5,5 inch, camera kép 12 MP. Đây là smartphone đầu tiên của Apple hỗ trợ sạc không dây. Ảnh: Nydjlive.  ',0,'nhieu-mau-iphone-chinh-hang-dang-dong-loat-giam-gia-tai-viet-nam',NULL,1,6,314,'<img src=\"Assets/images/iphone_8_and_iphone_8_plus_A.jpg\">'),(174,11,'Galaxy Watch3 giảm 1 triệu đồng ở Thế Giới Di Động','Không chỉ phân phối độc quyền smartphone mới của Samsung, nhà bán lẻ còn giảm đến 1 triệu đồng cho người dùng mua Galaxy Watch3.','Cụ thể, từ ngày 10/8 đến 20/8, khi mua Galaxy Watch3 tại cửa hàng Thế Giới Di Động trên cả nước, khách hàng sẽ được giảm từ 500.000 đồng đến 1 triệu đồng (tùy sản phẩm), hỗ trợ trả góp lãi suất 0%. <img src=\"Assets/images/ga1.jpg\"> Bên cạnh đó, khi mua sản phẩm, khách hàng còn nhận được chính sách bảo hành chính hãng 12 tháng, và 1 đổi 1 trong một tháng.\n \n Được Samsung bổ sung thêm loạt tính năng nổi bật cùng thiết kế trau chuốt, Galaxy Watch3 đáp ứng tiêu chí “3 trong 1”: Trang sức thời thượng - thiết bị theo dõi sức khỏe tiên tiến - thiết bị luyện tập thể thao đa năng. <img src=\"Assets/images/ga2.jpg\"> Về thiết kế, Galaxy Watch3 giữ nguyên kiểu mặt tròn và khung thép chống gỉ, sáng bóng. Được trang bị màn hình Super AMOLED và kính cường lực Gorilla Glass DX chống trầy, tỷ lệ màn hình lớn, máy cho hiệu ứng chuyển động mượt mà, độ hiển thị sắc nét hơn. Khung xoay vật lý trở lại với thiết kế mỏng hơn, bố cục nút bấm được sắp xếp gọn gàng giúp người dùng thực hiện thao tác thực tế dễ dàng.\n \n Sản phẩm có 2 phiên bản mặt đồng hồ với kích thước 41 mm và 45 mm, phù hợp cho cả nam và nữ. Đồng thời, Samsung thay thế dây silicon bằng dây da sang trọng, mang đến cho Galaxy Watch3 nét đẹp vừa cổ điển, vừa hiện đại, dễ dàng kết hợp với nhiều kiểu outfit thường ngày, hay tham dự sự kiện sang trọng. <img src=\"Assets/images/ga3.jpg\"> Galaxy Watch3 có tất cả tính năng và tiện ích của “đàn anh” Galaxy Watch trước đó, như theo dõi sức khỏe từ đo nhịp tim, đo nồng độ oxy trong máu, cải thiện giấc ngủ và kiểm soát mức độ căng thẳng.\n \n Máy được Samsung cải tiến và phát triển thêm nhiều tính năng mới, như khả năng phát hiện và trực tiếp thực hiện cuộc gọi đến một số liên lạc, dịch vụ khẩn cấp khi người dùng té ngã; tính năng theo dõi chu kỳ kinh nguyệt ở nữ giới, hữu ích và cần thiết trong cuộc sống hàng ngày. <img src=\"Assets/images/ga4.jpg\"> Watch3 được xem như một huấn luyện viên ảo nhờ khả năng phân tích và chỉ dẫn luyện tập khi người dùng chạy bộ. Đây là tính năng Samsung Galaxy Watch trước đó không có. Ngoài ra, sản phẩm có nhiều trang bị nổi bật khác như sạc không dây; bộ nhớ trong đến 8 GB, cao gấp đôi so với Galaxy Watch, cho dung lượng lưu trữ bằng một chiếc smartphone; chuẩn chống nước IP68 và tiêu chuẩn độ bền theo quân đội Mỹ MIL-STD-810G, tăng khả năng chống chịu và bền bỉ...\n \n Galaxy Watch3 đang được bán tại Thế Giới Di Động với giá 9,49 triệu đồng cho phiên bản 41 mm Bluetooth, và 10,49 triệu đồng cho bản 41 mm LTE. Phiên bản 45 mm Bluetooth giá 9,99 triệu đồng; bản 45 mm LTE giá 10,99 triệu đồng. Chi tiết về chương trình khuyến mãi, độc giả xem tại đây.  ',0,'galaxy-watch3-giam-1-trieu-dong-o-the-gioi-di-dong',NULL,1,4,2513,'<img src=\"Assets/images/ga1.jpg\">'),(175,11,'Hàng loạt kênh bị hack, YouTube vẫn làm ngơ','Hàng loạt kênh YouTube bị hacker chiếm quyền để livestream lừa đảo Bitcoin. Thế nhưng, YouTube chỉ biết làm ngơ khi chủ kênh gửi yêu cầu hỗ trợ.','Từ đầu tháng 8, một loạt video phát trực tiếp trên YouTube với những tựa đề hấp dẫn. Khi bấm vào xem, phần lớn video hiển thị các thông điệp kêu gọi gửi Bitcoin cho một địa chỉ ví điện tử, sau đó sẽ được nhận lại gấp đôi lượng Bitcoin.\n\nTheo chuyên gia công nghệ Rene Ritchie của iMore, những video này có thể đã dùng máy ảo để đẩy lượng xem lên cao, qua đó khiến cho YouTube đề xuất tới người xem. <img src=\"Assets/images/Bitcoin_scam_1_2.jpg\"> Jon Prosser, người sở hữu kênh Front Page Tech cho biết tài khoản anh đã bị hack, kể cả lớp xác thực hai yếu tố cũng bị vượt qua.\n\nHacker đã kiếm được số Bitcoin giá trị khoảng 4.000 USD từ kênh của Prosser. Người này cũng cho biết mọi video trên kênh của anh đã bị xóa và chỉ vài giờ sau kênh cũng đã biến mất.\n\nKhi liên hệ với YouTube, Prosser chỉ được hướng dẫn điền thông tin vào một trang hỗ trợ. Tuy nhiên, tài khoản hỗ trợ cũng cho biết trường hợp này \"phải mất vài tuần mới có các bước giải quyết tiếp theo\".\n\nJon Prosser cho biết mặc dù YouTube đã ghi nhận trường hợp của anh và hứa giải quyết, video livestream lừa Bitcoin vẫn tiếp tục phát.\n\n\"Những hacker này đang ăn cướp hàng nghìn USD, trên nền tảng của YouTube. Thế nhưng, YouTube lại không làm gì được. Ít nhất Twitter còn ngừng vụ hack lại khi nó đang diễn ra\", chủ kênh này bức xúc.\n\nProsser không phải YouTuber duy nhất bị mất kênh. Theo chuyên gia eSports Rod Breslau, nhiều kênh YouTube đã bị hack và đổi tên thành \"Elon Musk NASA\" hay \"SpaceX NASA\". <img src=\"Assets/images/Bitcoin_scam_2.jpg\"> Breslau nhận ra những dấu hiệu bất thường từ ngày 3/8. Tuy nhiên, những vụ hack có thể đã diễn ra nhiều tháng.\n\n\"Khi nói chuyện với các YouTuber khác, tôi nhận thấy việc lấy cắp tài khoản để lừa Bitcoin đã diễn ra nhiều tháng nay, sớm hơn nhiều so với vụ hack Twitter. Đến nay, YouTube vẫn không có động thái gì để giải quyết và còn khiến những người chủ kênh phải chờ hàng tuần mới có thể lấy lại tài khoản\", ông Breslau viết trên Twitter.\n\nTheo MarcoStyle, một YouTuber từng bị hack kênh vào cuối năm 2019, thủ đoạn hack này đã tồn tại từ lâu. MarcoStyle cho biết kênh của anh bị xâm nhập sau khi anh bị lừa bấm vào một đường link trong email. Sau khi chiếm được tài khoản điều khiển, hacker đã đổi thành kênh của thương hiệu, cho phép nhiều tài khoản cùng quản lý.\n\nMarcoStyle cho rằng phương pháp hack này có thể hạn chế nếu YouTube áp dụng xác thực hai yếu tố mỗi lần đăng nhập hoặc tải video lên.\n\n\"Đúng là không thế bắt YouTube khắc phục ngay sau khi tôi mắc lỗi. Tuy nhiên, tôi cũng ước giá mà YouTube có thêm nhiều lựa chọn bảo mật hơn\", MarcoStyle nói trong video kể lại vụ hack.  ',0,'hang-loat-kenh-bi-hack-youtube-van-lam-ngơ',NULL,1,3,624,'<img src=\"Assets/images/Bitcoin_scam_2.jpg\">'),(176,11,'Mỹ cấm vận, Huawei hết chip Kirin để làm smartphone','Dòng flagship chủ lực Mate 40 có thể là smartphone cuối cùng của Huawei sử dụng con chip ','Trong khuôn khổ sự kiện hội nghị thượng đỉnh công nghệ Trung Quốc 2020, Richard Yu - CEO mảng Kinh doanh Tiêu dùng của Huawei xác nhận dòng flagship chủ lực Mate 40 sử dụng chip Kirin 1000 sẽ được ra mắt vào mùa thu năm nay.\n\nTrước đó, chipset Kirin 1000 để dùng trên dòng Mate 40 dự kiến sẽ được giới thiệu ở triển lãm điện tử IFA tổ chức tại Berlin vào đầu tháng 9 tới. Kirin 1000 được gia công trên tiến trình 5 nm của TSMC và thiết kế dựa trên nhân Cortex A77 của ARM.\n\nÔng Yu cũng xác nhận dòng chip Kirin sẽ không được sản xuất kể từ ngày 15/9 sau khi TSMC, công ty gia công vi xử lý chính cho Huawei hoàn thành đơn hàng cuối cùng. <img src=\"Assets/images/Mate40render_scaled.jpg\"> Theo GSMArena, các lãnh đạo của Huawei đã dự đoán trước việc TSMC dừng hợp tác và mua sẵn số lượng lớn chipset Kirin 1000 đủ dùng trên khoảng 15 triệu máy thuộc dòng Mate 40. Hiện Huawei chưa công bố sẽ sử dụng chip nào cho smartphone trong tương lai sau khi lô hàng cuối cùng với TSMC hoàn thành.\n\nÔng Richard Yu cho biết thêm trong nửa đầu năm 2020, Huawei đã xuất xưởng 105 triệu smartphone, mang về doanh thu 255,8 tỷ nhân dân tệ. Trong quý II/2020, ông Yu tuyên bố Huawei đã vượt qua Samsung về số lượng lô hàng điện thoại xuất xưởng.\n\nDự kiến dòng Mate 40 sẽ có 4 sản phẩm gồm Mate 40, Mate 40 Pro, Mate 40 Pro+ cùng phiên bản đặc biệt hợp tác với hãng xe Porsche. Tất cả đều sẽ được trang bị màn hình cong. Camera trên thiết bị được dự đoán sẽ có nhiều nâng cấp đáng giá.  ',12,'my-cam-van-huawei-het-chip-kirin-de-lam-smartphone',NULL,1,155,123,'<img src=\"Assets/images/Mate40render_scaled.jpg\">'),(177,12,'Hai bệnh nhân Covid-19 tử vong ở tuổi 33 và 47','Bệnh nhân 430, 737 là ca tử vong do Covid-19 thứ 12 và 13 tính từ khi dịch xảy ra ở Việt Nam.','Sáng nay (10/8), Thứ trưởng Bộ Y tế Nguyễn Trường Sơn, Trưởng Bộ phận thường trực đặc biệt chống dịch Covid-19 của Bộ Y tế tại TP Đà Nẵng, thông tin về 2 bệnh nhân Covid-19 tử vong.\n\nBệnh nhân 430 (nữ, 33 tuổi, địa chỉ ở phường Hòa Khánh Nam, quận Liên Chiểu, TP Đà Nẵng) có tiền sử suy thận mạn giai đoạn cuối, suy tim, tăng huyết áp, nhiễm trùng huyết. Ngày 23/7, người phụ nữ này mệt nhiều, khó thở nên đi khám và được nhập viện điều trị tại khoa Nội thận, Bệnh viện Đà Nẵng. Các bác sĩ chẩn đoán bệnh nhân bị viêm phổi cấp do Covid-19.\n\nKết quả xét nghiệm cho thấy nữ bệnh nhân dương tính với SARS-CoV-2. Ngày 30/7, BN430 diễn biến nặng, được chuyển đến Bệnh viện Đa khoa Trung ương Huế cơ sở 2 và lọc máu liên tục.\n\nNgày 1/8, bệnh nhân được đặt nội khí quản, thở máy và can thiệp ECMO (tim phổi nhân tạo) vào 2 ngày sau. 22h30 ngày 9/8, bệnh nhân nguy kịch, xuất hiện ngừng tuần hoàn hô hấp. BN430 tử vong vào 0h30 sáng 10/8.\n\nChẩn đoán nguyên nhân tử vong là viêm phổi nặng do Covid-19, biến chứng suy đa tạng không hồi phục, sốc nhiễm trùng nhiễm độc trên bệnh nhân suy thận mạn giai đoạn cuối, lọc màng bụng chu kỳ và suy tim, tăng huyết áp. <img src=\"Assets/images/BV1990027_1.jpg\"> Ca tử vong thứ 13 là BN737 (nữ, 47 tuổi, địa chỉ quận Sơn Trà, TP Đà Nẵng). Người phụ nữ này có tiền sử suy thận mạn giai đoạn cuối, đã chạy thận nhân tạo chu kỳ và đặt stent, suy tim, tăng huyết áp.\n\nNgày 4/8, bà kết quả dương tính với virus gây bệnh Covid-19. Bệnh nhân này được chuyển từ khoa Hồi sức tích cực của Bệnh viện Đà Nẵng sang Trung tâm Y tế huyện Hòa Vang để điều trị.\n\n19h30 ngày 9/8, bệnh nhân suy hô hấp, thở máy, đặt nội khí quản và phải sử dụng thuốc an thần, trợ tim. 0h45 ngày 10/8, BN737 hôn mê sâu, nhịp tim rời rạc rồi ngừng hẳn. Đến 1h15, bệnh nhân tử vong. Chẩn đoán nguyên nhân tử vong là nhồi máu cơ tim, suy tim, suy thận mạn tính giai đoạn cuối, thận nhân tạo chu kỳ và mắc Covid-19.\n\nTrước đó, Việt Nam ghi nhận 11 bệnh nhân mắc Covid-19 tử vong. Hai người ở Quảng Nam (BN428, BN651) và 9 ca tại Đà Nẵng (BN437, 499, 524, 475, 429, 426, 496, 718, 456). Họ đều là những người cao tuổi, mắc bệnh lý nền như suy thận mạn tính, tiểu đường type II, ung thư, tăng huyết áp...\n\nViệt Nam có tổng cộng 841 ca mắc Covid-19, trong đó, 317 ca nhiễm nhập cảnh được cách ly ngay.\n\nCác tỉnh, thành phố tại Việt Nam đang có dịch gồm: Đà Nẵng, Quảng Ngãi, Quảng Nam, Hà Nội, TP.HCM, Đắk Lắk, Thái Bình, Đồng Nai, Hà Nam, Bắc Giang, Lạng Sơn, Quảng Trị, Thanh Hóa, Hải Dương, Khánh Hòa.  ',5,'hai-benh-nhan-covid-19-tu-vong-o-tuoi-33-va-47',NULL,2,30,0,'<img src=\"Assets/images/BV1990027_1.jpg\">');

